INSERT INTO `FILMS` (`id`, `title`, `description`, `video_src`) VALUES
(1, 'Якщо всі разом', 'Поетичним твором \"Якщо всі та разом\" Віктор Кириченко намагався відмітити п\"ятиріччя Небесної Сотні. Рубен Чілутян своєю музикальною композицією розкрив біль глибокий задум цього поетичного твору.\n', 'https://www.youtube.com/embed/fWI58DPDpcE'),
(2, 'Мій рідний край', 'Поетичний твір \"Рідний край\" народився під впливом вражень Віктора Кириченка красотами Слобожанського краю: його родючими ланами, тихими річками, прекрасними мешканцями, чудовими піснями, особими бджолярами. Рубен Чілутян разом з виконавицею музичної композиції Тетяною Яковенко додали яскравих рис поетичному твору, який може бути гімном мешканців Слобожанського краю.', 'https://www.youtube.com/embed/czNpxFWBP64'),
(3, 'Вітання до 70-річчя Віктора Васильовича Кириченка', '', 'https://www.youtube.com/embed/MPM2Ohwg_PI'),
(5, 'Істина', 'Пісня \"Істина\" присвячена ролі поета у пошуках істини та сенсу життя. Слова пісні були написані в 2015 році і відносяться до збірника \"Щаслива мить\".', 'https://www.youtube.com/embed/3OtZzupNWAM'),
(6, 'Белые розы', 'Твір \"Белые розы\" Віктор Кириченко написав ще в юнацькі роки, але його сучасний зміст з\"явився через багато років в наслідок осмислення періоду юнацтва. Перші побачення, значення квітів задля радості милої дівчини не зовсім розкривають задум автора, але в наслідок союзу автора твору та композитора Рубена Чілутяна розкриваються нові риси почуттів у молодості.', 'https://www.youtube.com/embed/sXuHwGxNcX4'),
(7, 'Я - твой, а ты - моя', 'Поетичний твір \"Я - твой, а ты - моя\" передбачений для людей старшого віку. Музика Рубена Чілутяна придала поетичному твору особливого колориту і привабливості для будь-якого слухача.', 'https://www.youtube.com/embed/nLzZH_dWgvk'),
(8, 'Молитва за сина', 'Поетичний твір \"Молитва за сина\" з\"явився в переліку творів Віктора Кириченка, як результат переживань за дітей багатьох громадян світу. Також твір яскраво висвітлює усі переживання батьків за своіх дітей.', 'https://www.youtube.com/embed/6qp8DGbHIoA');
INSERT INTO `LYRICS` (`id`, `set_id`, `name`, `content`, `write_date`, `author`, `description`, `author_comment`, `lyrics_type`, `TABBED`, `BRS`, `STROPHE_LENGTH`, `STROPHE_DELIM`, `LINE_WRAPPER`, `LINE_WRAPPER_END`, `LINE_WRAPPER_TABBED`, `LINE_WRAPPER_TABBED_END`, `auto_format`) VALUES
(435, 32, 'Лето', '<div class = \'tab\'>Чудная погода</div><div class = \'tab\'>Пришла в начале лета,</div><div class = \'tab\'>Ожившая природа</div><div class = \'tab\'>Окрылила поэта.</div><br/><div class = \'tab\'>Собою вдохновила,</div><div class = \'tab\'>Заставив сердце петь</div><div class = \'tab\'>И красоту земную</div><div class = \'tab\'>В стихах запечатлеть.</div><br/><div class = \'tab\'>Не Пушкин я, не Лермонтов, –</div><div class = \'tab\'>Куда мне до их муз!</div><div class = \'tab\'>Любовь к «земным орнаментам»</div><div class = \'tab\'>Привил мне славный ВУЗ.</div><br/><div class = \'tab\'>Для тех поэтов лето –</div><div class = \'tab\'>Скучнейшая пора,</div><div class = \'tab\'>А мне, а мне все это –</div><div class = \'tab\'>Забот и дел гора.</div><br/><div class = \'tab\'>В начале лета трудно</div><div class = \'tab\'>В селе и на полях.</div><div class = \'tab\'>Весь сельский люд в заботах,</div><div class = \'tab\'>И сено не в стогах.</div><br/><div class = \'tab\'>Идут дожди обильные</div><div class = \'tab\'>(Ох, сено не убрать!),</div><div class = \'tab\'>Но так необходимые</div><div class = \'tab\'>Для всех культур подряд.</div><br/><div class = \'tab\'>Средина лета – знойная,</div><div class = \'tab\'>Нектар «бежит рекой»,</div><div class = \'tab\'>Пшеница с полным колосом</div><div class = \'tab\'>Стоит живой стеной.</div><br/><div class = \'tab\'>Июль приносит краски</div><div class = \'tab\'>И первые плоды.</div><div class = \'tab\'>Все красочно, как в сказке,</div><div class = \'tab\'>Во всем видны труды.</div><br/><div class = \'tab\'>А август – красотища:</div><div class = \'tab\'>С полей зерно идет</div><div class = \'tab\'>В амбары и хранилища,</div><div class = \'tab\'>Как реченька, течет!</div><br/><div class = \'tab\'>Все будет: пышный хлебушек,</div><div class = \'tab\'>Румяный и душистый,</div><div class = \'tab\'>И сахар белоснежный,</div><div class = \'tab\'>И сладкий, и пушистый.</div><br/><div class = \'tab\'>Картошечка с капустой,</div><div class = \'tab\'>Морковкой и лучком,</div><div class = \'tab\'>И яблоки, и грушки</div><div class = \'tab\'>С оранжевым бочком.</div><br/><div class = \'tab\'>Во всем я этом вижу</div><div class = \'tab\'>Всей жизни полноту,</div><div class = \'tab\'>В стихах хочу поведать</div><div class = \'tab\'>Земную красоту!</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(436, 32, 'Природа и мы', '<div class = \'tab\'>Сентябрь уж на исходе,</div><div class = \'tab\'>Осталось десять дней,</div><div class = \'tab\'>Но дарит «бабье лето»</div><div class = \'tab\'>Тепло душе моей.</div><br/><div class = \'tab\'>…Налетела тучка,</div><div class = \'tab\'>Грозой угрожая,</div><div class = \'tab\'>И пролился дождик</div><div class = \'tab\'>На пользу урожаю.</div><br/><div class = \'tab\'>Вот и мы дождались</div><div class = \'tab\'>Радугу красивую,</div><div class = \'tab\'>Лился теплый ливень</div><div class = \'tab\'>На поля озимые.</div><br/><div class = \'tab\'>Тучка разрядилась</div><div class = \'tab\'>Дождиком обильным,</div><div class = \'tab\'>Надежда появилась –</div><div class = \'tab\'>Быть урожаю сильным.</div><br/><div class = \'tab\'>Вот она – отдача</div><div class = \'tab\'>От земли народу!</div><div class = \'tab\'>И это что-то значит,</div><div class = \'tab\'>Ведь такова Природа.</div><br/><div class = \'tab\'>Не обмануть Природу,</div><div class = \'tab\'>Она сильнее всех,</div><div class = \'tab\'>А если труд приложим –</div><div class = \'tab\'>Полюбит нас навек!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(437, 32, 'Прикмети осені', '<div class = \'tab\'>Золотом прикрашені</div><div class = \'tab\'>Листя на деревах,</div><div class = \'tab\'>Вітер шаленіє,</div><div class = \'tab\'>Дощ вже накрапа.</div><br/><div class = \'tab\'>Польові дороги</div><div class = \'tab\'>Блищать здалека</div><div class = \'tab\'>Вірная прикмета –</div><div class = \'tab\'>Осінь наступа.</div><br/><div class = \'tab\'>Ластівки залишили</div><div class = \'tab\'>Гніздечка свої,</div><div class = \'tab\'>Горобці крикливі –</div><div class = \'tab\'>В стріхах сараїв.</div><br/><div class = \'tab\'>Голуби грайливі</div><div class = \'tab\'>Ходять поруч з нами,</div><div class = \'tab\'>До домівок тягнуться</div><div class = \'tab\'>Стугонять дзьобами.</div><br/><div class = \'tab\'>Пізні груші падають,</div><div class = \'tab\'>Падають з дерев,</div><div class = \'tab\'>Яблука осінні</div><div class = \'tab\'>Зібрано за день.</div><br/><div class = \'tab\'>Винограду грони</div><div class = \'tab\'>Висять під вікном,</div><div class = \'tab\'>До вінця наповнені</div><div class = \'tab\'>Чарівним вином.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(438, 32, 'Осень', '<div class = \'tab\'>Осень богата на краски – </div><div class = \'tab\'>Как будто художник лихой</div><div class = \'tab\'>Огромнейший холст разукрасил</div><div class = \'tab\'>Умелой и щедрой рукой.</div><br/><div class = \'tab\'>Весь лес – в золоченых коронах,</div><div class = \'tab\'>Прозрачен и чист небосвод.</div><div class = \'tab\'>В оврагах, полях и на склонах</div><div class = \'tab\'>Добавил затейник пестрот.</div><br/><div class = \'tab\'>С утра в сентябре уже зябко,</div><div class = \'tab\'>А днем нестерпимо печет.</div><div class = \'tab\'>Но солнце уходит с оглядкой,</div><div class = \'tab\'>Дождям начиная отсчет.</div><br/><div class = \'tab\'>Пшеничные зерна готовы</div><div class = \'tab\'>На ложе земное прилечь</div><div class = \'tab\'>Заботы на поле не новы,</div><div class = \'tab\'>Не скинешь их бременем с плеч.</div><br/><div class = \'tab\'>Работы так много, бесспорно,</div><div class = \'tab\'>У всех, кто причастен к земле.</div><div class = \'tab\'>В осенний период повторно</div><div class = \'tab\'>Мы слышим дыханье полей.</div><br/><div class = \'tab\'>Итог трудовой – вот он, рядом,</div><div class = \'tab\'>Сентябрь на исходе уже.</div><div class = \'tab\'>Озимые всходы нас радуют,</div><div class = \'tab\'>Октябрь уж стоит на меже.</div><br/><div class = \'tab\'>Осенние дни – не забава,</div><div class = \'tab\'>Вот свѐклу пора убирать.</div><div class = \'tab\'>Она уродила на славу –</div><div class = \'tab\'>Мы с сахаром будем опять!</div><br/><div class = \'tab\'>На стол урожай прибывает</div><div class = \'tab\'>Груш, яблок и всех овощей.</div><div class = \'tab\'>У нас каждый год так бывает,</div><div class = \'tab\'>Ведь это – порядок вещей.</div><br/><div class = \'tab\'>Погода, конечно, влияет</div><div class = \'tab\'>На всѐ, что «зелѐнка» дает.</div><div class = \'tab\'>От этого точно зависит</div><div class = \'tab\'>Сельский достаток, почет.</div><br/><div class = \'tab\'>Октябрь под конец свирепеет</div><div class = \'tab\'>И листья летят чередой,</div><div class = \'tab\'>Дожди, как те частые гости</div><div class = \'tab\'>Приходят к столу за едой.</div><br/><div class = \'tab\'>Попрятались птицы и звери</div><div class = \'tab\'>И к стужам готовятся вновь,</div><div class = \'tab\'>И песню уж ветер заводит,</div><div class = \'tab\'>Ворчит, как старуха-свекровь.</div><br/><div class = \'tab\'>А вот и ноябрь на подходе:</div><div class = \'tab\'>Морозец за щеки берѐт,</div><div class = \'tab\'>На зверя открыта охота,</div><div class = \'tab\'>И первый снежок уж идет.</div><br/><div class = \'tab\'>И пусть не вся тема раскрыта –</div><div class = \'tab\'>Нюансов осенних не счесть,</div><div class = \'tab\'>Но труд селянина успешный</div><div class = \'tab\'>На празднике осени есть!</div><br/><div class = \'tab\'>Стол длинный и очень широкий</div><div class = \'tab\'>Дарами земными укрыт,</div><div class = \'tab\'>И здравица в праздник аграрный</div><div class = \'tab\'>Мелодией сладкой звучит!</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(439, 32, 'Упрямый листочек', '<div class = \'tab\'>Осенняя листва</div><div class = \'tab\'>Шумит над головой,</div><div class = \'tab\'>Настойчиво мне шепчет,</div><div class = \'tab\'>Тревожит мой покой.</div><br/><div class = \'tab\'>Берусь писать я снова</div><div class = \'tab\'>Красивые слова,</div><div class = \'tab\'>Но тема здесь не нова,</div><div class = \'tab\'>Она как мир стара.</div><br/><div class = \'tab\'>А что там по сюжету?</div><div class = \'tab\'>Скрывается пора,</div><div class = \'tab\'>Любимая поэтом,</div><div class = \'tab\'>Достойная пера.</div><br/><div class = \'tab\'>Иду я по аллее,</div><div class = \'tab\'>Смотрю, а вся листва</div><div class = \'tab\'>Так красочно играет –</div><div class = \'tab\'>В душе моей весна!</div><br/><div class = \'tab\'>Но одинокий листик,</div><div class = \'tab\'>Как флюгер на ветру,</div><div class = \'tab\'>Готов сорваться с ветки,</div><div class = \'tab\'>Кружится по кругу.</div><br/><div class = \'tab\'>Листочек ветру сильному</div><div class = \'tab\'>Так хочет устоять,</div><div class = \'tab\'>Что ветру бесноватому</div><div class = \'tab\'>Не так легко сорвать!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(440, 32, 'Осенние заботы', '<div class = \'tab\'>Любое время года по-своему сияет</div><div class = \'tab\'>Красотами природы и пользой для людей.</div><div class = \'tab\'>Прохладою ночною сентябрь воодушевляет,</div><div class = \'tab\'>И теплым днем, и свежестью идей.</div><br/><div class = \'tab\'>Посев озимых поэтичен</div><div class = \'tab\'>Со всех сторон, как не смотри,</div><div class = \'tab\'>А срок посева ограничен,</div><div class = \'tab\'>И агроном его обязан провести.</div><br/><div class = \'tab\'>В хороший грунт ложится семя,</div><div class = \'tab\'>Одето в пленку от невзгод,</div><div class = \'tab\'>Но на уборку поздних тоже нужно время.</div><div class = \'tab\'>Добротный урожай – запас еды на год.</div><br/><div class = \'tab\'>Октябрьский холодок бодрит,</div><div class = \'tab\'>На юг стремятся птицы.</div><div class = \'tab\'>Они – к теплу, а мы куда?</div><div class = \'tab\'>Зачем нам торопиться?</div><br/><div class = \'tab\'>Землею занят весь народ,</div><div class = \'tab\'>Идет переворот пласта,</div><div class = \'tab\'>И тут спеши: ведь неспроста</div><div class = \'tab\'>Денек осенний кормит целый год.</div><br/><div class = \'tab\'>Уюта мало – слякоть есть,</div><div class = \'tab\'>И череда дождей холодных,</div><div class = \'tab\'>Селянам некогда присесть:</div><div class = \'tab\'>Запас кормов так нужен для животных!</div><br/><div class = \'tab\'>Труда другого не дано –</div><div class = \'tab\'>Взрастить все то, что станет пищей,</div><div class = \'tab\'>Так было, есть, и быть должно,</div><div class = \'tab\'>Ведь мы – народ не нищий!</div><br/><div class = \'tab\'>Дары осенние приятно принимать!</div><div class = \'tab\'>Но при условии одном</div><div class = \'tab\'>Земля готова все отдать:</div><div class = \'tab\'>Ей нужен честный агроном!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(441, 32, 'Вересень', '<div class = \'tab\'>Осінь наступає :</div><div class = \'tab\'>Вже мольберт дістала</div><div class = \'tab\'>Й кольорами грає.</div><div class = \'tab\'>Фарби розкидала</div><br/><div class = \'tab\'>В трави золотаві,</div><div class = \'tab\'>На поля пожовклі,</div><div class = \'tab\'>В листя, що в заплаві</div><div class = \'tab\'>Плавають промоклі.</div><br/><div class = \'tab\'>Всі дуби у лісі</div><div class = \'tab\'>Одягла в кофтину:</div><div class = \'tab\'>Стало все узлісся</div><div class = \'tab\'>Кольору бурштину.</div><br/><div class = \'tab\'>Йдуть дощі ліниві,</div><div class = \'tab\'>Роботі заважають,</div><div class = \'tab\'>Посівну закінчить</div><div class = \'tab\'>Люди поспішають.</div><br/><div class = \'tab\'>Наша світла дата–</div><div class = \'tab\'>Двадцять перше й друге,</div><div class = \'tab\'>Ліпше з ліпших свято –</div><div class = \'tab\'>Це Пречиста друга.</div><br/><div class = \'tab\'>Зáвжди цими днями</div><div class = \'tab\'>Святкують християни,</div><div class = \'tab\'>Дари несуть до храму</div><div class = \'tab\'>Селяни і містяни.</div><br/><div class = \'tab\'>…Вересень скінчився,</div><div class = \'tab\'>І старий лелека</div><div class = \'tab\'>Ось вже намірився</div><div class = \'tab\'>Линуть в даль далеку.</div><br/><div class = \'tab\'>В ті краї летіти,</div><div class = \'tab\'>Туди, де є тепло.</div><div class = \'tab\'>Ми будемо чекати</div><div class = \'tab\'>Та берегти гніздо.</div><br/><div class = \'tab\'>І віримо ми свято</div><div class = \'tab\'>Що вороття відбудеться,</div><div class = \'tab\'>Повернеться до хати,</div><div class = \'tab\'>Знов навесні все збудеться!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(442, 32, 'Жовтень', '<div class = \'tab\'>Жовтень сипле листям</div><div class = \'tab\'>На алеї парку,</div><div class = \'tab\'>Розкидає кольори</div><div class = \'tab\'>Попід саму арку.</div><br/><div class = \'tab\'>Дощик прохолодний</div><div class = \'tab\'>Краплями туману</div><div class = \'tab\'>Зросив листя жовте,</div><div class = \'tab\'>Вводить нас в оману.</div><br/><div class = \'tab\'>Вітерець до сонця</div><div class = \'tab\'>Піднімає зграї,</div><div class = \'tab\'>Листя пожовтіле</div><div class = \'tab\'>Із дерев зриває.</div><br/><div class = \'tab\'>Сонце враз з\'явилося,</div><div class = \'tab\'>Обігріло листя,</div><div class = \'tab\'>Зник туман раптово −</div><div class = \'tab\'>\"Жовтень зупинився\"!</div><br/><div class = \'tab\'>На шляхах до парку</div><div class = \'tab\'>Листя вже опале</div><div class = \'tab\'>Від дощу промокло,</div><div class = \'tab\'>Фарбами не грає.</div><br/><div class = \'tab\'>День наступний в жовтні</div><div class = \'tab\'>Важко угадати.</div><div class = \'tab\'>Будуть дні хороші –</div><div class = \'tab\'>Треба почекати.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(443, 32, 'Начало зимы', '<div class = \'tab\'>Зима постучалась внезапно</div><div class = \'tab\'>В открытое мною окно.</div><div class = \'tab\'>Расслабился я не случайно –</div><div class = \'tab\'>Я в осень влюблен уж давно.</div><br/><div class = \'tab\'>Я осени много доверил</div><div class = \'tab\'>Тайн, привилегий, преданий…</div><div class = \'tab\'>А сколько в стихах я ей вверил</div><div class = \'tab\'>Ласковых слов и признаний!</div><br/><div class = \'tab\'>Снег и мороз в одночасье</div><div class = \'tab\'>Заполнили весь этот свет,</div><div class = \'tab\'>Ночь беспроглядна, несчастна,</div><div class = \'tab\'>Скорей бы дождаться рассвет!</div><br/><div class = \'tab\'>Солнышка нет, и не будет,</div><div class = \'tab\'>Скучно, тоскливо в душе,</div><div class = \'tab\'>День светлый, но быстро убудет,</div><div class = \'tab\'>Скоро уже, в декабре.</div><br/><div class = \'tab\'>Утром дорога опасна,</div><div class = \'tab\'>Коркой ледовой покрыта слегка,</div><div class = \'tab\'>Иней укутал деревья,</div><div class = \'tab\'>И низко плывут облака…</div><br/><div class = \'tab\'>Но снежной дождемся погоды,</div><div class = \'tab\'>И лыжи пойдут, будто сами!</div><div class = \'tab\'>В преддверии Нового года</div><div class = \'tab\'>Мы будем так счастливы с вами!</div><br/><div class = \'tab\'></div><div class = \'tab\'></div><div class = \'tab\'></div><div class = \'tab\'></div><br/><div class = \'tab\'></div><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(444, 32, 'Новый год', '<div class = \'tab\'>Настанет день, придѐт погода,</div><div class = \'tab\'>Кружится снег, мороз бодрит.</div><div class = \'tab\'>Зима, зима – в начале года,</div><div class = \'tab\'>Она за солнышком бежит.</div><br/><div class = \'tab\'>Снежинок белых кутерьма,</div><div class = \'tab\'>Дышать легко, скрипит снежок –</div><div class = \'tab\'>Пришла кудесница Зима,</div><div class = \'tab\'>Покрыла кружевом ледок.</div><br/><div class = \'tab\'>И так из года в год идѐт</div><div class = \'tab\'>Земли святой круговорот.</div><div class = \'tab\'>А в нас самих дитя живѐт,</div><div class = \'tab\'>Что сказку ждѐт под Новый год.</div><br/><div class = \'tab\'>Живѐт любовью и надеждой</div><div class = \'tab\'>К земным утехам и искусам,</div><div class = \'tab\'>И Новый год приносит всем</div><div class = \'tab\'>Святое рождество Исуса.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(445, 32, 'Мороз-художник', '<div class = \'tab\'>Вікно в моїй кімнаті</div><div class = \'tab\'>Покрилось візерунком,</div><div class = \'tab\'>Мороз по двору ходить,</div><div class = \'tab\'>Розкидав кружала.</div><br/><div class = \'tab\'>Скрипить новеньким снігом</div><div class = \'tab\'>Й виводить так неспішно</div><div class = \'tab\'>Мереживо успішно</div><div class = \'tab\'>На склі мого вікна.</div><br/><div class = \'tab\'>… Я був тоді маленький</div><div class = \'tab\'>І вперше це побачив,</div><div class = \'tab\'>Чомусь злякався, плачу,</div><div class = \'tab\'>Та в мами запитав:</div><br/><div class = \'tab\'>– Що коїться на вікнах?</div><div class = \'tab\'>Нічого я не бачу!</div><div class = \'tab\'>Хто ходить біля хати</div><div class = \'tab\'>І вікна закрива?</div><br/><div class = \'tab\'>– Не бійся, мій синочку,</div><div class = \'tab\'>Зима ж, – мороз малює</div><div class = \'tab\'>Малюнки-візерунки</div><div class = \'tab\'>На нашому вікні.</div><br/><div class = \'tab\'>А вранці у віконце</div><div class = \'tab\'>Постукалося сонце:</div><div class = \'tab\'>Розтанули малюнки</div><div class = \'tab\'>І зникли із вікна.</div><br/><div class = \'tab\'>Весь двір – як на долоні!</div><div class = \'tab\'>В вікні я бачу чистім:</div><div class = \'tab\'>Стоять усі дерева</div><div class = \'tab\'>В прозорому намисті!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(446, 32, 'Рябинушка манит...', '<div class = \'tab\'>Рябина зимою</div><div class = \'tab\'>Под окном стоит,</div><div class = \'tab\'>Своим красным цветом</div><div class = \'tab\'>К себе манит, манит...</div><br/><div class = \'tab\'>Стайкой снегириной</div><div class = \'tab\'>Дружно поклевали</div><div class = \'tab\'>И вспорхнули быстро,</div><div class = \'tab\'>Что-то \"прокричали\".</div><br/><div class = \'tab\'>Ягода отменная</div><div class = \'tab\'>На снегу видна,</div><div class = \'tab\'>Птицам красногрудым</div><div class = \'tab\'>Очень уж нужна.</div><br/><div class = \'tab\'>...Снова появились</div><div class = \'tab\'>Большою гурьбою,</div><div class = \'tab\'>Очень веселились,</div><div class = \'tab\'>Что наелись вволю.</div><br/><div class = \'tab\'>А зима морозная</div><div class = \'tab\'>Давит! Ух, держись!</div><div class = \'tab\'>Ягоды красивые</div><div class = \'tab\'>Сберегают жизнь.</div><br/><div class = \'tab\'>Под нашим окошком</div><div class = \'tab\'>Рябинушка стоит,</div><div class = \'tab\'>Своим красным цветом</div><div class = \'tab\'>Снегирей манит.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(447, 32, 'Зимние забавы', '<div class = \'tab\'>Зима, зимушка, зима –</div><div class = \'tab\'>Много снега, много льда,</div><div class = \'tab\'>Зима – лучшая пора,</div><div class = \'tab\'>Деткам радость принесла.</div><br/><div class = \'tab\'>Санки детские бегут,</div><div class = \'tab\'>Словно кони резвые:</div><div class = \'tab\'>Прямо, вправо и в сугроб</div><div class = \'tab\'>С поворота левого.</div><br/><div class = \'tab\'>Поменяли снег на лѐд –</div><div class = \'tab\'>Вот вам и утеха!</div><div class = \'tab\'>Дети встали на коньки –</div><div class = \'tab\'>Ждѐм от них успеха.</div><br/><div class = \'tab\'>Щѐки алые у них –</div><div class = \'tab\'>Жарко видно стало.</div><div class = \'tab\'>Не пришел ещѐ успех</div><div class = \'tab\'>К фигуристам малым.</div><br/><div class = \'tab\'>Тренируйтесь каждый день</div><div class = \'tab\'>Весело и споро –</div><div class = \'tab\'>Будет лучше результат</div><div class = \'tab\'>У детишек скоро.</div><br/><div class = \'tab\'>Зима, зимушка, зима –</div><div class = \'tab\'>Много снега запасла,</div><div class = \'tab\'>Зима – чудная пора,</div><div class = \'tab\'>Деткам радость принесла.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(448, 32, 'Мачуха-зима', '<div class = \'tab\'>Сувора мачуха-зима</div><div class = \'tab\'>Прийшла в окрузі панувати:</div><div class = \'tab\'>Міцним морозом обійма</div><div class = \'tab\'>І снігом прагне засипати.</div><br/><div class = \'tab\'>Птахи, звірята – всі істоти –</div><div class = \'tab\'>В щоденних пошуках кормів.</div><div class = \'tab\'>Під снігом сховані запаси,</div><div class = \'tab\'>Тепла так хочеться усім.</div><br/><div class = \'tab\'>Нарешті сонечко з’явилось!</div><div class = \'tab\'>Ось-ось прокинуться лани</div><div class = \'tab\'>І серце радісно забилось,</div><div class = \'tab\'>Ми так чекаємо весни!</div><br/><div class = \'tab\'>Але на дворі тільки лютий,</div><div class = \'tab\'>Ще править мачуха-зима:</div><div class = \'tab\'>Тріщить морозом, сипле снігом,</div><div class = \'tab\'>І вітер знову підійма.</div><br/><div class = \'tab\'>Селяни, як завжди, в турботі:</div><div class = \'tab\'>Що буде з урожаєм знов?</div><div class = \'tab\'>Озимі сплять ще у дрімоті,</div><div class = \'tab\'>До них у нас своя любов.</div><br/><div class = \'tab\'>Зима мине, весна настане,</div><div class = \'tab\'>А з нею – радості момент:</div><div class = \'tab\'>Забудем зиму неслухняну</div><div class = \'tab\'>Мов незначний експеримент.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(449, 32, 'В очікуванні весни…', '<div class = \'tab\'>Зима набридла до нестями –</div><div class = \'tab\'>Турбує снігом та дощем,</div><div class = \'tab\'>На полі видно мокрі плями,</div><div class = \'tab\'>Усі весну ми ждем-пождем.</div><br/><div class = \'tab\'>Тумани з хмарами приходять,</div><div class = \'tab\'>Негода з ними, мов та тінь,</div><div class = \'tab\'>Та,слава богу, вже відходить</div><div class = \'tab\'>Від нас зимова сплячка-лінь.</div><br/><div class = \'tab\'>Чекаєм співу солов’їв,</div><div class = \'tab\'>У небі журавлиних зграй.</div><div class = \'tab\'>Ми відчуваєм дихання полів</div><div class = \'tab\'>Весною знов розквітне край.</div><br/><div class = \'tab\'>Готові до закінчення зими:</div><div class = \'tab\'>Шукаємо весняний одяг у шухлядах,</div><div class = \'tab\'>На рік ми попрощаємось з саньми,</div><div class = \'tab\'>Вже уявляємо бутони на трояндах.</div><br/><div class = \'tab\'>Ласкаво просимо весну!</div><div class = \'tab\'>Зимі ми скажемо: «ад’ю»!</div><div class = \'tab\'>Побачили природи зміну –</div><div class = \'tab\'>Та й віримо, що будемо в раю!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(450, 32, 'Весна пришла...', '<div class = \'tab\'>Пришла красавица весна</div><div class = \'tab\'>В убранстве свежем и нарядном.</div><div class = \'tab\'>Природа пробудилась ото сна,</div><div class = \'tab\'>Озимые стоят в строю парадном.</div><br/><div class = \'tab\'>Весна пришла, ей нет границ –</div><div class = \'tab\'>Зиме пора сходить с арены.</div><div class = \'tab\'>И льются трели певчих птиц,</div><div class = \'tab\'>Как звуки радостной сирены.</div><br/><div class = \'tab\'>Тепло исходит от полей,</div><div class = \'tab\'>И все аграрии в заботах.</div><div class = \'tab\'>Весною дышится вольней</div><div class = \'tab\'>С мечтами о земных красотах!</div><br/><div class = \'tab\'>И солнце светит по-другому</div><div class = \'tab\'>Всѐ ближе, ближе у зенита.</div><div class = \'tab\'>Весна пришла – уже пришла!</div><div class = \'tab\'>Еѐ победа знаменита!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(451, 32, 'Дождинки растаяли', '<div class = \'tab\'>Поздно ночью тучка</div><div class = \'tab\'>С неба опустилась</div><div class = \'tab\'>Прямо возле дома</div><div class = \'tab\'>Дождем «разродилась».</div><br/><div class = \'tab\'>Дождинки ночные</div><div class = \'tab\'>На кустах растаяли,</div><div class = \'tab\'>Выпали как бисер</div><div class = \'tab\'>Мелкою росой.</div><br/><div class = \'tab\'>Очарован полностью –</div><div class = \'tab\'>Смотрел и любовался,</div><div class = \'tab\'>Покорен был прелестью,</div><div class = \'tab\'>И неземной красой!</div><br/><div class = \'tab\'>Очень я доволен,</div><div class = \'tab\'>Хотя досталась малость,</div><div class = \'tab\'>Что в мире называется</div><div class = \'tab\'>Гармония и радость.</div><br/><div class = \'tab\'>Брызги от дождинок</div><div class = \'tab\'>Утром все растаяли,</div><div class = \'tab\'>На землю опустились</div><div class = \'tab\'>Мелкою росой.</div><br/><div class = \'tab\'>До чего же мило!</div><div class = \'tab\'>Какова же прелесть!</div><div class = \'tab\'>Получил я радость</div><div class = \'tab\'>Божественной рукой.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(452, 32, 'Птенчик', '<div class = \'tab\'>Вышел поутру я</div><div class = \'tab\'>Погулять в саду.</div><div class = \'tab\'>Дышится свободно,</div><div class = \'tab\'>И я иду, иду…</div><br/><div class = \'tab\'>Вдруг слышу: птенчик</div><div class = \'tab\'>Пищит, зовет о помощи,</div><div class = \'tab\'>И лучик солнца теплого</div><div class = \'tab\'>Упал ему в крыло.</div><br/><div class = \'tab\'>Вспорхнул и постарался</div><div class = \'tab\'>Взлететь, но приземлился,</div><div class = \'tab\'>И тут же повторил он</div><div class = \'tab\'>Свою попытку вновь.</div><br/><div class = \'tab\'>Расправил крылья слабые,</div><div class = \'tab\'>Взмахнул еще, еще…</div><div class = \'tab\'>И улетел уверенно</div><div class = \'tab\'>В родимое гнездо.</div><br/><div class = \'tab\'>А тут родная маменька</div><div class = \'tab\'>Кружит вокруг него</div><div class = \'tab\'>И бесконечно рада</div><div class = \'tab\'>За сына своего.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(453, 32, 'Квітневі розваги', '<div class = \'tab\'>Квітень з викрутасами</div><div class = \'tab\'>Вже дійшов до нас,</div><div class = \'tab\'>Холодний вітер з півночі</div><div class = \'tab\'>Пішов в шалений пляс.</div><br/><div class = \'tab\'>А ввечері в таночку</div><div class = \'tab\'>Сніжечок мерехтить,</div><div class = \'tab\'>Вночі та спозаранку</div><div class = \'tab\'>Ще й дощик моросить.</div><br/><div class = \'tab\'>Південний схил схопив тепло,</div><div class = \'tab\'>Зазеленіла \"травка\",</div><div class = \'tab\'>Малеча вийшла всім селом</div><div class = \'tab\'>В квача пограти зранку.</div><br/><div class = \'tab\'>Прийшла пора розваг</div><div class = \'tab\'>Дитячих на природі,</div><div class = \'tab\'>Бо вдома засиділися</div><div class = \'tab\'>І мріють про пригоди.</div><br/><div class = \'tab\'>Команди стали по місцях,</div><div class = \'tab\'>Суддя сигнал подав –</div><div class = \'tab\'>І враз дитячий колектив</div><div class = \'tab\'>Активно весь заграв.</div><br/><div class = \'tab\'>І так загрались у квача,</div><div class = \'tab\'>Що вже батьки зовуть,</div><div class = \'tab\'>І діточки щасливі</div><div class = \'tab\'>Додому всі ідуть...</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(454, 32, 'Солнце и мы', '<div class = \'tab\'>Тучи свинцовые,</div><div class = \'tab\'>Тучи осенние</div><div class = \'tab\'>Прочно зависли</div><div class = \'tab\'>Над головой.</div><br/><div class = \'tab\'>Ноги устали –</div><div class = \'tab\'>Душа леденеет,</div><div class = \'tab\'>Думы витают</div><div class = \'tab\'>О жизни такой.</div><br/><div class = \'tab\'>Поздняя осень −</div><div class = \'tab\'>Солнца так мало,</div><div class = \'tab\'>Холод жутчайший,</div><div class = \'tab\'>А газ дорогой.</div><br/><div class = \'tab\'>А солнце родное</div><div class = \'tab\'>Прорвалось сквозь тучи,</div><div class = \'tab\'>Теплом отогрело –</div><div class = \'tab\'>Мы всѐ же живучи.</div><br/><div class = \'tab\'>Чтобы согреться,</div><div class = \'tab\'>Двигаться надо</div><div class = \'tab\'>Взад и вперѐд</div><div class = \'tab\'>По дороге одной.</div><br/><div class = \'tab\'>Всех обогрело –</div><div class = \'tab\'>Вселило добро,</div><div class = \'tab\'>Лица раскрасило –</div><div class = \'tab\'>Всѐ ожило.</div><br/><div class = \'tab\'>Будьте здоровы,</div><div class = \'tab\'>Славьте его!</div><div class = \'tab\'>Солнцу ведь с нами</div><div class = \'tab\'>Не так уж легко.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(455, 32, 'Рано утром', '<div class = \'tab\'>Обильна роса</div><div class = \'tab\'>Уходит уже прочь,</div><div class = \'tab\'>Ведь солнце побеждает</div><div class = \'tab\'>Увядающую ночь.</div><br/><div class = \'tab\'>Листва блестит</div><div class = \'tab\'>От капелек росы,</div><div class = \'tab\'>А лучики играют</div><div class = \'tab\'>И млеют от красы.</div><br/><div class = \'tab\'>Разбросан бисер капель</div><div class = \'tab\'>Умелою рукой,</div><div class = \'tab\'>Смотрю и отдыхаю</div><div class = \'tab\'>Уставшею душой.</div><br/><div class = \'tab\'>И как не восхищаться</div><div class = \'tab\'>Сверканием лучей,</div><div class = \'tab\'>Душа стаѐт моложе,</div><div class = \'tab\'>Заметен блеск очей.</div><br/><div class = \'tab\'>Обильная роса</div><div class = \'tab\'>Ушла с листвы уж прочь</div><div class = \'tab\'>И светлый день уводит</div><div class = \'tab\'>Стареющую ночь.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(456, 32, 'Зима на пороге', '<div class = \'tab\'>Осень в разгаре –</div><div class = \'tab\'>Оставила следы,</div><div class = \'tab\'>Листья все опали,</div><div class = \'tab\'>Дозрели плоды.</div><br/><div class = \'tab\'>Первый снег порошею</div><div class = \'tab\'>Укрыл поля озимых,</div><div class = \'tab\'>Рябинки, как горошины,</div><div class = \'tab\'>Висят для птиц \"родимых\".</div><br/><div class = \'tab\'>Видны поля зелѐные,</div><div class = \'tab\'>Деревья уж темны,</div><div class = \'tab\'>Скворцы неугомонные</div><div class = \'tab\'>В поисках еды.</div><br/><div class = \'tab\'>Зима на пороге –</div><div class = \'tab\'>Видны еѐ следы,</div><div class = \'tab\'>Коньки уж наготове –</div><div class = \'tab\'>К зиме готовы мы.</div><br/><div class = \'tab\'>Детишки деревенские</div><div class = \'tab\'>Готовятся к зиме,</div><div class = \'tab\'>Снежинки суетятся,</div><div class = \'tab\'>Как-будто в кутерьме.</div><br/><div class = \'tab\'>Январь мы любим страстно –</div><div class = \'tab\'>\"Прийди-ка поскорей!\"</div><div class = \'tab\'>Ведь Новый год прекрасен</div><div class = \'tab\'>Для взрослых и детей.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(457, 32, '31-е грудня', '<div class = \'tab\'>Грудень добіг до кінця,</div><div class = \'tab\'>Темряві вже не дозволено</div><div class = \'tab\'>Впливати на наші серця,</div><div class = \'tab\'>На психіку й нерви оголені.</div><br/><div class = \'tab\'>Грудень давав нам потроху:</div><div class = \'tab\'>Світла та сонця щодня,</div><div class = \'tab\'>Сніг і волога удосталь</div><div class = \'tab\'>Впали на луки й поля.</div><br/><div class = \'tab\'>Озимі надійно заховані,</div><div class = \'tab\'>Їм тепло, як в нових чоботях.</div><div class = \'tab\'>Ми ж з вами сьогодні захоплені</div><div class = \'tab\'>У справах, у нових турботах.</div><br/><div class = \'tab\'>Вже підсумки грудня підведено,</div><div class = \'tab\'>Згадаймо його, мовби чудо.</div><div class = \'tab\'>Ми з вами нічого не змінимо,</div><div class = \'tab\'>Бо зранку вже січень повсюди.</div><br/><div class = \'tab\'>Думкàми живем новорічними –</div><div class = \'tab\'>Різдво на порозі стоїть.</div><div class = \'tab\'>Ми в мріях, щоб стати щасливими</div><div class = \'tab\'>В наступному році. − За мить!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(458, 32, 'Январские ѐлки', '<div class = \'tab\'>Ели припорошило</div><div class = \'tab\'>Снежною крупою,</div><div class = \'tab\'>Той что, как горошины,</div><div class = \'tab\'>Выпала зимою.</div><br/><div class = \'tab\'>Ёлочки пушистые –</div><div class = \'tab\'>Вечно зелѐные,</div><div class = \'tab\'>С шишками душистыми,</div><div class = \'tab\'>Как игрушки новые.</div><br/><div class = \'tab\'>Я смотрю в окошко,</div><div class = \'tab\'>Вижу эти ели –</div><div class = \'tab\'>Вот уж понемножку</div><div class = \'tab\'>Строчки полетели.</div><br/><div class = \'tab\'>Строфы, рифмы ладные</div><div class = \'tab\'>В памяти рождаются.</div><div class = \'tab\'>В эти дни прохладные</div><div class = \'tab\'>Строки не кончаются.</div><br/><div class = \'tab\'>Птицы \"зголоднели\" –</div><div class = \'tab\'>Прячутся в иголках,</div><div class = \'tab\'>Что заледенели</div><div class = \'tab\'>На замѐрзших ѐлках.</div><br/><div class = \'tab\'>Всѐ же наступают</div><div class = \'tab\'>Перемены года</div><div class = \'tab\'>И январь кончается –</div><div class = \'tab\'>Уж не та погода.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(459, 32, 'Колючий вітер', '<div class = \'tab\'>Вітер холодний, північний –</div><div class = \'tab\'>Шкодить південним степам.</div><div class = \'tab\'>Від нього постійна біда,</div><div class = \'tab\'>Але ж тут природа жива.</div><br/><div class = \'tab\'>Привабливість теплого краю</div><div class = \'tab\'>Завжди відчували усі.</div><div class = \'tab\'>В краю цім пронизливий вітер</div><div class = \'tab\'>Долали, тримали кордони свої.</div><br/><div class = \'tab\'>Бували часи лихоліття –</div><div class = \'tab\'>Слабкими ставали відраз.</div><div class = \'tab\'>Зі зрадою боролись уміло,</div><div class = \'tab\'>Вдавалось позбутись образ.</div><br/><div class = \'tab\'>На жаль, і сьогодні вже зранку</div><div class = \'tab\'>Дме вітер ганебно чужий,</div><div class = \'tab\'>Колючий, зі снігом і холодом −</div><div class = \'tab\'>Для півдня чомусь він пустий.</div><br/><div class = \'tab\'>Озброєний хмарами снігу,</div><div class = \'tab\'>Брехнею, ненавистю, люттю,</div><div class = \'tab\'>Убивчою силою граду,</div><div class = \'tab\'>Брудними словами із аду.</div><br/><div class = \'tab\'>Вітер колючий, північний</div><div class = \'tab\'>Не на користь широким ланам −</div><div class = \'tab\'>Від нього тут тільки негода.</div><div class = \'tab\'>Навіщо потрібен він нам?</div><br/><div class = \'tab\'>Повстанемо, браття, сміливо</div><div class = \'tab\'>Назустріч навалі холодній,</div><div class = \'tab\'>З\'єднаємось серцем і тілом,</div><div class = \'tab\'>З\'єднаємось в думці ми одній.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(460, 32, 'Море', '<div class = \'tab\'>Вранці море синє</div><div class = \'tab\'>В берег б\"ється сильно,</div><div class = \'tab\'>Вдень воно блакитне,</div><div class = \'tab\'>Стелеться повільно.</div><br/><div class = \'tab\'>Бірюзове звечора –</div><div class = \'tab\'>Лагідно шепоче,</div><div class = \'tab\'>А вночі темніє –</div><div class = \'tab\'>Хвилями гуркоче.</div><br/><div class = \'tab\'>Кожен день постійно</div><div class = \'tab\'>Свариться з собою,</div><div class = \'tab\'>Місячною стежкою</div><div class = \'tab\'>З небом гомонить.</div><br/><div class = \'tab\'>В нову ніч щезає –</div><div class = \'tab\'>Сріблом все блищить,</div><div class = \'tab\'>Кольорами грає</div><div class = \'tab\'>В цю щасливу мить.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(461, 32, 'Весеннее настроение', '<div class = \'tab\'>Весна улыбается мне,</div><div class = \'tab\'>Лучами раздвинув простор,</div><div class = \'tab\'>Вижу я в чистом окне</div><div class = \'tab\'>Зелѐных полей семафор.</div><br/><div class = \'tab\'>Радость клокочет в груди,</div><div class = \'tab\'>Душа переполнена счастьем:</div><div class = \'tab\'>Чувствую силу любви</div><div class = \'tab\'>В борьбе с надоевшим ненастьем.</div><br/><div class = \'tab\'>Весна всколыхнула меня,</div><div class = \'tab\'>Любовно наполнила страстью,</div><div class = \'tab\'>Живыми увидел поля –</div><div class = \'tab\'>Как мало нам надо для счастья!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(462, 32, 'О душе', '<div class = \'tab\'>Весна пришла –</div><div class = \'tab\'>Душа поѐт,</div><div class = \'tab\'>Весна пришла –</div><div class = \'tab\'>Пошла на взлѐт.</div><br/><div class = \'tab\'>Летит красиво –</div><div class = \'tab\'>Лучами светит.</div><div class = \'tab\'>Душе приятно –</div><div class = \'tab\'>Она ответит.</div><br/><div class = \'tab\'>Весна пришла,</div><div class = \'tab\'>Зажгла все свечи,</div><div class = \'tab\'>Моя душа</div><div class = \'tab\'>Ждѐт новой встречи.</div><br/><div class = \'tab\'>Душа страдает</div><div class = \'tab\'>Этой весной,</div><div class = \'tab\'>Душа желает</div><div class = \'tab\'>Любви святой.</div><br/><div class = \'tab\'>В моей душе</div><div class = \'tab\'>Любовь пылает,</div><div class = \'tab\'>Любовь во мне</div><div class = \'tab\'>Весной играет.</div><br/><div class = \'tab\'>Весна пришла –</div><div class = \'tab\'>Душа поѐт,</div><div class = \'tab\'>Весна пришла –</div><div class = \'tab\'>Пошла на взлѐт.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(463, 32, 'Дарение весны', '<div class = \'tab\'>Тучки разрядились</div><div class = \'tab\'>Дождиком весенним –</div><div class = \'tab\'>Тут же проявились</div><div class = \'tab\'>Полюшком ячменным.</div><br/><div class = \'tab\'>Всѐ зазеленело,</div><div class = \'tab\'>Кругом так приятно;</div><div class = \'tab\'>Как-то по-весеннему</div><div class = \'tab\'>Стало всѐ понятно.</div><br/><div class = \'tab\'>Птицы, что замолкли,</div><div class = \'tab\'>Вдруг запели снова,</div><div class = \'tab\'>Радуются всходам –</div><div class = \'tab\'>Жизни всей основа.</div><br/><div class = \'tab\'>Стебелѐк ячменный</div><div class = \'tab\'>В юности красив,</div><div class = \'tab\'>Листики расправил,</div><div class = \'tab\'>Набрал немножко сил.</div><br/><div class = \'tab\'>...Смотрим, обсуждаем,</div><div class = \'tab\'>Дивуемся мы,</div><div class = \'tab\'>Любезно принимаем</div><div class = \'tab\'>Дарение весны.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(464, 32, 'Сила Земли', '<div class = \'tab\'>Взлететь от Земли высоко –</div><div class = \'tab\'>Увидеть простор голубой,</div><div class = \'tab\'>Почувствовать Силу Земли,</div><div class = \'tab\'>Земли для нас всех дорогой.</div><br/><div class = \'tab\'>Дорогая ты наша Земля!</div><div class = \'tab\'>Ты многих землян воспитала:</div><div class = \'tab\'>Кормила, поила, растила,</div><div class = \'tab\'>И в этом во всем твоя сила.</div><br/><div class = \'tab\'>Пройти по Земле далеко</div><div class = \'tab\'>И воздух всей грудью вдохнуть,</div><div class = \'tab\'>Увидеть наш труд наяву,</div><div class = \'tab\'>Усталость от бед всех стряхнуть.</div><br/><div class = \'tab\'>Дорогая ты наша Земля!</div><div class = \'tab\'>Ты многих землян воспитала:</div><div class = \'tab\'>Кормила, поила, растила,</div><div class = \'tab\'>И в этом во всем твоя сила.</div><br/><div class = \'tab\'>Пройтись босиком по траве –</div><div class = \'tab\'>Слегка ощутить прохладу,</div><div class = \'tab\'>Почувствовать Силу Земли –</div><div class = \'tab\'>Еѐ получить в награду.</div><br/><div class = \'tab\'>Дорогая ты наша Земля!</div><div class = \'tab\'>Ты многих землян воспитала:</div><div class = \'tab\'>Кормила, поила, растила,</div><div class = \'tab\'>И в этом во всем твоя сила.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(465, 32, 'Свободная птица', '<div class = \'tab\'>Свободная птица –</div><div class = \'tab\'>Ранена в крыло,</div><div class = \'tab\'>С неба опустилась</div><div class = \'tab\'>Прямо за селом.</div><br/><div class = \'tab\'>Птица живая –</div><div class = \'tab\'>Оставлена стаей,</div><div class = \'tab\'>Ищет тепла</div><div class = \'tab\'>У жителей села.</div><br/><div class = \'tab\'>На озере холод –</div><div class = \'tab\'>Сковал воду лѐд,</div><div class = \'tab\'>Ужаснейший голод</div><div class = \'tab\'>Со всех сторон \"ползѐт\".</div><br/><div class = \'tab\'>Зверьѐ поджидает –</div><div class = \'tab\'>Готовы сожрать,</div><div class = \'tab\'>Такое бывает...</div><div class = \'tab\'>Без слѐз нельзя писать.</div><br/><div class = \'tab\'>Но тут православный</div><div class = \'tab\'>С доброй душой,</div><div class = \'tab\'>Заметил калеку</div><div class = \'tab\'>В беде вот такой.</div><br/><div class = \'tab\'>Забрал он к себе –</div><div class = \'tab\'>Помог ей в беде,</div><div class = \'tab\'>Пригрел, накормил,</div><div class = \'tab\'>Крыло подлечил.</div><br/><div class = \'tab\'>Зима пролетела –</div><div class = \'tab\'>Морозы ушли,</div><div class = \'tab\'>Дороги просохли,</div><div class = \'tab\'>Открылись пути.</div><br/><div class = \'tab\'>Почувствовав силу,</div><div class = \'tab\'>Птица сама</div><div class = \'tab\'>Взмахнула крылами,</div><div class = \'tab\'>К водичке ушла.</div><br/><div class = \'tab\'>На озеро снова</div><div class = \'tab\'>Весна опустилась,</div><div class = \'tab\'>И птицы всей стаей</div><div class = \'tab\'>Опять возвратились.</div><br/><div class = \'tab\'>Красавец-самец</div><div class = \'tab\'>\"Ну, сущий нахал\",</div><div class = \'tab\'>Подал ей свой голос,</div><div class = \'tab\'>Крылом помахал.</div><br/><div class = \'tab\'>И свили совместно</div><div class = \'tab\'>Родное гнездо,</div><div class = \'tab\'>Птенцов народили</div><div class = \'tab\'>Без глупеньких слов.</div><br/><div class = \'tab\'>И снова взлетели</div><div class = \'tab\'>Все! Дружной семьѐй,</div><div class = \'tab\'>Довольны остались</div><div class = \'tab\'>Своею судьбой.</div><br/><div class = \'tab\'>Свободные птицы</div><div class = \'tab\'>Летят над землѐй,</div><div class = \'tab\'>И всех, кто помог им,</div><div class = \'tab\'>Зовут за собой.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(466, 32, 'Березові сльози', '<div class = \'tab\'>Білі берези</div><div class = \'tab\'>На вітрі стоять,</div><div class = \'tab\'>Крапельки, як сльози,</div><div class = \'tab\'>На корі блищать.</div><br/><div class = \'tab\'>Змінилася погода –</div><div class = \'tab\'>Настала весна,</div><div class = \'tab\'>Березова \"спільнота\"</div><div class = \'tab\'>Знову ожила.</div><br/><div class = \'tab\'>Берези встали зранку</div><div class = \'tab\'>Від сплячки голодної</div><div class = \'tab\'>Разом оговтались</div><div class = \'tab\'>Від зими холодної.</div><br/><div class = \'tab\'>Звідки ж ваші сльози,</div><div class = \'tab\'>Чому у скорботі?</div><div class = \'tab\'>Не хвилюйтесь дуже –</div><div class = \'tab\'>Що в вас за турботи?</div><br/><div class = \'tab\'>Люди, схаменіться!</div><div class = \'tab\'>Кору не руйнуйте,</div><div class = \'tab\'>Березам поклоніться –</div><div class = \'tab\'>Вам на користь буде.</div><br/><div class = \'tab\'>Сонечко пригріло –</div><div class = \'tab\'>Все навкруг вирує,</div><div class = \'tab\'>Листочки з\"явилися –</div><div class = \'tab\'>Птах гніздо будує.</div><br/><div class = \'tab\'>Білі берези</div><div class = \'tab\'>На сонці стоять,</div><div class = \'tab\'>Крапельки-сльози</div><div class = \'tab\'>Уже не блищать.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(467, 32, 'Річка', '<div class = \'tab\'>Тече вода прохолодна –</div><div class = \'tab\'>Вранці прибуває,</div><div class = \'tab\'>Вона в Удах взимку, влітку –</div><div class = \'tab\'>Завжди тут буває.</div><br/><div class = \'tab\'>Річка ця є невеличка,</div><div class = \'tab\'>Береги зелені.</div><div class = \'tab\'>Кожен день іду до неї,</div><div class = \'tab\'>До моєї нені.</div><br/><div class = \'tab\'>Річка, річка – мов та ненька</div><div class = \'tab\'>Мене зустрічає,</div><div class = \'tab\'>В ній все небо синьооке</div><div class = \'tab\'>Променями грає.</div><br/><div class = \'tab\'>До містечка проводжає,</div><div class = \'tab\'>Свариться зі мною,</div><div class = \'tab\'>Вона мене розважає,</div><div class = \'tab\'>Біжить стороною.</div><br/><div class = \'tab\'>Навкруги природа мила –</div><div class = \'tab\'>Річку поважаю,</div><div class = \'tab\'>Намалюю я в картині</div><div class = \'tab\'>Все, що пам\"ятаю.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(468, 32, '2017-й рік', '<div class = \'tab\'>Весна прохолодна, як звір той голодна,</div><div class = \'tab\'>Підкралася раптом – зненацька,</div><div class = \'tab\'>Але не вдалося їй нас обдурити,</div><div class = \'tab\'>Ми будемо довго-предовго ще жити.</div><br/><div class = \'tab\'>Літо посушливе з сонцем пекучим</div><div class = \'tab\'>Задуми наші звело нанівець,</div><div class = \'tab\'>І тільки завзятість та віра в майбутнє –</div><div class = \'tab\'>Ми нездоланні і це не кінець...</div><br/><div class = \'tab\'>Осінь багата, яскрава, як свято,</div><div class = \'tab\'>Пройшла блискавично свій шлях.</div><div class = \'tab\'>Зима на порозі, і ми мов в облозі</div><div class = \'tab\'>Холодній і лютій, аж жах.</div><br/><div class = \'tab\'>Давайте! Складемо план новорічний,</div><div class = \'tab\'>План переможний в наступному році.</div><div class = \'tab\'>Ми віримо разом, що Перемога</div><div class = \'tab\'>Буде на нашому боці!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1);
INSERT INTO `LYRICS` (`id`, `set_id`, `name`, `content`, `write_date`, `author`, `description`, `author_comment`, `lyrics_type`, `TABBED`, `BRS`, `STROPHE_LENGTH`, `STROPHE_DELIM`, `LINE_WRAPPER`, `LINE_WRAPPER_END`, `LINE_WRAPPER_TABBED`, `LINE_WRAPPER_TABBED_END`, `auto_format`) VALUES
(469, 32, 'Зимушка-зима', '<div class = \'tab\'>Зима, зимушка, зима –</div><div class = \'tab\'>Много снега, много льда.</div><div class = \'tab\'>Зима, зимушка, зима</div><div class = \'tab\'>Детям радость принесла.</div><br/><div class = \'tab\'>По полям и по оврагам</div><div class = \'tab\'>Снега много нанесла,</div><div class = \'tab\'>По опушкам, по лугам</div><div class = \'tab\'>Разложила по стогам.</div><br/><div class = \'tab\'>Рано утром смельчаки</div><div class = \'tab\'>Споро делают лыжню</div><div class = \'tab\'>И родителям на диво</div><div class = \'tab\'>Увлекли всю малышню.</div><br/><div class = \'tab\'>Стали ровно по линейке –</div><div class = \'tab\'>Им сугробы не страшны,</div><div class = \'tab\'>Полетели! Словно птицы –</div><div class = \'tab\'>Крики радости слышны...</div><br/><div class = \'tab\'>Лѐд на солнышке блестит.</div><div class = \'tab\'>Он на речке стал прочней</div><div class = \'tab\'>Под ногами не хрустит –</div><div class = \'tab\'>Заиграли все в хоккей.</div><br/><div class = \'tab\'>Зима, зимушка, зима</div><div class = \'tab\'>Много снега, много льда.</div><div class = \'tab\'>Зима, зимушка, зима</div><div class = \'tab\'>Нам всем радость принесла!</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(470, 32, 'Ветер-разбойник', '<div class = \'tab\'>Ветер, ветер, ветерок –</div><div class = \'tab\'>Ты нам надоел,</div><div class = \'tab\'>Сколько можно так шуметь,</div><div class = \'tab\'>Отвлекать от важных дел.</div><br/><div class = \'tab\'>Мы же терпим, как злодея –</div><div class = \'tab\'>Сколько бед наделал ты,</div><div class = \'tab\'>Повалил деревья наземь</div><div class = \'tab\'>И разрушил все мосты.</div><br/><div class = \'tab\'>Мы хотим спокойной жизни,</div><div class = \'tab\'>Чтоб творить дела.</div><div class = \'tab\'>Ты врываешься без спросу,</div><div class = \'tab\'>Превращаешь всѐ в \"дрова\".</div><br/><div class = \'tab\'>Сушишь землю на полях.</div><div class = \'tab\'>Валишь все растения.</div><div class = \'tab\'>Что за польза от тебя?!</div><div class = \'tab\'>Нет тебе прощения!</div><br/><div class = \'tab\'>Ты – потомок Посейдона,</div><div class = \'tab\'>Перестань пугать детей,</div><div class = \'tab\'>Укроти ты свой характер,</div><div class = \'tab\'>Уважай же нас людей.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(471, 32, 'Посуха', '<div class = \'tab\'>Сухе повітря забирає</div><div class = \'tab\'>Останнії краплиночки вологи,</div><div class = \'tab\'>Рослини мужньо виживають</div><div class = \'tab\'>У викрутасах кепської погоди.</div><br/><div class = \'tab\'>У ґрунті утворилися щілини,</div><div class = \'tab\'>Скрутилися листочки у кільце,</div><div class = \'tab\'>Озимі ледве виживають</div><div class = \'tab\'>І ярим не подобається це.</div><br/><div class = \'tab\'>Селяни збитки вже рахують,</div><div class = \'tab\'>Надію мають та чекають,</div><div class = \'tab\'>У Бога просять урожаю,</div><div class = \'tab\'>Своєю вірністю вражають.</div><br/><div class = \'tab\'>І скільки треба наступати</div><div class = \'tab\'>На граблі, пастки і негоду,</div><div class = \'tab\'>Потрібно урядовців всіх дістати,</div><div class = \'tab\'>Щоб в Міністерстві дбали про природу.</div><br/><div class = \'tab\'>В країнах інших вже дійшли –</div><div class = \'tab\'>Живуть усупереч погоді.</div><div class = \'tab\'>Візьміть в Ізраїлі готову</div><div class = \'tab\'>Програму зрошення та й годі!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(472, 32, 'Поле', '<div class = \'tab\'>Поле пшеницы янтарное</div><div class = \'tab\'>Хозяина ждѐт уж с утра,</div><div class = \'tab\'>Поле такое родное –</div><div class = \'tab\'>Отменная здесь чистота!</div><br/><div class = \'tab\'>Завтра прибудут комбайны</div><div class = \'tab\'>Пшеницу с полей убирать,</div><div class = \'tab\'>И знойное солнце на небе</div><div class = \'tab\'>На помощь приходит опять.</div><br/><div class = \'tab\'>Дни наступили тяжѐлые –</div><div class = \'tab\'>Спеши, \"шевелись\" агроном,</div><div class = \'tab\'>В бункер посыплются резво</div><div class = \'tab\'>Зѐрна тяжѐлые вновь.</div><br/><div class = \'tab\'>Так всѐ сложилось в природе –</div><div class = \'tab\'>Замкнутый видим мы круг.</div><div class = \'tab\'>Аграриев труд благородный,</div><div class = \'tab\'>Где агроном им всем друг.</div><br/><div class = \'tab\'>Поле пшеницы янтарное</div><div class = \'tab\'>Колосом спелым манит.</div><div class = \'tab\'>Боженька даст им погоду –</div><div class = \'tab\'>Выполнить долг свой велит.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(473, 32, 'Шалений супротив', '<div class = \'tab\'>Дощенту літо догоріло,</div><div class = \'tab\'>Осінь готова до змін –</div><div class = \'tab\'>Стоїть на порозі, в облозі.</div><div class = \'tab\'>Шкільний пролунав перший дзвін.</div><br/><div class = \'tab\'>Всі діти до школи ідуть,</div><div class = \'tab\'>А дощ накрапає зненацька.</div><div class = \'tab\'>Корми вже до ферми везуть –</div><div class = \'tab\'>Погода сьогодні дурацька.</div><br/><div class = \'tab\'>Тощо пересічний господар</div><div class = \'tab\'>Без знань катаклізмів таких,</div><div class = \'tab\'>І як виживати аграрію</div><div class = \'tab\'>В умовах не зовсім простих.</div><br/><div class = \'tab\'>Вночі, як завжди, прохолодно,</div><div class = \'tab\'>Вдень спекою диха земля,</div><div class = \'tab\'>Пекучєє сонце набридло,</div><div class = \'tab\'>Нестерпним здається життя.</div><br/><div class = \'tab\'>Ніщо не зупинить господаря</div><div class = \'tab\'>В бажанні боротися проти</div><div class = \'tab\'>Стихії жорстоко природної</div><div class = \'tab\'>В сучаснім житті та супротиві.</div><br/><div class = \'tab\'>Всю мудрість, знання та здоров\'я</div><div class = \'tab\'>Аграрій направить на те,</div><div class = \'tab\'>Як вміло завершить завдання,</div><div class = \'tab\'>А успіх та шана прийде!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(474, 32, 'Любимый сюжет', '<div class = \'tab\'>Зима пришла в наши края</div><div class = \'tab\'>Блеснула снегом, льдом,</div><div class = \'tab\'>Любовь проснулася моя</div><div class = \'tab\'>К еѐ красотам с холодом.</div><br/><div class = \'tab\'>Наверно я забыл еѐ –</div><div class = \'tab\'>Увлѐкся сильно только летом,</div><div class = \'tab\'>Теперь я вижу, что напрасно –</div><div class = \'tab\'>Не заслужила зима это.</div><br/><div class = \'tab\'>Поеду срочно в край родимый</div><div class = \'tab\'>Дорогой зимнею своей,</div><div class = \'tab\'>Найду для глаз сюжет любимый,</div><div class = \'tab\'>Оставлю в памяти людей.</div><br/><div class = \'tab\'>Пейзажем зимним удивляюсь –</div><div class = \'tab\'>Деревья словно пуховик,</div><div class = \'tab\'>Полями снежными любуюсь</div><div class = \'tab\'>Мой взор туда уже проник.</div><br/><div class = \'tab\'>Сверкает снег в превратностях погоды,</div><div class = \'tab\'>Согретый солнцем горячо,</div><div class = \'tab\'>Кладу на холст мазки, как строчки оды,</div><div class = \'tab\'>Во славу края моего.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(475, 33, 'Сила Правди', '<div class = \'tab\'>Минає час, спливають роки</div><div class = \'tab\'>На тлі бурхливого життя.</div><div class = \'tab\'>Були колись і ширші кроки,</div><div class = \'tab\'>І більш чітким серцебиття...</div><br/><div class = \'tab\'>Давно вже став я дідусем</div><div class = \'tab\'>І маю внуків-учнів.</div><div class = \'tab\'>Я розповісти хочу їм</div><div class = \'tab\'>Історію минулих днів.</div><br/><div class = \'tab\'>Послухайте, всі, небайдужі,</div><div class = \'tab\'>Хто має розум і знання,</div><div class = \'tab\'>Й завжди хто повнить душі</div><div class = \'tab\'>Правдивим словом-надбанням.</div><br/><div class = \'tab\'>Вам може стати у пригоді</div><div class = \'tab\'>Моє відверте послання.</div><div class = \'tab\'>Згадайте також при нагоді</div><div class = \'tab\'>Подій сучасних пізнання.</div><br/><div class = \'tab\'>…Згадую дитинство</div><div class = \'tab\'>Босе і голодне.</div><div class = \'tab\'>Бачив і безчинство,</div><div class = \'tab\'>Бачив й бунт народний.</div><br/><div class = \'tab\'>А хотілось щастя</div><div class = \'tab\'>І свободи люду,</div><div class = \'tab\'>Тому й виникали</div><div class = \'tab\'>Заколоти всюди.</div><br/><div class = \'tab\'>Горем скористались</div><div class = \'tab\'>Злодії-пройдохи:</div><div class = \'tab\'>До влади дірвались</div><div class = \'tab\'>На крові епохи.</div><br/><div class = \'tab\'>Про достойну працю,</div><div class = \'tab\'>Ворогів, комуни</div><div class = \'tab\'>Головний злодюга</div><div class = \'tab\'>Всім віщав з трибуни.</div><br/><div class = \'tab\'>Праця примусова,</div><div class = \'tab\'>Розстріли, знущання,</div><div class = \'tab\'>ГУЛАГів розбудова –</div><div class = \'tab\'>Ось його надбання.</div><br/><div class = \'tab\'>Село занапастили,</div><div class = \'tab\'>За хліб – була стаття.</div><div class = \'tab\'>А люди все терпіли</div><div class = \'tab\'>І вірили в життя!</div><br/><div class = \'tab\'>Та Воля й Правди сила,</div><div class = \'tab\'>Про Господа думки</div><div class = \'tab\'>Не вбили – захистили</div><div class = \'tab\'>У грізні ті роки.</div><br/><div class = \'tab\'>А далі все страшніше:</div><div class = \'tab\'>Війна і окупація...</div><div class = \'tab\'>Та стали ми сильнішими,</div><div class = \'tab\'>Бо вистояла нація!</div><br/><div class = \'tab\'>Горе й сліз потоки</div><div class = \'tab\'>В усіх простих людей</div><div class = \'tab\'>У повоєнні роки</div><div class = \'tab\'>Не сходили з очей...</div><br/><div class = \'tab\'>А від ЦК партійного –</div><div class = \'tab\'>Пайок мініатюрний,</div><div class = \'tab\'>Для люду непокірного –</div><div class = \'tab\'>Процес номенклатурний.</div><br/><div class = \'tab\'>Вашій прабабусі</div><div class = \'tab\'>Теж дісталось лиха!</div><div class = \'tab\'>Тоді ми, ще безвусі,</div><div class = \'tab\'>Лиш чули: плаче тихо...</div><br/><div class = \'tab\'>Та щирості шляхетної</div><div class = \'tab\'>Не загубила слід,</div><div class = \'tab\'>Правдивості безсмертної</div><div class = \'tab\'>Був символом наш рід!</div><br/><div class = \'tab\'>У чому сила наша?</div><div class = \'tab\'>У Правді, тільки в Правді!</div><div class = \'tab\'>А від брехні імперської</div><div class = \'tab\'>Жуть іде насправді.</div><br/><div class = \'tab\'>Важко нам сьогодні:</div><div class = \'tab\'>Живем в нелегкий час.</div><div class = \'tab\'>Та Правда, та, народна –</div><div class = \'tab\'>Ще більш єднає нас.</div><br/><div class = \'tab\'>Тому і вам кажу я:</div><div class = \'tab\'>Цю Правду не здолати.</div><div class = \'tab\'>Девіз наш: Мир, Свобода –</div><div class = \'tab\'>За це разом стояти!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(476, 33, 'Корівка-рятівниця', '<div class = \'tab\'>На лавці глечик молока...</div><div class = \'tab\'>Яке воно духмяне!</div><div class = \'tab\'>Я бачив голод здалека –</div><div class = \'tab\'>Тому лице рум’яне.</div><br/><div class = \'tab\'>В той час я був мале дитя.</div><div class = \'tab\'>Корова Майка отелилась,</div><div class = \'tab\'>І доля вся, й моє життя</div><div class = \'tab\'>У сорок шостому не зупинились.</div><br/><div class = \'tab\'>Нелюдська і тяжка біда</div><div class = \'tab\'>На Курщині в той час творилась:</div><div class = \'tab\'>Нестерпна спека та страшна,</div><div class = \'tab\'>Немов природа розлютилась…</div><br/><div class = \'tab\'>На полі й луках стало пусто, –</div><div class = \'tab\'>Від уряду підтримка не дійшла,</div><div class = \'tab\'>Та у сусідських регіонах густо</div><div class = \'tab\'>Пшениця яра вся зійшла.</div><br/><div class = \'tab\'>Проте наказ із Центру був таким,</div><div class = \'tab\'>З якого враз ставало холодно:</div><div class = \'tab\'>Мов, необхідна поміч іншим, тим!</div><div class = \'tab\'>А наші хай загинуть з голоду?</div><br/><div class = \'tab\'>Корові сіна не дісталось,</div><div class = \'tab\'>Солому з стріхи брали,</div><div class = \'tab\'>Мочили, парили, сікли,</div><div class = \'tab\'>До весни майже дотягли.</div><br/><div class = \'tab\'>Пройшли роки, порядки всі змінились,</div><div class = \'tab\'>Немає тих страшних проблем,</div><div class = \'tab\'>А думка про корівку-рятівницю</div><div class = \'tab\'>У пам’яті назавжди залишилась…</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(477, 33, 'Мій садочок', '<div class = \'tab\'>Яблуня в садочку</div><div class = \'tab\'>Біля мого дому –</div><div class = \'tab\'>Наречена в квітах</div><div class = \'tab\'>Навесні стоїть.</div><br/><div class = \'tab\'>Я її плекаю,</div><div class = \'tab\'>Яблучок чекаю:</div><div class = \'tab\'>Пригощу онуків</div><div class = \'tab\'>В яблуневий Спас.</div><br/><div class = \'tab\'>Вишенька Олежчина</div><div class = \'tab\'>Підросла в садочку,</div><div class = \'tab\'>Червоніють ягідки,</div><div class = \'tab\'>Листячко блищить.</div><br/><div class = \'tab\'>Грушенька Кірюшеньки</div><div class = \'tab\'>Гіллячком шепоче:</div><div class = \'tab\'>\"Я грушок солодких</div><div class = \'tab\'>Вам дам всім досхочу\".</div><br/><div class = \'tab\'>А черешня Максика</div><div class = \'tab\'>Проситься до столу,</div><div class = \'tab\'>Стиглих абрикосів</div><div class = \'tab\'>Вже Андрійко жде.</div><br/><div class = \'tab\'>Ви мої онуки –</div><div class = \'tab\'>Рідний мій садочок!</div><div class = \'tab\'>Українських пращурів</div><div class = \'tab\'>Славнії сини!</div><br/><div class = \'tab\'>Я вас так кохаю!</div><div class = \'tab\'>Радощів бажаю</div><div class = \'tab\'>І життя щасливого</div><div class = \'tab\'>На довгії роки!</div><br/><div class = \'tab\'>Хай садочку сила</div><div class = \'tab\'>Завжди буде з вами,</div><div class = \'tab\'>Тож зростайте з садом</div><div class = \'tab\'>Й бережіть його!</div><br/><div class = \'tab\'>Прийде час садити</div><div class = \'tab\'>Вам і свій садочок −</div><div class = \'tab\'>Вчіть дітей любити</div><div class = \'tab\'>Радість на землі!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(478, 33, 'Детская шалость', '<div class = \'tab\'>Как-то раз, зимой морозной</div><div class = \'tab\'>Зазвонил наш телефон.</div><div class = \'tab\'>– Неужели это внук?</div><div class = \'tab\'>В трубке молвили: «Не он!»</div><br/><div class = \'tab\'>Выяснять не стали сразу,</div><div class = \'tab\'>Но узнали всѐ потом:</div><div class = \'tab\'>Оказалось, по ошибке</div><div class = \'tab\'>«Нам звонил из цирка слон!»</div><br/><div class = \'tab\'>Человечьим ясным слогом</div><div class = \'tab\'>Излагал свои слова:</div><div class = \'tab\'>«Здравствуй, деда! Здравствуй, баба!</div><div class = \'tab\'>Что, пришла уже зима?»</div><br/><div class = \'tab\'>В трубке что-то зашумело,</div><div class = \'tab\'>Заворчало, а потом и замолчало.</div><div class = \'tab\'>Из ответа бабы Вали:</div><div class = \'tab\'>«Мы как в цирке побывали!»</div><br/><div class = \'tab\'>Не прошло пяти минут –</div><div class = \'tab\'>Снова повторилось:</div><div class = \'tab\'>В телефоне кряк спросил:</div><div class = \'tab\'>«Что у Вас случилось?»</div><br/><div class = \'tab\'>Тут уж дед все раскусил:</div><div class = \'tab\'>Это внучек с шуткой!</div><div class = \'tab\'>Просто брат ему помог</div><div class = \'tab\'>Обернуться уткой.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(479, 33, 'Моё село', '<div class = \'tab\'>На земле мест немало хороших,</div><div class = \'tab\'>Ведь у каждого мерка своя.</div><div class = \'tab\'>Для меня Милаѐвка дороже,</div><div class = \'tab\'>Чем Чикаго, Торонто, Москва.</div><br/><div class = \'tab\'>Где я был и что видел, известно.</div><div class = \'tab\'>Ах! Как много всего повидал!</div><div class = \'tab\'>Но село, где родился однажды,</div><div class = \'tab\'>Я запомнил, мой друг, навсегда!</div><br/><div class = \'tab\'>Ширь полей и опушка лесная,</div><div class = \'tab\'>Шум травы и рогоз у ручья,</div><div class = \'tab\'>Мне казалось, я буду здесь вечно</div><div class = \'tab\'>Слушать с радостью трель соловья.</div><br/><div class = \'tab\'>Ах, места! До чего же прекрасно!</div><div class = \'tab\'>Тут Тургенев так часто бывал,</div><div class = \'tab\'>Мне слагать о них песни приятно,</div><div class = \'tab\'>Потому что здесь в детстве шагал.</div><br/><div class = \'tab\'>Обошел все вокруг, повсеместно,</div><div class = \'tab\'>Знал друзей я обширнейший круг,</div><div class = \'tab\'>И учиться всему интересно</div><div class = \'tab\'>Довелось мне, любезнейший друг!</div><br/><div class = \'tab\'>Ну а нынче я все же жалею,</div><div class = \'tab\'>Что не вижу всю ту красоту!..</div><div class = \'tab\'>Мой сегодняшний дом –мегаполис,</div><div class = \'tab\'>Но село я забыть не могу!</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(480, 33, 'Збір яблук', '<div class = \'tab\'>У моїм садочку</div><div class = \'tab\'>Яблуні зжовтілі</div><div class = \'tab\'>Кличуть нас до себе:</div><div class = \'tab\'>Яблука дозріли!</div><br/><div class = \'tab\'>Зібрались біля дому,</div><div class = \'tab\'>Я бачу всю сім’ю.</div><div class = \'tab\'>Вже діти на порозі –</div><div class = \'tab\'>До збору врожаю!</div><br/><div class = \'tab\'>Тут мої синочки,</div><div class = \'tab\'>Невістки, внучата</div><div class = \'tab\'>Стали до збирання:</div><div class = \'tab\'>Роботу розпочато!</div><br/><div class = \'tab\'>Гомонять внучата,</div><div class = \'tab\'>Чути далеченько,</div><div class = \'tab\'>Вихваляють яблуньку –</div><div class = \'tab\'>Ох, яблук багатенько!</div><br/><div class = \'tab\'>Разом до обіду</div><div class = \'tab\'>Зібрали й зверху, й здолу.</div><div class = \'tab\'>Ось мама кличе голосно –</div><div class = \'tab\'>Запрошує до столу.</div><br/><div class = \'tab\'>Дякуючи Богові,</div><div class = \'tab\'>Пройшли і цей ми рік</div><div class = \'tab\'>У злагоді та дружбі,</div><div class = \'tab\'>Як і завжди, й навік.</div><br/><div class = \'tab\'>А у всьому винні</div><div class = \'tab\'>Яблуньки такі,</div><div class = \'tab\'>Що сім’ю гартують</div><div class = \'tab\'>В праці на віки!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(481, 33, 'Кінець осені', '<div class = \'tab\'>Осінь зійшла нанівець,</div><div class = \'tab\'>Сонце спокійним стає,</div><div class = \'tab\'>Роботі у полі – кінець,</div><div class = \'tab\'>Зима на порозі встає.</div><br/><div class = \'tab\'>Листопад прощатися хоче,</div><div class = \'tab\'>Вже грудень в вікно заглядає.</div><div class = \'tab\'>Мороз проявляється з ночі,</div><div class = \'tab\'>Та вдень він усе ж відступає.</div><br/><div class = \'tab\'>Ніщо не зупинить початок зими,</div><div class = \'tab\'>Вже скоро та біла облога,</div><div class = \'tab\'>Що снігом обійме усе, мов крильми</div><div class = \'tab\'>Й складною враз стане дорога.</div><br/><div class = \'tab\'>Всі люди прискорюють рухи</div><div class = \'tab\'>І одяг міняють злегка.</div><div class = \'tab\'>Ось-ось налетять «білі мухи»</div><div class = \'tab\'>Й із вітром утнуть гопака!</div><br/><div class = \'tab\'>Жалкую, що осінь скінчилась –</div><div class = \'tab\'>Для мене пора ця свята:</div><div class = \'tab\'>Рахую, підводжу я підсумки –</div><div class = \'tab\'>Чергові стежинки життя.</div><br/><div class = \'tab\'>Святкую я свій день народження</div><div class = \'tab\'>В ці дні – немов іспит здаю.</div><div class = \'tab\'>Гостей з хвилюванням чекаю</div><div class = \'tab\'>Й радію, що з ними в строю!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(482, 33, 'Шануйте родичів своїх', '<div class = \'tab\'>Із висоти прожитого життя</div><div class = \'tab\'>Зробив я висновок один:</div><div class = \'tab\'>Шануйте родичів своїх,</div><div class = \'tab\'>Завжди любіть, цінуйте їх .</div><br/><div class = \'tab\'>Прости, матусю, – перед тобою винен,</div><div class = \'tab\'>Не дбав про тебе при житті.</div><div class = \'tab\'>Ця помилка – моя провина,</div><div class = \'tab\'>Але не дорікала ти мені…</div><br/><div class = \'tab\'>Чим більш дорослішим стаю</div><div class = \'tab\'>Від дня народження – тій дати –</div><div class = \'tab\'>Тим більше згадую тебе,</div><div class = \'tab\'>Твої страждання й великі втрати.</div><br/><div class = \'tab\'>Мені хрещена розказала,</div><div class = \'tab\'>Як ти усіх нас рятувала</div><div class = \'tab\'>І добротою зігрівала,</div><div class = \'tab\'>Оберігала і навчала.</div><br/><div class = \'tab\'>Твої слова і настанови</div><div class = \'tab\'>Ми пам’ятаєм по цей час.</div><div class = \'tab\'>Твої слова, матусю, знову</div><div class = \'tab\'>Стають пророчими для нас!</div><br/><div class = \'tab\'>Науку жити ти дістала</div><div class = \'tab\'>Від дідуся мого й бабусі.</div><div class = \'tab\'>Не бачив я ніколи їх,</div><div class = \'tab\'>Але від тебе чув про них.</div><br/><div class = \'tab\'>Отак, напевно, все в житті</div><div class = \'tab\'>Складається по-різному у всіх.</div><div class = \'tab\'>Від діда й баби до матусі</div><div class = \'tab\'>Шануйте родичів своїх!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(483, 33, 'Колискова сину', '<div class = \'tab\'>Спи рідненький сину,</div><div class = \'tab\'>Засинай наш любий,</div><div class = \'tab\'>Вже на небі зіроньки</div><div class = \'tab\'>Світять здалека.</div><br/><div class = \'tab\'>Місяць теж на захід</div><div class = \'tab\'>Рухається хутко</div><div class = \'tab\'>І на нас з тобою</div><div class = \'tab\'>Якби погляда.</div><br/><div class = \'tab\'>Птахи гомін разом</div><div class = \'tab\'>Щойно припинили,</div><div class = \'tab\'>Адже чути звуки</div><div class = \'tab\'>Від нічних комах.</div><br/><div class = \'tab\'>Вони як музики</div><div class = \'tab\'>Грають всі оркестром,</div><div class = \'tab\'>Грають так дружненько</div><div class = \'tab\'>На своїх \"губах\".</div><br/><div class = \'tab\'>Засинай наш любий –</div><div class = \'tab\'>Набирайся сил,</div><div class = \'tab\'>Набирайся розуму</div><div class = \'tab\'>Від солодких снів.</div><br/><div class = \'tab\'>Ми тобі бажаємо</div><div class = \'tab\'>Подій світлих більше</div><div class = \'tab\'>І щасливих, радісних,</div><div class = \'tab\'>Безтурботних днів.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(484, 33, 'Молодая команда', '<div class = \'tab\'>Настанет день, придѐт пора,</div><div class = \'tab\'>Приедет снова детвора.</div><div class = \'tab\'>В команде нашей молодой</div><div class = \'tab\'>Работа спорится с лихвой.</div><br/><div class = \'tab\'>Володя воду подключит,</div><div class = \'tab\'>Андрюша стол поставит,</div><div class = \'tab\'>Олежка траву тут же скосит,</div><div class = \'tab\'>Кирюша лестницу подносит.</div><br/><div class = \'tab\'>А Максик что-то мастерит,</div><div class = \'tab\'>Но как-то всѐ не так,</div><div class = \'tab\'>Ему уж хочется помочь,</div><div class = \'tab\'>Но папа Игорь гонит прочь.</div><br/><div class = \'tab\'>Он с топором имеет дело,</div><div class = \'tab\'>Корчует старые кусты,</div><div class = \'tab\'>Сажает новые умело –</div><div class = \'tab\'>Ведь сад не любит пустоты.</div><br/><div class = \'tab\'>Работа спорится у всех,</div><div class = \'tab\'>И Максик вскорости научится.</div><div class = \'tab\'>Мы видим маленький успех −</div><div class = \'tab\'>В команде всѐ получится!</div><br/><div class = \'tab\'>А как мы рады с бабушкой</div><div class = \'tab\'>Команде молодой.</div><div class = \'tab\'>Кругом порядок новый,</div><div class = \'tab\'>Наш дом ожил весной.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(485, 33, 'Моя доля', '<div class = \'tab\'>Вийшов з інституту–</div><div class = \'tab\'>По алеї йду.</div><div class = \'tab\'>Думки про науку,</div><div class = \'tab\'>Про долю свою.</div><br/><div class = \'tab\'>Згадую я школу,</div><div class = \'tab\'>Інститутський курс,</div><div class = \'tab\'>Знов в аспірантуру</div><div class = \'tab\'>Хочу «зазирнуть».</div><br/><div class = \'tab\'>Все життя навчатись –</div><div class = \'tab\'>Важко, друже мій,</div><div class = \'tab\'>Але необхідно</div><div class = \'tab\'>Пройти шлях такий.</div><br/><div class = \'tab\'>Учні-аспіранти</div><div class = \'tab\'>Знову «атакують»,</div><div class = \'tab\'>Ставлять запитання,</div><div class = \'tab\'>Вміло провокують.</div><br/><div class = \'tab\'>Широта досліджень,</div><div class = \'tab\'>Досвід спілкування,</div><div class = \'tab\'>Мушу дати відповідь</div><div class = \'tab\'>На їхні всі питання.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(486, 33, 'У аквариума', '<div class = \'tab\'>Рыбки плавают по дну,</div><div class = \'tab\'>Ищут себе пищу.</div><div class = \'tab\'>Их сегодня покормлю,</div><div class = \'tab\'>Завтра всѐ почищу.</div><br/><div class = \'tab\'>Утром рано ждут меня,</div><div class = \'tab\'>Хвостами виляют,</div><div class = \'tab\'>Словно просят подойти,</div><div class = \'tab\'>К себе приглашают.</div><br/><div class = \'tab\'>До чего весѐлый сброд</div><div class = \'tab\'>Все породы вместе:</div><div class = \'tab\'>Малышня снуѐт вперѐд,</div><div class = \'tab\'>Крупнячок на месте.</div><br/><div class = \'tab\'>Зорко смотрят на меня,</div><div class = \'tab\'>Пищу принимают.</div><div class = \'tab\'>Ближе к донцу – малышня,</div><div class = \'tab\'>Что-то соображают.</div><br/><div class = \'tab\'>Корм схватили – разбежались,</div><div class = \'tab\'>Плавниками двигают,</div><div class = \'tab\'>А внучата размечтались,</div><div class = \'tab\'>Глазѐнками хлопают.</div><br/><div class = \'tab\'>Смотрят дети, взрослый люд</div><div class = \'tab\'>На красоты моря.</div><div class = \'tab\'>Рыбкам дали пищу с рук –</div><div class = \'tab\'>Они не знают горя.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(487, 33, 'Мамино тепло', '<div class = \'tab\'>Брезжит свет в окошке –</div><div class = \'tab\'>За окном зима,</div><div class = \'tab\'>Мамочка в платочке,</div><div class = \'tab\'>Мама молода.</div><br/><div class = \'tab\'>Мама, мама, мамочка,</div><div class = \'tab\'>Мамочка моя,</div><div class = \'tab\'>Мамочка любимая –</div><div class = \'tab\'>Святая для меня.</div><br/><div class = \'tab\'>Мама печь затопит</div><div class = \'tab\'>И пошло тепло,</div><div class = \'tab\'>К завтраку появится</div><div class = \'tab\'>Каша с молоком.</div><br/><div class = \'tab\'>Я беру учебники,</div><div class = \'tab\'>Я беру дневник,</div><div class = \'tab\'>Свежий хлеб с картошкой,</div><div class = \'tab\'>Я к нему привык.</div><br/><div class = \'tab\'>Слышу зов товарищей</div><div class = \'tab\'>С улицы морозной –</div><div class = \'tab\'>Я хватаю сумку,</div><div class = \'tab\'>Рвусь в туман промозглый.</div><br/><div class = \'tab\'>Мама перекрестит,</div><div class = \'tab\'>Скажет: \"Будь здоров!\"</div><div class = \'tab\'>Прижмѐт к себе нежно –</div><div class = \'tab\'>Я идти готов.</div><br/><div class = \'tab\'>Годы пролетели –</div><div class = \'tab\'>Мамы нет давно,</div><div class = \'tab\'>Вспоминаю с трепетом.</div><div class = \'tab\'>Мамино тепло.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(488, 33, 'Знову до школи', '<div class = \'tab\'>Промайнуло літо,</div><div class = \'tab\'>Осінь наступа.</div><div class = \'tab\'>Нам до школи треба,</div><div class = \'tab\'>За парту пора.</div><br/><div class = \'tab\'>Квіти у школяриків</div><div class = \'tab\'>Прикрашають руки.</div><div class = \'tab\'>Наша люба вчителька</div><div class = \'tab\'>Стомилась від розлуки.</div><br/><div class = \'tab\'>Вчителька старенька</div><div class = \'tab\'>Ледве що не плаче</div><div class = \'tab\'>І тремтить від щастя,</div><div class = \'tab\'>Що нас знову бачить.</div><br/><div class = \'tab\'>Музика лунає,</div><div class = \'tab\'>Гомін навкруги,</div><div class = \'tab\'>Ми зайшли до школи –</div><div class = \'tab\'>Ми це чи не ми?!</div><br/><div class = \'tab\'>Ось і парти нові –</div><div class = \'tab\'>Зайняли місця,</div><div class = \'tab\'>Радісні обличчя,</div><div class = \'tab\'>Радісне життя.</div><br/><div class = \'tab\'>Коси перевірити –</div><div class = \'tab\'>Розваги хлоп\'ят,</div><div class = \'tab\'>Язик показати –</div><div class = \'tab\'>Жарти у дівчат.</div><br/><div class = \'tab\'>Це все так миттєво –</div><div class = \'tab\'>Вчитися потрібно,</div><div class = \'tab\'>Скоро буде залік,</div><div class = \'tab\'>Вже кінцівку видно.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(489, 33, 'К другу детства', '<div class = \'tab\'>Детский мой друг,</div><div class = \'tab\'>Как сейчас поживаешь?</div><div class = \'tab\'>Часто ли ты</div><div class = \'tab\'>Меня вспоминаешь?</div><br/><div class = \'tab\'>Детские годы –</div><div class = \'tab\'>Счастливые дни,</div><div class = \'tab\'>Наши походы</div><div class = \'tab\'>Ты помнишь о них?</div><br/><div class = \'tab\'>Мы обошли</div><div class = \'tab\'>Все поля и леса,</div><div class = \'tab\'>Места повидали –</div><div class = \'tab\'>Вот чудеса!</div><br/><div class = \'tab\'>В школу и техникум</div><div class = \'tab\'>Вместе ходили,</div><div class = \'tab\'>В зиму и слякоть</div><div class = \'tab\'>Дружбу водили.</div><br/><div class = \'tab\'>А помнишь снега</div><div class = \'tab\'>И овраги размытые,</div><div class = \'tab\'>Речку Удаву,</div><div class = \'tab\'>Дороги разбитые.</div><br/><div class = \'tab\'>Девочек милых</div><div class = \'tab\'>Губы красивые,</div><div class = \'tab\'>Мамочек наших</div><div class = \'tab\'>Лица счастливые.</div><br/><div class = \'tab\'>Хочется встретить</div><div class = \'tab\'>Тебя молодого –</div><div class = \'tab\'>Друга из детства</div><div class = \'tab\'>Для нас дорогого.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(490, 33, 'Поздравление другу', '<div class = \'tab\'>Ты не любишь свои Дня рождения,</div><div class = \'tab\'>Не привык получать поздравления,</div><div class = \'tab\'>Ты становишься всѐ же мудрее,</div><div class = \'tab\'>Голова все белей и белее.</div><br/><div class = \'tab\'>Пожеланья друзей и товарищей</div><div class = \'tab\'>Принимаешь теперь свысока −</div><div class = \'tab\'>Ты не любишь в свои Дни рождения</div><div class = \'tab\'>Поздравленья получать неспроста.</div><br/><div class = \'tab\'>Хочешь жить ты обычными днями</div><div class = \'tab\'>В ритме жизни своей не спеша,</div><div class = \'tab\'>Замечать за собой как-бы нехотя,</div><div class = \'tab\'>Что дошѐл или нет до \"венца\".</div><br/><div class = \'tab\'>На работе ты занят делами</div><div class = \'tab\'>В круговерти весь день допоздна.</div><div class = \'tab\'>Тебе нравится жизнь современная –</div><div class = \'tab\'>Как не выпить с тобою винца!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(491, 33, 'До свята друга', '<div class = \'tab\'>Друже, ми бачим – весна на порозі</div><div class = \'tab\'>І День твій народження скоро.</div><div class = \'tab\'>Друзі твої уже у дорозі –</div><div class = \'tab\'>І разом ми вип\'ємо в колі.</div><br/><div class = \'tab\'>Згадаймо минулі роки,</div><div class = \'tab\'>Коли ми були іще \"голі\",</div><div class = \'tab\'>Торили ми наші шляхи</div><div class = \'tab\'>Навіть у чистому полі.</div><br/><div class = \'tab\'>В житті ти досяг чималенько,</div><div class = \'tab\'>Дім збудував і дітей народив,</div><div class = \'tab\'>Онуків вже маєш маленьких</div><div class = \'tab\'>Й садочок ти свій посадив.</div><br/><div class = \'tab\'>...І рідні твої уже разом</div><div class = \'tab\'>Бажають тобі довголіття,</div><div class = \'tab\'>Бажаємо й ми довго жити,</div><div class = \'tab\'>Наразі тобі не дозволим тужити!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(492, 33, 'Ностальгия', '<div class = \'tab\'>Накатила волна ностальгии –</div><div class = \'tab\'>Дни ушедшие словно во сне,</div><div class = \'tab\'>Их я вижу, как тяжкие гири,</div><div class = \'tab\'>И как-будто в открытом окне.</div><br/><div class = \'tab\'>...Летний день, длинный день на исходе,</div><div class = \'tab\'>Стадо гонит пастух \"боевой\",</div><div class = \'tab\'>Наши мамы со старыми вѐдрами</div><div class = \'tab\'>Всех бурѐнок напоят водой.</div><br/><div class = \'tab\'>Лошадей недовольных фырчанье,</div><div class = \'tab\'>Лай собак во дворах до утра –</div><div class = \'tab\'>Это всѐ наше детство привольное</div><div class = \'tab\'>Не вернуть, не вернуть никогда!</div><br/><div class = \'tab\'>…Слышу крики мальчишек счастливых</div><div class = \'tab\'>На футбольном, на школьном дворе,</div><div class = \'tab\'>Вижу их на коньках, как на крыльях,</div><div class = \'tab\'>На хоккейном холодном катке.</div><br/><div class = \'tab\'>Сожалеть нам уже не приходится,</div><div class = \'tab\'>Это всѐ – так, слегка, ни о чѐм,</div><div class = \'tab\'>А внучата мои хорохорятся,</div><div class = \'tab\'>Ностальгия для них нипочѐм.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(493, 33, 'Каяття', '<div class = \'tab\'>Господи, господи! Силоньки дай,</div><div class = \'tab\'>Щоб подолати хворобу.</div><div class = \'tab\'>Господи, господи! Розуму дай,</div><div class = \'tab\'>Щоб подолати негоду.</div><br/><div class = \'tab\'>Господи, господи! Хоробрості дай,</div><div class = \'tab\'>Щоб Перемогу дістати.</div><div class = \'tab\'>Господи, господи! Мудрості дай,</div><div class = \'tab\'>Щоб справедливими стати.</div><br/><div class = \'tab\'>Господи, господи! Радості дай,</div><div class = \'tab\'>Щоб святкувати Свободу.</div><div class = \'tab\'>Господи, господи! Миру дай,</div><div class = \'tab\'>Всьому християнському роду.</div><br/><div class = \'tab\'>Господи, каємось перед тобою.</div><div class = \'tab\'>Гріху набралися вже через край,</div><div class = \'tab\'>Дай нам ще часу, щоб схаменутись.</div><div class = \'tab\'>Боже, ми просимо, нас не карай.</div><br/><div class = \'tab\'>Станемо поруч браття по вірі,</div><div class = \'tab\'>Каємось разом перед святими,</div><div class = \'tab\'>Клятву даємо у вірності й мирі,</div><div class = \'tab\'>Щоб поєднатися з ними.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(494, 33, 'Зустрічаємо день', '<div class = \'tab\'>Солодкі сни добігли до кінця –</div><div class = \'tab\'>Наше тепло у ліжку збереглося.</div><div class = \'tab\'>Ми вранці просимо Отця –</div><div class = \'tab\'>Це в нашому житті вже прижилося.</div><br/><div class = \'tab\'>І дякуємо Богові сумлінно</div><div class = \'tab\'>За вже наступний світлий день.</div><div class = \'tab\'>Ми всі будуємо здійсненні плани,</div><div class = \'tab\'>А в голові постійно \"дзинь-дзень\".</div><br/><div class = \'tab\'>Про що нам свідчить очевидність –</div><div class = \'tab\'>Прогнали ми таємну тінь,</div><div class = \'tab\'>Що всі турботи знов спочатку,</div><div class = \'tab\'>Та гонимо від себе лінь.</div><br/><div class = \'tab\'>Нам хочеться скоріш до столу –</div><div class = \'tab\'>Сніданок вже готовий, як завжди,</div><div class = \'tab\'>І \"Отче наш\" тут слово в слово</div><div class = \'tab\'>Промовить кожен в голові.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(495, 34, 'Завжди так буде', '<div class = \'tab\'>Я вийшов рано у поля,</div><div class = \'tab\'>Дивлюсь в простори неозорі,</div><div class = \'tab\'>Стою, мов у зеленім морі...</div><div class = \'tab\'>Яка ж красива ця земля!</div><br/><div class = \'tab\'>Радію кожному листку,</div><div class = \'tab\'>Як їх теплом цілує сонце,</div><div class = \'tab\'>Як заглядає у віконце</div><div class = \'tab\'>Та й оселяється в садку.</div><br/><div class = \'tab\'>Я чую пісню солов’я,</div><div class = \'tab\'>І гомін пастухів сільських,</div><div class = \'tab\'>І шепіт трав, таких м’яких...</div><div class = \'tab\'>Це рідний край, любов моя!</div><br/><div class = \'tab\'>Настане час на цій землі</div><div class = \'tab\'>Полям у золото вдягатись,</div><div class = \'tab\'>Зерном колоссю наливатись,</div><div class = \'tab\'>Щоб бути хлібу на столі.</div><br/><div class = \'tab\'>Із року в рік, завжди, щоднини</div><div class = \'tab\'>Кипить робота на полях,</div><div class = \'tab\'>Зерно проходить довгий шлях</div><div class = \'tab\'>Від паростка і до хлібини.</div><br/><div class = \'tab\'>Селянський піт, солоний піт</div><div class = \'tab\'>Лишається на тому полі,</div><div class = \'tab\'>І працьовитих рук мозолі</div><div class = \'tab\'>Благословенні на весь світ.</div><br/><div class = \'tab\'>Я вийшов рано на зорі</div><div class = \'tab\'>У новий день, в нові турботи,</div><div class = \'tab\'>І знов беруться до роботи</div><div class = \'tab\'>Прості селяни-трударі.</div><br/><div class = \'tab\'>Хай родить хліб, співають люди,</div><div class = \'tab\'>Цвіте мій Слобожанський край,</div><div class = \'tab\'>Духмяний пахне коровай!</div><div class = \'tab\'>І вірю я: завжди так буде!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(496, 34, 'Люблю Україну', '<div class = \'tab\'>Люблю мою неньку, люблю Україну,</div><div class = \'tab\'>Козацький наш рід! Я бачу навкруг</div><div class = \'tab\'>Велику, незламну і мудру родину,</div><div class = \'tab\'>А кожен з добром – той для неї є друг.</div><br/><div class = \'tab\'>Я хочу радіти, я хочу творити</div><div class = \'tab\'>З тобою, Вкраїно, у серці завжди,</div><div class = \'tab\'>І стомлені плечі твої прихистити,</div><div class = \'tab\'>Що стільки випробувань знали й біди.</div><br/><div class = \'tab\'>Як сталося зараз, чия в тім провина,</div><div class = \'tab\'>Що місце любові займає війна?</div><div class = \'tab\'>Війна між братами по вірі єдиній!</div><div class = \'tab\'>Нічого святого не дасть нам вона…</div><br/><div class = \'tab\'>Старі і малеча безмежно страждають,</div><div class = \'tab\'>Руйнується побут і мирний уклад,</div><div class = \'tab\'>Снаряди там шкіру землі виїдають…</div><div class = \'tab\'>Гей, ви, схаменіться! Здавайте назад!</div><br/><div class = \'tab\'>Не зазіхайте на волю сусіда −</div><div class = \'tab\'>Ми хаті своїй самі ради дамо!</div><div class = \'tab\'>Збудуємо, браття, ми нашу країну, –</div><div class = \'tab\'>Це нам, українцям, від бога дано.</div><br/><div class = \'tab\'>Мої ви рідненькі сини України!</div><div class = \'tab\'>Я зичу вам щастя у долях людських.</div><div class = \'tab\'>Робіть все на користь своєї країни</div><div class = \'tab\'>Для себе й прийдешніх нащадків своїх.</div><br/><div class = \'tab\'>Люблю мою неньку, люблю Україну:</div><div class = \'tab\'>І гори, і ріки, і велич степів.</div><div class = \'tab\'>Ніщо не замінить мені Батьківщину,</div><div class = \'tab\'>Народ працьовитий, і мову, і спів.</div><br/><div class = \'tab\'>Люблю всю Планету, люблю Україну,</div><div class = \'tab\'>Бажаю їй квітнути і багатіть,</div><div class = \'tab\'>Сімейні проблеми рішати сімейно</div><div class = \'tab\'>І гідність у серці своєму носить!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(497, 34, 'Городской парк', '<div class = \'tab\'>Парк тенистый на пригорке</div><div class = \'tab\'>Возле ФЭДа разместился.</div><div class = \'tab\'>Здесь когда-то бывал Горький,</div><div class = \'tab\'>И к нему он прицепился.</div><br/><div class = \'tab\'>Парк Центральный – это верно,</div><div class = \'tab\'>Но в народе говорят:</div><div class = \'tab\'>Лучше \"Горького\" на свете</div><div class = \'tab\'>Парка нет и не бывать.</div><br/><div class = \'tab\'>Кто же создал эту прелесть –</div><div class = \'tab\'>Многих можно нам назвать,</div><div class = \'tab\'>А за то, что он красивый,</div><div class = \'tab\'>Спасибо мэрии сказать.</div><br/><div class = \'tab\'>Взрослым, детям и подросткам,</div><div class = \'tab\'>Молодым и не совсем,</div><div class = \'tab\'>Всем влюблѐнным и не очень</div><div class = \'tab\'>По душе аллеек тень.</div><br/><div class = \'tab\'>Карусели, горки, игры</div><div class = \'tab\'>Здесь представлены для всех.</div><div class = \'tab\'>Танцы, песни, даже шутки</div><div class = \'tab\'>И весѐлый детский смех.</div><br/><div class = \'tab\'>Горожане все довольны:</div><div class = \'tab\'>\"Всѐ по-новому опять!\"</div><div class = \'tab\'>И за это парку надо</div><div class = \'tab\'>Пальму первенства отдать!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(498, 34, 'Наш Харків', '<div class = \'tab\'>В чудовім місті, на кордоні,</div><div class = \'tab\'>Де вольність є у всіх в житті,</div><div class = \'tab\'>Тут всі присутні, як в долоні:</div><div class = \'tab\'>Містяни, вчені, вчителі.</div><br/><div class = \'tab\'>Це місто Харків, це наш край,</div><div class = \'tab\'>Це осередок мешканців заможних,</div><div class = \'tab\'>Це трудівник, студентський рай,</div><div class = \'tab\'>Це науковий центр і установ художніх.</div><br/><div class = \'tab\'>...Зелене світло цілий день</div><div class = \'tab\'>Буяє свіжістю вночі:</div><div class = \'tab\'>Проспекти, парки, магазини –</div><div class = \'tab\'>Все в сяйві світла навкруги.</div><br/><div class = \'tab\'>Метро, вокзали, стадіони,</div><div class = \'tab\'>Басейни, гольф-поля і корти –</div><div class = \'tab\'>Все це для послуг всіх містян</div><div class = \'tab\'>І для гостей із нашої когорти.</div><br/><div class = \'tab\'>...Про те, що було в давнину,</div><div class = \'tab\'>Уже написано по плану,</div><div class = \'tab\'>Поети присвятили їй хвалу</div><div class = \'tab\'>І мешканцям усім надали шану.</div><br/><div class = \'tab\'>Були часи скрутні для них:</div><div class = \'tab\'>Війна, розруха, злидні,</div><div class = \'tab\'>Але відродження збулось,</div><div class = \'tab\'>Тому що харків\'яни духом сильні.</div><br/><div class = \'tab\'>...О п\'ятій ранку із депо</div><div class = \'tab\'>Трамваї дзвоник подають</div><div class = \'tab\'>І на роботу хутко-хутко</div><div class = \'tab\'>Містяни з гордістю ідуть.</div><br/><div class = \'tab\'>Маршрутки тихо під\'їжджають</div><div class = \'tab\'>До перших купочок людей</div><div class = \'tab\'>І всіх зі швидкістю збирають</div><div class = \'tab\'>Завчасно у своїх дверей.</div><br/><div class = \'tab\'>...Зима бере своє потроху,</div><div class = \'tab\'>Диктує правила свої,</div><div class = \'tab\'>А все ж і сніг нам не до сміху –</div><div class = \'tab\'>Працюють служби і ДАІ.</div><br/><div class = \'tab\'>Бува, що вітер скаженіє,</div><div class = \'tab\'>Рве сильно всі дроти.</div><div class = \'tab\'>І світло вимикається миттєво,</div><div class = \'tab\'>Та дітки не бояться темноти.</div><br/><div class = \'tab\'>Бо знають – враз буде воно,</div><div class = \'tab\'>Те, що боронить їх від темряви і скуки.</div><div class = \'tab\'>Спасибі всім робітникам,</div><div class = \'tab\'>Що мають золотії руки.</div><br/><div class = \'tab\'>...Коли ж весна приходить знову</div><div class = \'tab\'>І Харків квітне, як завжди,</div><div class = \'tab\'>По річках наших йде вода</div><div class = \'tab\'>І криги ставить на диби.</div><br/><div class = \'tab\'>І хоч маленькі ці річки −</div><div class = \'tab\'>Несуть вологу жваво,</div><div class = \'tab\'>Вода гарненько гомонить,</div><div class = \'tab\'>Про харків\'ян розносить славу!</div><br/><div class = \'tab\'>Вкраїнський Харків – ним пишаюсь,</div><div class = \'tab\'>Як він подобається всім!</div><div class = \'tab\'>Любовно з ним я зустрічаюсь,</div><div class = \'tab\'>Живу думками тільки з ним!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(499, 34, 'К Дню города', '<div class = \'tab\'>Харьков живѐт своей жизнью</div><div class = \'tab\'>В море людей и огней,</div><div class = \'tab\'>В шумном сообществе наций,</div><div class = \'tab\'>В смехе счастливых детей.</div><br/><div class = \'tab\'>Харьков - трудяга отменный,</div><div class = \'tab\'>Славен историй всей.</div><div class = \'tab\'>Двери открыты все настежь</div><div class = \'tab\'>Для честных и добрых друзей.</div><br/><div class = \'tab\'>День ото дня он красивее Город весѐлый, родной.</div><div class = \'tab\'>Все в нѐм работают дружно,</div><div class = \'tab\'>Любят, лелеют его.</div><div class = \'tab\'>Школы, театры и вузы,</div><br/><div class = \'tab\'>Садики, клубы, кино –</div><div class = \'tab\'>Всѐ говорит нам о будущем.</div><div class = \'tab\'>Так будь же всегда хорошо!</div><div class = \'tab\'>Храмы и церкви трезвонят –</div><br/><div class = \'tab\'>В праздники просят прийти.</div><div class = \'tab\'>Чинно идут харьковчане</div><div class = \'tab\'>К Господу дружно в пути.</div><div class = \'tab\'>Славим свой город-красавец,</div><br/><div class = \'tab\'>Господа просим о том,</div><div class = \'tab\'>Чтобы на многие лета</div><div class = \'tab\'>Мир воцарился кругом.</div>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1);
INSERT INTO `LYRICS` (`id`, `set_id`, `name`, `content`, `write_date`, `author`, `description`, `author_comment`, `lyrics_type`, `TABBED`, `BRS`, `STROPHE_LENGTH`, `STROPHE_DELIM`, `LINE_WRAPPER`, `LINE_WRAPPER_END`, `LINE_WRAPPER_TABBED`, `LINE_WRAPPER_TABBED_END`, `auto_format`) VALUES
(500, 34, 'Дергачі на Лопані', '<div class = \'tab\'>В наш рідний край із-за кордону</div><div class = \'tab\'>Повільно річечка біжить,</div><div class = \'tab\'>Проміж поселень, по заплавах,</div><div class = \'tab\'>Біжить та тихо шепотить.</div><br/><div class = \'tab\'>Тихенько промовля до очерету,</div><div class = \'tab\'>Сама з собою гомонить,</div><div class = \'tab\'>Можливо із птахами по секрету</div><div class = \'tab\'>Разом з осоками шумить.</div><br/><div class = \'tab\'>У річки назва нам відома,</div><div class = \'tab\'>Здавен у неї є ім\"я.</div><div class = \'tab\'>Веде вона всіх нас додому,</div><div class = \'tab\'>В якому справжнє є життя.</div><br/><div class = \'tab\'>Були часи – ставала катом,</div><div class = \'tab\'>Змітала все в велику повінь.</div><div class = \'tab\'>Бідненькі курені та хати,</div><div class = \'tab\'>Ця річка має назву − Лопань.</div><br/><div class = \'tab\'>Вздовж річки оселялись бідні</div><div class = \'tab\'>На суходолі, на ярах.</div><div class = \'tab\'>Спочатку тільки одинокі,</div><div class = \'tab\'>А потім – сім\'ї на паях.</div><br/><div class = \'tab\'>Згадали мешканці птахів,</div><div class = \'tab\'>Які гніздились в осокорах –</div><div class = \'tab\'>Численних і грайливих деркачів,</div><div class = \'tab\'>З гучним і галасливим хором.</div><br/><div class = \'tab\'>Постійно тут змінялись люди,</div><div class = \'tab\'>Проте надійно вже звели</div><div class = \'tab\'>Мости, будинки та заводи,</div><div class = \'tab\'>Вокзали, школи і церкви.</div><br/><div class = \'tab\'>В цей час зростає всім на диво</div><div class = \'tab\'>Славетне місто Дергачі!</div><div class = \'tab\'>Ім\'я це носить справедливо</div><div class = \'tab\'>Від назви вільних деркачів.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(501, 34, 'Слобожанский край', '<div class = \'tab\'>Смотрю в просторы дальние</div><div class = \'tab\'>И небо лучезарное</div><div class = \'tab\'>Сады наши обильные,</div><div class = \'tab\'>Пруды и речки чистые.</div><br/><div class = \'tab\'>Любуюсь красотою –</div><div class = \'tab\'>Дышу здесь полной грудью,</div><div class = \'tab\'>Дивуюсь полнотою</div><div class = \'tab\'>Природы красок буйных.</div><br/><div class = \'tab\'>И лучше нету в мире</div><div class = \'tab\'>Полей – простор широкий,</div><div class = \'tab\'>Наполненный культурами</div><div class = \'tab\'>Родными! Не далёкими.</div><br/><div class = \'tab\'>Пшеницы спелой колос</div><div class = \'tab\'>Тянет, как магнит,</div><div class = \'tab\'>Подсолнечника жёлтого</div><div class = \'tab\'>Цветок манит, манит....</div><br/><div class = \'tab\'>И все красоты мира</div><div class = \'tab\'>Не стоят ничего</div><div class = \'tab\'>В сравнении с богатством</div><div class = \'tab\'>Края моего.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(502, 34, 'Рідний край', '<div class = \'tab\'>Квітучий гай –</div><div class = \'tab\'>Бджолиний рай,</div><div class = \'tab\'>Ти мій навіки</div><div class = \'tab\'>Рідний край!</div><br/><div class = \'tab\'>Безмежний край –</div><div class = \'tab\'>Зелений гай,</div><div class = \'tab\'>Твої сади −</div><div class = \'tab\'>Бджолиний рай.</div><br/><div class = \'tab\'>Рідненький край,</div><div class = \'tab\'>Твої поля</div><div class = \'tab\'>Багаті на врожай −</div><div class = \'tab\'>Любов моя.</div><br/><div class = \'tab\'>Квітковий гай –</div><div class = \'tab\'>Ліси що рай.</div><div class = \'tab\'>Мій любий край,</div><div class = \'tab\'>Зелений гай.</div><br/><div class = \'tab\'>Мій любий край,</div><div class = \'tab\'>Ти дав повагу,</div><div class = \'tab\'>Ти дав любов,</div><div class = \'tab\'>Життя й наснагу.</div><br/><div class = \'tab\'>Квітучий гай –</div><div class = \'tab\'>Бджолиний рай,</div><div class = \'tab\'>Ти мій навіки</div><div class = \'tab\'>Рідний край!</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(503, 35, 'Моя звезда', '<div class = \'tab\'>Люблю тебя, моя любимая!</div><div class = \'tab\'>Люблю тебя, люблю тебя.</div><div class = \'tab\'>Ты для меня одна единая,</div><div class = \'tab\'>Ты для меня – моя звезда!</div><br/><div class = \'tab\'>Хранитель тайн земных утех,</div><div class = \'tab\'>Страданий горьких в твоих упрѐках,</div><div class = \'tab\'>Душевных ран и радостный успех</div><div class = \'tab\'>В моих мечтах таких далѐких.</div><br/><div class = \'tab\'>В любви сплелись мы воедино,</div><div class = \'tab\'>Счастливо испытали миг мечты</div><div class = \'tab\'>В наследство детям нашим передали</div><div class = \'tab\'>Совместные похожие черты.</div><br/><div class = \'tab\'>Мечта одна: им быть счастливыми,</div><div class = \'tab\'>Идти по жизни, продолжать любовь</div><div class = \'tab\'>Такую, как у нас, а может быть и лучше.</div><div class = \'tab\'>Мы заслужили, милая, с тобой.</div><br/><div class = \'tab\'>Ты счастлива и я безмерно счастлив,</div><div class = \'tab\'>Ничто не нужно нам и более того:</div><div class = \'tab\'>Нас радуют детей поступки,</div><div class = \'tab\'>У них судьба похожа на любовь.</div><br/><div class = \'tab\'>До бесконечности лежит любовь меж нами,</div><div class = \'tab\'>Дай бог не изменять святой любовный круг.</div><div class = \'tab\'>Мы смотрим с высоты, парим мы над годами −</div><div class = \'tab\'>Теперь я для тебя уж настоящий друг.</div><br/><div class = \'tab\'>А мы живем не зря и наш семейный род</div><div class = \'tab\'>Живѐт в любви и вере к Богу.</div><div class = \'tab\'>Молитвою своей всех просим быть верней</div><div class = \'tab\'>В своей любви и радости другу к другу.</div><br/><div class = \'tab\'>Люблю тебя, моя родимая!</div><div class = \'tab\'>Люблю тебя, люблю всегда.</div><div class = \'tab\'>Ты для меня одна единая,</div><div class = \'tab\'>Ты для меня – моя звезда!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(504, 35, 'Одна любовь', '<div class = \'tab\'>Капельки дождинок</div><div class = \'tab\'>Нежно омывают</div><div class = \'tab\'>Личико прекрасное</div><div class = \'tab\'>Миленькой моей.</div><br/><div class = \'tab\'>Жизнь, увы, проходит,</div><div class = \'tab\'>Время мчится быстро,</div><div class = \'tab\'>Годы не воротишь,</div><div class = \'tab\'>Но любовь жива.</div><br/><div class = \'tab\'>Лишь тебя увидел –</div><div class = \'tab\'>Полюбил тотчас,</div><div class = \'tab\'>Как любил тогда я,</div><div class = \'tab\'>Так люблю сейчас.</div><br/><div class = \'tab\'>Думаю, не зря мне</div><div class = \'tab\'>Ты судьбой дана.</div><div class = \'tab\'>На двоих одна ведь</div><div class = \'tab\'>Жизнь у нас, жена!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(505, 35, 'Люблю тебя...', '<div class = \'tab\'>Люблю тебя, моя краса,</div><div class = \'tab\'>Неповторима и прекрасна!</div><div class = \'tab\'>Ведь наша жизнь с тобою не напрасна,</div><div class = \'tab\'>Люблю тебя, моя душа.</div><br/><div class = \'tab\'>Свети, моя звезда, моя земная радость!</div><div class = \'tab\'>Шепчу тебе: \"Люблю, как прежде\" и сейчас.</div><div class = \'tab\'>Я знаю, что с тобой всѐ счастье мне досталось,</div><div class = \'tab\'>И для тебя одной пою я свой романс.</div><br/><div class = \'tab\'>За всѐ благодарю: за преданность, за счастье,</div><div class = \'tab\'>За нежный поцелуй прекрасных губ твоих,</div><div class = \'tab\'>За милость, за мечту, за тишину в ненастье,</div><div class = \'tab\'>За то, что создала наш мир для нас двоих.</div><br/><div class = \'tab\'>Свою любовь храним, еѐ мы не предáли,</div><div class = \'tab\'>Не разводили – строили мосты.</div><div class = \'tab\'>Любовь с успехом нашим детям передали,</div><div class = \'tab\'>Исполнили свои заветные мечты.</div><br/><div class = \'tab\'>Ты светишься, и я с тобою счастлив,</div><div class = \'tab\'>Нам в жизни очень-очень повезло:</div><div class = \'tab\'>Нас радует детей любимых счастье,</div><div class = \'tab\'>Хотим, чтобы оно вовеки не прошло!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(506, 35, 'Ты и я', '<div class = \'tab\'>Я и Ты, Ты и Я –</div><div class = \'tab\'>С мыслью одной.</div><div class = \'tab\'>Любишь ты меня,</div><div class = \'tab\'>Любим ли я тобой?</div><br/><div class = \'tab\'>Пришла любви пора,</div><div class = \'tab\'>Настало наше лето.</div><div class = \'tab\'>Горим мы страстью, ты сказала: \"Да…\"</div><div class = \'tab\'>Давай-ка вместе испытаем это.</div><br/><div class = \'tab\'>Кружится сильно голова</div><div class = \'tab\'>Мы крепко увлеклись, подруга,</div><div class = \'tab\'>Не слышим ничего вокруг,</div><div class = \'tab\'>Мы видим лишь глаза и губы.</div><br/><div class = \'tab\'>Как сладок первый поцелуй,</div><div class = \'tab\'>Крепки объятья нежные...</div><div class = \'tab\'>Не упусти момент, целуй</div><div class = \'tab\'>Меня сегодня первого.</div><br/><div class = \'tab\'>А я тотчас же дам ответ!</div><div class = \'tab\'>Люблю, люблю тебя навечно!</div><div class = \'tab\'>Пришла пора нашей любви</div><div class = \'tab\'>И это обещаю, вечно!</div><br/><div class = \'tab\'>Я и Ты, Ты и Я,</div><div class = \'tab\'>Я люблю, люблю тебя!</div><div class = \'tab\'>Я и Ты, Ты и Я</div><div class = \'tab\'>Мы теперь одна семья.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(507, 35, 'Первая любовь', '<div class = \'tab\'>Пришла ты, как из сказки,</div><div class = \'tab\'>Той раннею весной!</div><div class = \'tab\'>Хоть строишь ты мне глазки –</div><div class = \'tab\'>Проходишь стороной…</div><br/><div class = \'tab\'>Всѐ светом озаряешь,</div><div class = \'tab\'>В шелка облачена…</div><div class = \'tab\'>За что меня караешь?</div><div class = \'tab\'>Ведь за окном – весна!</div><br/><div class = \'tab\'>Я все-таки мужчина,</div><div class = \'tab\'>Я вижу твой задор</div><div class = \'tab\'>И знаю, в чем причина:</div><div class = \'tab\'>Пришла, пришла любовь!</div><br/><div class = \'tab\'>Идти с тобою рядом</div><div class = \'tab\'>Готов уже сейчас,</div><div class = \'tab\'>«Обвитый виноградом»,</div><div class = \'tab\'>Явлюсь к тебе тотчас!</div><br/><div class = \'tab\'>Ты окажи любезность,</div><div class = \'tab\'>Улыбку подари –</div><div class = \'tab\'>Я упаду, как в бездну,</div><div class = \'tab\'>В наш океан любви!</div><br/><div class = \'tab\'>Пришла ты, как из сказки,</div><div class = \'tab\'>Той раннею весной!</div><div class = \'tab\'>Хоть строишь ты мне глазки –</div><div class = \'tab\'>Но я теперь с тобой!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(508, 35, 'Солнышко мое!', '<div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Иди ко мне сейчас!</div><div class = \'tab\'>Люблю тебя и жду</div><div class = \'tab\'>Уж долгий, долгий час!</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Люблю, люблю тебя!</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Люблю, люблю всегда.</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Я вижу, как встаешь,</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Я слышу, как зовешь.</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Не уходи, люблю,</div><div class = \'tab\'>Солнышко мое,</div><div class = \'tab\'>Я без тебя умру.</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Со мной еще побудь.</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Я еду в долгий путь.</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Тебя я снова жду.</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Я за тобой пойду.</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Ну, обогрей меня!</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Люблю, люблю тебя!</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Прости, прости меня.</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Не жить мне без тебя!</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Тебя я вижу вновь.</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Одна у нас любовь!</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Эта любовь прекрасна!</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Жизнь не совсем напрасна.</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Согрей меня, согрей!</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Иди ко мне скорей!</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>И снова ты идешь.</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>В душе моей поешь.</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>За все благодарю.</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Тебе любовь дарю.</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Гори, гори, гори,</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>От пламенной любви.</div><br/><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Люблю, люблю тебя.</div><div class = \'tab\'>Солнышко мое!</div><div class = \'tab\'>Люблю, люблю всегда!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(509, 35, 'Сонце і любов', '<div class = \'tab\'>Сонячне світло, сонячне сяйво</div><div class = \'tab\'>Сховалось за тучі, сховалося знов.</div><div class = \'tab\'>Де покотилась, де поділася</div><div class = \'tab\'>Наша з тобою любов?</div><br/><div class = \'tab\'>Чекаю сьогодні тебе уже зранку</div><div class = \'tab\'>І покажу – я новий уже знов.</div><div class = \'tab\'>Хочу побачити теж я новим</div><div class = \'tab\'>Тебе, моє сонце – любов.</div><br/><div class = \'tab\'>Ну, розігрій же ти наші стосунки,</div><div class = \'tab\'>Наші страждання і нелюбов,</div><div class = \'tab\'>Щоб повернути завчасно, довічно</div><div class = \'tab\'>Нашу з тобою любов!</div><br/><div class = \'tab\'>Сонце зігріло нас поодинці,</div><div class = \'tab\'>Туча пішла уже геть.</div><div class = \'tab\'>Знову ми поруч, знову ми разом –</div><div class = \'tab\'>Божою милістю – надано честь.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(510, 35, 'Мы снова вместе...', '<div class = \'tab\'>Мне радостно видеть тебя,</div><div class = \'tab\'>Моя дорогая подруга,</div><div class = \'tab\'>Мы снова, мы снова – опять,</div><div class = \'tab\'>Опять ощущаем друг друга!</div><br/><div class = \'tab\'>Разлука была нестерпимой,</div><div class = \'tab\'>Болезненной раной, такою,</div><div class = \'tab\'>Что жизни хотелось иной,</div><div class = \'tab\'>Но в мыслях я всѐ же с тобою.</div><br/><div class = \'tab\'>Мы рядом, я снова с тобой,</div><div class = \'tab\'>Звучат оправданья напрасно,</div><div class = \'tab\'>Мы любим всѐ так же, как прежде,</div><div class = \'tab\'>Сердца наши бьются согласно.</div><br/><div class = \'tab\'>Согласие есть на века,</div><div class = \'tab\'>Мы гоним разлуку долой.</div><div class = \'tab\'>Жду я тебя издалека,</div><div class = \'tab\'>Жду я в наш город родной!</div><br/><div class = \'tab\'>Я счастлив, что вижу тебя,</div><div class = \'tab\'>Моя дорогая подруга.</div><div class = \'tab\'>Мы снова, мы снова – опять,</div><div class = \'tab\'>Опять ощущаем друг друга!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(511, 35, 'Случайная встреча', '<div class = \'tab\'>На площади города Харькова</div><div class = \'tab\'>В тот день было много людей:</div><div class = \'tab\'>Стояли цветным, пестрым маревом</div><div class = \'tab\'>И плыли в дорожках аллей.</div><br/><div class = \'tab\'>Тебя я случайно увидел</div><div class = \'tab\'>В толпе откровенных зевак.</div><div class = \'tab\'>Куда же ты так торопилась? –</div><div class = \'tab\'>Бежала на встречу. Во как!</div><br/><div class = \'tab\'>Встревоженный взгляд я заметил</div><div class = \'tab\'>И сразу решил, что могу</div><div class = \'tab\'>Помочь тебе в эту минуту,</div><div class = \'tab\'>Затронул тебя на бегу.</div><br/><div class = \'tab\'>Пора бы уйти, но сознательно,</div><div class = \'tab\'>Уж что-то держало меня.</div><div class = \'tab\'>К тебе присмотрелся внимательно,</div><div class = \'tab\'>Решил не спешить никуда.</div><br/><div class = \'tab\'>И вдруг пригласил я решительно</div><div class = \'tab\'>Со мною пройти на бульвар.</div><div class = \'tab\'>Мы с площади вышли стремительно,</div><div class = \'tab\'>В душе загорелся пожар.</div><br/><div class = \'tab\'>Прошлись мы по улице Пушкинской,</div><div class = \'tab\'>Забыли в тот миг обо всем!</div><div class = \'tab\'>Друг другу понравились с ходу,</div><div class = \'tab\'>Махнули рукой мы на все.</div><br/><div class = \'tab\'>Пронзила та встреча стрелою</div><div class = \'tab\'>И стала прологом любви.</div><div class = \'tab\'>Сегодня считаем с тобою</div><div class = \'tab\'>Счастл во прожитые дни!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(512, 35, 'Одна любовь', '<div class = \'tab\'>Люблю тебя я безгранично,</div><div class = \'tab\'>И нет любви моей конца,</div><div class = \'tab\'>Люблю тебя сегодня и навечно</div><div class = \'tab\'>Ты у меня одна, одна.</div><br/><div class = \'tab\'>Тебя цветущую в саду</div><div class = \'tab\'>Я вижу и во сне и наяву,</div><div class = \'tab\'>И каждый день прошу у Бога,</div><div class = \'tab\'>Желая милости твоей,</div><br/><div class = \'tab\'>Хочу тебя я видеть рядом</div><div class = \'tab\'>До окончания всех дней.</div><div class = \'tab\'>Прошу прощения за слабость,</div><div class = \'tab\'>Прошу тебя любить меня.</div><br/><div class = \'tab\'>Люблю тебя и потому</div><div class = \'tab\'>Прошу руки твоей.</div><div class = \'tab\'>Люблю тебя и посему</div><div class = \'tab\'>Хочу, чтоб ты была моей.</div><br/><div class = \'tab\'>Люблю тебя я безгранично</div><div class = \'tab\'>И нет любви моей конца,</div><div class = \'tab\'>Готов любить тебя я вечно –</div><div class = \'tab\'>Ты у меня всегда одна.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(513, 35, 'Эхо любви', '<div class = \'tab\'>Грусть уходящего лета…</div><div class = \'tab\'>Вижу, что чувствуешь ты.</div><div class = \'tab\'>Лѐгкой прохладою эхо</div><div class = \'tab\'>Напрочь сметает мечты.</div><br/><div class = \'tab\'>Нет и не будет возврата</div><div class = \'tab\'>К мыслям любовным, святым.</div><div class = \'tab\'>Хочешь-не хочешь, а надо</div><div class = \'tab\'>Привыкнуть к поступкам иным.</div><br/><div class = \'tab\'>Столько сказали мы летом</div><div class = \'tab\'>Горестных слов! А зачем?</div><div class = \'tab\'>Ясно, любви уже нету</div><div class = \'tab\'>И больше не будет совсем.</div><br/><div class = \'tab\'>Там в отголосках сознания</div><div class = \'tab\'>Теплится новый рассвет,</div><div class = \'tab\'>Новые будут свиданья,</div><div class = \'tab\'>Возврата к былому уж нет.</div><br/><div class = \'tab\'>Зачем же грустить понапрасну?</div><div class = \'tab\'>Встретишь любовь посильней,</div><div class = \'tab\'>Жизнь твоя будет прекрасна,</div><div class = \'tab\'>Ведь много хороших людей.</div><br/><div class = \'tab\'>Грусть уходящего лета...</div><div class = \'tab\'>Знаю, что чувствуешь ты.</div><div class = \'tab\'>Звуки далѐкого эха</div><div class = \'tab\'>Не оставляют следы.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(514, 35, 'В поисках золотой породы', '<div class = \'tab\'>Обильный дождь сегодня</div><div class = \'tab\'>Льѐт, словно из ведра.</div><div class = \'tab\'>Укрывшись под навесом,</div><div class = \'tab\'>Сидим мы у костра.</div><br/><div class = \'tab\'>С тобой легко и радостно,</div><div class = \'tab\'>Понятно, как всегда.</div><div class = \'tab\'>Любви отдались сладостно,</div><div class = \'tab\'>Ведь ты сказала \"Да\"!</div><br/><div class = \'tab\'>Уставшие, счастливые</div><div class = \'tab\'>Решаем отдохнуть.</div><div class = \'tab\'>Придѐт к утру погода</div><div class = \'tab\'>И снова, снова в путь.</div><br/><div class = \'tab\'>С тобою ходим рядом –</div><div class = \'tab\'>Исследуем природу.</div><div class = \'tab\'>Уж завтра точно вместе</div><div class = \'tab\'>Отыщем ту породу,</div><br/><div class = \'tab\'>Где жила золотая</div><div class = \'tab\'>Заложена давно,</div><div class = \'tab\'>А мы с тобой так молоды</div><div class = \'tab\'>И оба заодно.</div><br/><div class = \'tab\'>Но дождь стоит стеною,</div><div class = \'tab\'>Мешает нам идти.</div><div class = \'tab\'>Любимая, с тобою</div><div class = \'tab\'>Прошли мы полпути.</div><br/><div class = \'tab\'>Тяжѐлая профессия –</div><div class = \'tab\'>Исследовать природу,</div><div class = \'tab\'>Но мы с тобою призваны</div><div class = \'tab\'>Найти, найти породу!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(515, 35, 'Встреча с любовью', '<div class = \'tab\'>Ты опустилась с неба,</div><div class = \'tab\'>Собой меня маня,</div><div class = \'tab\'>Красивая и стройная,</div><div class = \'tab\'>Любимая моя!</div><br/><div class = \'tab\'>Я очень очарован,</div><div class = \'tab\'>Смотрю издалека,</div><div class = \'tab\'>Мечтаю быть с тобою,</div><div class = \'tab\'>Да так, чтоб на века.</div><br/><div class = \'tab\'>Сегодня жду я встречи,</div><div class = \'tab\'>Готовлюсь долго я.</div><div class = \'tab\'>Тебя увижу вечером,</div><div class = \'tab\'>Любимая моя!</div><br/><div class = \'tab\'>Прилив любви и нежности</div><div class = \'tab\'>Почувствовал сполна,</div><div class = \'tab\'>Клянусь тебе я в верности,</div><div class = \'tab\'>Любимая моя!</div><br/><div class = \'tab\'>Подходит час свидания,</div><div class = \'tab\'>Горю я, как свеча,</div><div class = \'tab\'>И буду лишь с тобою −</div><div class = \'tab\'>Любовь так горяча!</div><br/><div class = \'tab\'>И вот настало время,</div><div class = \'tab\'>Тебя увидел я,</div><div class = \'tab\'>Забыл слова красивые,</div><div class = \'tab\'>Любимая моя!</div><br/><div class = \'tab\'>Но всѐ прошло прекрасно −</div><div class = \'tab\'>Застенчивость ушла.</div><div class = \'tab\'>Ты помогла мне бережно</div><div class = \'tab\'>Сказать, что думал я.</div><br/><div class = \'tab\'>Ты опустилась с неба,</div><div class = \'tab\'>Собой меня маня,</div><div class = \'tab\'>Счастливая, весѐлая,</div><div class = \'tab\'>Сказала: \"Я твоя!\"</div><br/><div class = \'tab\'>Я счастлив, что с тобою</div><div class = \'tab\'>Нашли любовь вдвоѐм,</div><div class = \'tab\'>Которая позволила</div><div class = \'tab\'>Построить общий дом.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(516, 35, 'Я твой, а ты моя', '<div class = \'tab\'>Привет, привет, привет,</div><div class = \'tab\'>Любимая моя!</div><div class = \'tab\'>Прошло уж столько лет –</div><div class = \'tab\'>Я твой, а ты моя.</div><br/><div class = \'tab\'>История нашей любви</div><div class = \'tab\'>В запахах юности свежей,</div><div class = \'tab\'>Радость от счастья в груди,</div><div class = \'tab\'>Твой поцелуй такой нежный!</div><br/><div class = \'tab\'>Любовь окрылила меня –</div><div class = \'tab\'>Прислала счастливый привет,</div><div class = \'tab\'>Волнуюсь по-прежнему я,</div><div class = \'tab\'>Услышав любимой ответ.</div><br/><div class = \'tab\'>Пускай много лет уж прошло,</div><div class = \'tab\'>Но чувствую даже сейчас</div><div class = \'tab\'>Души твоей милой тепло</div><div class = \'tab\'>И вижу сияние глаз.</div><br/><div class = \'tab\'>Любовь отогрела меня,</div><div class = \'tab\'>Тоска вся ушла без осадка,</div><div class = \'tab\'>Люблю я сейчас, как тогда –</div><div class = \'tab\'>Я твой, ты моя без остатка.</div><br/><div class = \'tab\'>Привет, привет, привет,</div><div class = \'tab\'>Любимая моя!</div><div class = \'tab\'>Прошло уж сколько лет –</div><div class = \'tab\'>Я твой, а ты моя.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(517, 35, 'Моя люба', '<div class = \'tab\'>Давно-давно спостерігаю,</div><div class = \'tab\'>Одну загадку у собі:</div><div class = \'tab\'>Чому голубу лиш бажаю,</div><div class = \'tab\'>Чому люблю тільки її?</div><br/><div class = \'tab\'>Моя ти люба половинка,</div><div class = \'tab\'>Тебе люблю я усі дні,</div><div class = \'tab\'>Тебе я поруч відчуваю,</div><div class = \'tab\'>Тебе я бачу в кожнім сні.</div><br/><div class = \'tab\'>А ще я бачу кожен ранок –</div><div class = \'tab\'>Бажання посилаєш ти мені,</div><div class = \'tab\'>Хоча тобі нелегко в собі,</div><div class = \'tab\'>Авжеж, ти ось, а не у сні.</div><br/><div class = \'tab\'>Наступний день приходить знов,</div><div class = \'tab\'>І ми готові до роботи.</div><div class = \'tab\'>Ми Богу дякуєм постійно,</div><div class = \'tab\'>Що спільні є у нас турботи.</div><br/><div class = \'tab\'>Турботи наші про родину:</div><div class = \'tab\'>Синів, онуків, невісток.</div><div class = \'tab\'>У цьому є секрет єдиний –</div><div class = \'tab\'>Любові вічної виток.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(518, 35, 'Музыка звучит', '<div class = \'tab\'>Музыка звучит –</div><div class = \'tab\'>Меня воодушевляет,</div><div class = \'tab\'>Музыка плывѐт,</div><div class = \'tab\'>Аккордами играет.</div><br/><div class = \'tab\'>Музыка звучит –</div><div class = \'tab\'>Оттенками маня,</div><div class = \'tab\'>Музыка звучит,</div><div class = \'tab\'>Зовѐт меня.</div><br/><div class = \'tab\'>Зовѐт в страну –</div><div class = \'tab\'>Страну чудес,</div><div class = \'tab\'>Своим волшебным,</div><div class = \'tab\'>Ясным звоном.</div><br/><div class = \'tab\'>Тревожит душу,</div><div class = \'tab\'>Бодрит звеня,</div><div class = \'tab\'>Каскадом звуков</div><div class = \'tab\'>Пленит меня.</div><br/><div class = \'tab\'>Оркестром славным,</div><div class = \'tab\'>Слаженным аккордом,</div><div class = \'tab\'>Гитары томной</div><div class = \'tab\'>С приятным перебором,</div><br/><div class = \'tab\'>И скрипки солнечной</div><div class = \'tab\'>Эффектный водопад</div><div class = \'tab\'>Звучит стремительно,</div><div class = \'tab\'>Волшебно в такт.</div><br/><div class = \'tab\'>Музыка звучит –</div><div class = \'tab\'>Меня воодушевляет,</div><div class = \'tab\'>Музыка звучит −</div><div class = \'tab\'>Меня ласкает.</div><br/><div class = \'tab\'>Ласкает нежно –</div><div class = \'tab\'>Пленит меня,</div><div class = \'tab\'>Музыка звучит:</div><div class = \'tab\'>\"Ты мой, а я твоя!\"</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(519, 35, 'Весенний букет', '<div class = \'tab\'>Весна мне прислала букет</div><div class = \'tab\'>Подснежников нежных и милых.</div><div class = \'tab\'>Каким же твой будет ответ</div><div class = \'tab\'>В поступках решительных, смелых?</div><br/><div class = \'tab\'>Тебе я сказал много слов,</div><div class = \'tab\'>Готов повторять снова, снова.</div><div class = \'tab\'>Возникла меж нами любовь –</div><div class = \'tab\'>Всей жизненной силы основа.</div><br/><div class = \'tab\'>Весна мне открыла секрет</div><div class = \'tab\'>О счастья минутах отрадных.</div><div class = \'tab\'>Я жду не дождусь твой ответ,</div><div class = \'tab\'>В мечтах я сгораю приятных.</div><br/><div class = \'tab\'>Молил я о встрече зимой</div><div class = \'tab\'>И грел твоѐ сердце усердно,</div><div class = \'tab\'>Я вижу тебя лишь со мной,</div><div class = \'tab\'>Ты будешь моей непременно.</div><br/><div class = \'tab\'>Весна мне прислала букет</div><div class = \'tab\'>Подснежников нежных и милых,</div><div class = \'tab\'>В глазах твоих вижу ответ,</div><div class = \'tab\'>В таких неземных, синих, смелых...</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(520, 35, 'Демон', '<div class = \'tab\'>Тебя я вижу как во сне,</div><div class = \'tab\'>Так ждал я снова нашей встречи,</div><div class = \'tab\'>А ты клялась – придешь ко мне</div><div class = \'tab\'>В тот наш последний, летний вечер.</div><br/><div class = \'tab\'>Судьба разводит, как котят,</div><div class = \'tab\'>И ей не жалко нас с тобою.</div><div class = \'tab\'>Я улетаю – боги так хотят.</div><div class = \'tab\'>О, как хочу забрать тебя с собою!</div><br/><div class = \'tab\'>Но кто-то рядом супротив</div><div class = \'tab\'>Играет чувствами до боли,</div><div class = \'tab\'>Не хочет мне помочь – напротив –</div><div class = \'tab\'>Он захватил остаток воли.</div><br/><div class = \'tab\'>Ну что за демон и откуда?</div><div class = \'tab\'>Вдруг появился не вчера,</div><div class = \'tab\'>Залез в сердца наши повсюду,</div><div class = \'tab\'>Забрал все наши вечера.</div><br/><div class = \'tab\'>Забрал бесстыдно и нахально,</div><div class = \'tab\'>Взорвавши мир привычный нам.</div><div class = \'tab\'>Все то, что демону похвально,</div><div class = \'tab\'>То ведомо всесильным лишь богам.</div><br/><div class = \'tab\'>...Душой болеем глубоко,</div><div class = \'tab\'>Ведь мы простые, без прикрас,</div><div class = \'tab\'>Жалеем всѐ же об одном,</div><div class = \'tab\'>Что всѐ написано про нас.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(521, 35, 'Свидание', '<div class = \'tab\'>В твоем окне вечерний свет</div><div class = \'tab\'>Увидел я и жду ответ.</div><div class = \'tab\'>Хотя ты мне уж много раз</div><div class = \'tab\'>Придти клялась в условный час.</div><br/><div class = \'tab\'>Твой силуэт еще в окне</div><div class = \'tab\'>Твой профиль, твой анфас,</div><div class = \'tab\'>А я как будто весь в огне</div><div class = \'tab\'>И в мыслях только лишь о нас.</div><br/><div class = \'tab\'>Не ухожу. Прошел уж час –</div><div class = \'tab\'>Надеюсь и гадаю,</div><div class = \'tab\'>Корю себя и эту страсть,</div><div class = \'tab\'>В порыве ревности сгораю.</div><br/><div class = \'tab\'>Калитки скрип услышал я –</div><div class = \'tab\'>Забилось сердце как мотор,</div><div class = \'tab\'>И тут увидел я тебя,</div><div class = \'tab\'>Летящей словно метеор.</div><br/><div class = \'tab\'>Я благодарен так судьбе</div><div class = \'tab\'>Безмерно рад тебя я видеть.</div><div class = \'tab\'>Твой поцелуй забрал себе,</div><div class = \'tab\'>Дарю и свой, чтоб не обидеть.</div><br/><div class = \'tab\'>Счастливый свет в твоем окне</div><div class = \'tab\'>Увидел я и жду признаний.</div><div class = \'tab\'>Прошу тебя в который раз</div><div class = \'tab\'>Не пропускать наших свиданий.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(522, 35, 'Нам хорошо вдвоѐм', '<div class = \'tab\'>Как хорошо –</div><div class = \'tab\'>Сегодня мы вдвоѐм.</div><div class = \'tab\'>Как хорошо,</div><div class = \'tab\'>Что мы ещѐ живѐм.</div><br/><div class = \'tab\'>Душа поѐт,</div><div class = \'tab\'>Желает продолжений,</div><div class = \'tab\'>Любовь светла</div><div class = \'tab\'>Без всяких одолжений.</div><br/><div class = \'tab\'>Тебя, тебя благодарю,</div><div class = \'tab\'>Благодарю за ласку,</div><div class = \'tab\'>Люблю тебя, тебя люблю!</div><div class = \'tab\'>И верю очень в нашу сказку.</div><br/><div class = \'tab\'>В душе мы</div><div class = \'tab\'>С нежностью живѐм,</div><div class = \'tab\'>Всѐ так же любим,</div><div class = \'tab\'>Как тогда.</div><br/><div class = \'tab\'>В начале нашего</div><div class = \'tab\'>Счастливого пути,</div><div class = \'tab\'>Когда впервые</div><div class = \'tab\'>Нас свела судьба.</div><br/><div class = \'tab\'>Тебя, тебя благодарю,</div><div class = \'tab\'>Благодарю за нежность,</div><div class = \'tab\'>Люблю тебя, тебя люблю!</div><div class = \'tab\'>И верю очень в нашу верность.</div><br/><div class = \'tab\'>Прошли года –</div><div class = \'tab\'>Нет! Пролетели,</div><div class = \'tab\'>Назад возврата</div><div class = \'tab\'>Уже нет.</div><br/><div class = \'tab\'>Любовь жива,</div><div class = \'tab\'>И в памяти всплывают</div><div class = \'tab\'>Счастливые минуты</div><div class = \'tab\'>Сегодня, как тогда.</div><br/><div class = \'tab\'>Тебя, тебя благодарю,</div><div class = \'tab\'>Благодарю за ласку,</div><div class = \'tab\'>Люблю тебя, тебя люблю!</div><div class = \'tab\'>И верю очень в нашу сказку.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(523, 35, 'Твои глаза и губы', '<div class = \'tab\'>Девичий запах твоих губ</div><div class = \'tab\'>Твои лучистые глаза</div><div class = \'tab\'>В моей судьбе прошли как след,</div><div class = \'tab\'>Оставив память навсегда.</div><br/><div class = \'tab\'>Глаза твои, как васильки,</div><div class = \'tab\'>Так сильно-сильно притянули</div><div class = \'tab\'>Меня к тебе - моя любовь,</div><div class = \'tab\'>Что вместе мы на верность присягнули.</div><br/><div class = \'tab\'>Твои глаза, твои глаза и губы,</div><div class = \'tab\'>Свели \"с ума\" меня надолго,</div><div class = \'tab\'>Как было это все давно,</div><div class = \'tab\'>Но в васильки я верю долго.</div><br/><div class = \'tab\'>Как было это все давноСовместно слились воедино,</div><div class = \'tab\'>Ведь, я не думал, что вдвоем</div><div class = \'tab\'>Приятно быть в гармонии единой.</div><div class = \'tab\'>Девичий запах твоих губТвои лучистые глаза</div><br/><div class = \'tab\'>В моей судьбе прошли как след,</div><div class = \'tab\'>Оставив память навсегда.</div>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(524, 35, 'Щаслива мить', '<div class = \'tab\'>Травневий дощ</div><div class = \'tab\'>Вечірньою порою</div><div class = \'tab\'>Миттєво дав</div><div class = \'tab\'>Мені любов.</div><br/><div class = \'tab\'>При нашій зустрічі</div><div class = \'tab\'>З тобою,</div><div class = \'tab\'>Мов блискавка</div><div class = \'tab\'>Проскочила тут знов.</div><br/><div class = \'tab\'>Моя любов</div><div class = \'tab\'>Тебе всю підкорила,</div><div class = \'tab\'>Взяла в полон</div><div class = \'tab\'>І новий світ відкрила.</div><br/><div class = \'tab\'>Я враз відчув</div><div class = \'tab\'>Красу жіночої статі</div><div class = \'tab\'>І вперше покохав,</div><div class = \'tab\'>Коли тебе побачив.</div><br/><div class = \'tab\'>Твої слова,</div><div class = \'tab\'>Ще очі й руки</div><div class = \'tab\'>Запам\'ятав</div><div class = \'tab\'>На все життя.</div><br/><div class = \'tab\'>Ми вбереглися</div><div class = \'tab\'>Від розлуки –</div><div class = \'tab\'>У всьому винна</div><div class = \'tab\'>Лагідність твоя.</div><br/><div class = \'tab\'>Наші роки</div><div class = \'tab\'>Миттєво пролетіли,</div><div class = \'tab\'>Мов з полювання</div><div class = \'tab\'>Грізні соколи,</div><br/><div class = \'tab\'>Але та мить,</div><div class = \'tab\'>Яскрава мить</div><div class = \'tab\'>Тримає нас,</div><div class = \'tab\'>З\'єднавши на віки.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(525, 35, 'Моя сирень', '<div class = \'tab\'>Куст сирени за окном –</div><div class = \'tab\'>Утром расцветает,</div><div class = \'tab\'>Запах милых твоих губ</div><div class = \'tab\'>Далеко летает.</div><br/><div class = \'tab\'>Рано встал, к тебе пришѐл</div><div class = \'tab\'>И любуюсь нежно,</div><div class = \'tab\'>Вижу глаз сиянье вновь –</div><div class = \'tab\'>Любишь ты, как прежде.</div><br/><div class = \'tab\'>Цвет сирени за окном</div><div class = \'tab\'>К вечеру закрылся,</div><div class = \'tab\'>Я хожу к тебе давно</div><div class = \'tab\'>Снова я влюбился.</div><br/><div class = \'tab\'>Полюбил тебя давно,</div><div class = \'tab\'>И живѐм мы вместе,</div><div class = \'tab\'>Сорок пять годков подряд</div><div class = \'tab\'>\"Я хожу к невесте\".</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(526, 35, 'Побудь зі мною', '<div class = \'tab\'>Я так люблю твої слова,</div><div class = \'tab\'>Ласкаві, теплі губи.</div><div class = \'tab\'>До тебе є моя жага,</div><div class = \'tab\'>Моя єдина люба.</div><br/><div class = \'tab\'>Не можу надивитися на тебе −</div><div class = \'tab\'>Заполонила ти мій світ,</div><div class = \'tab\'>Прошу тебе, іди до мене,</div><div class = \'tab\'>Побудь зі мною лише мить.</div><br/><div class = \'tab\'>Без тебе я втрачаю сенс</div><div class = \'tab\'>В житті, сумую – не радію.</div><div class = \'tab\'>Прийди до мене – я чекаю</div><div class = \'tab\'>Та маю знов надію.</div><br/><div class = \'tab\'>Надія є, ми поруч, разом</div><div class = \'tab\'>Зустріли наш світанок.</div><div class = \'tab\'>Ти віддала себе сповна,</div><div class = \'tab\'>У цей чудовий ранок.</div><br/><div class = \'tab\'>Не можу відірватися від тебе,</div><div class = \'tab\'>Ти захопила увесь світ.</div><div class = \'tab\'>Прошу тебе, не йди від мене,</div><div class = \'tab\'>Святкуй зі мною щастя мить.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(527, 35, 'Верные глаза', '<div class = \'tab\'>Моя душа желает продолженья</div><div class = \'tab\'>Любви прекрасной и святой,</div><div class = \'tab\'>Тебя я снова увидеть желаю –</div><div class = \'tab\'>Последний разговор был непростой.</div><br/><div class = \'tab\'>Не так мечтал его закончить –</div><div class = \'tab\'>Ведь мы с тобой имеем равные права.</div><div class = \'tab\'>Истории других людей нас учат...</div><div class = \'tab\'>Постой! Вернись – ты не права.</div><br/><div class = \'tab\'>Зачем коришь меня напрасно?</div><div class = \'tab\'>В чѐм виноват – я не пойму.</div><div class = \'tab\'>Ты знаешь точно, достоверно,</div><div class = \'tab\'>Что первым снова не уйду.</div><br/><div class = \'tab\'>В любви все возрасты прекрасны,</div><div class = \'tab\'>А ссоры разрушают мир.</div><div class = \'tab\'>Твои старания напрасны –</div><div class = \'tab\'>Давно об этом нам поведал Лир.</div><br/><div class = \'tab\'>Душа моя желает встречи –</div><div class = \'tab\'>Нашѐл я точные слова,</div><div class = \'tab\'>Я вижу нас лишь только вместе,</div><div class = \'tab\'>Я вижу верные глаза.</div><br/><div class = \'tab\'>Они не лгут – они страдают</div><div class = \'tab\'>И слов прекрасных ожидают,</div><div class = \'tab\'>В порывах страсти не сгорают</div><div class = \'tab\'>И нотки правды понимают.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(528, 35, 'Букет белых роз', '<div class = \'tab\'>Свежие розы, белые розы</div><div class = \'tab\'>Утром усеяны легкими росами.</div><div class = \'tab\'>Взял я для милой несколько роз</div><div class = \'tab\'>Иду на свидание − счастлив от грѐз.</div><br/><div class = \'tab\'>Белые розы омыты росой,</div><div class = \'tab\'>Где феромоны в запахах тонут.</div><div class = \'tab\'>Всѐ для тебя такой милой-родной,</div><div class = \'tab\'>Я твоей нежностью тронут.</div><br/><div class = \'tab\'>В парке под кленом,</div><div class = \'tab\'>Вечерней порою,</div><div class = \'tab\'>В назначенный день</div><div class = \'tab\'>Жду встречи с тобою.</div><br/><div class = \'tab\'>Взор от букета</div><div class = \'tab\'>Нельзя оторвать,</div><div class = \'tab\'>Жду я ответа –</div><div class = \'tab\'>Готов вечно ждать.</div><br/><div class = \'tab\'>Любовь пробудилась</div><div class = \'tab\'>При встрече с тобою.</div><div class = \'tab\'>Верю, что ходит</div><div class = \'tab\'>Счастье со мною.</div><br/><div class = \'tab\'>Розы влияют</div><div class = \'tab\'>На верный ответ –</div><div class = \'tab\'>Надежду вселяют,</div><div class = \'tab\'>Услышать: \"Привет...\"</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(529, 35, 'Дівчина-стріла', '<div class = \'tab\'>Дівчина юна і мила –</div><div class = \'tab\'>Років сімнадцять їй,</div><div class = \'tab\'>Раптом проходила мимо</div><div class = \'tab\'>І посміхнулась мені.</div><br/><div class = \'tab\'>Усмішка та чарувала,</div><div class = \'tab\'>Блискавкою в серце ввійшла,</div><div class = \'tab\'>Довго мене милувала,</div><div class = \'tab\'>Наскрізь пройшла, як стріла.</div><br/><div class = \'tab\'>Радію так зустрічі з нею,</div><div class = \'tab\'>Бажаю я кожного дня</div><div class = \'tab\'>Зустріти її у дорозі,</div><div class = \'tab\'>Підтримати радість життя.</div><br/><div class = \'tab\'>Любові нема перешкоди,</div><div class = \'tab\'>Шукаю причину щодня</div><div class = \'tab\'>Зустріти дівчину зненацька,</div><div class = \'tab\'>В якої сховалась стріла.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(530, 35, 'Калинушка', '<div class = \'tab\'>Калина красная, калина новая</div><div class = \'tab\'>Растѐт в садочке под окном.</div><div class = \'tab\'>Калина красная, калина милая,</div><div class = \'tab\'>И мы теперь с калинушкой вдвоѐм.</div><br/><div class = \'tab\'>Я очень жду и замираю,</div><div class = \'tab\'>Чтоб по весне укрылася листом,</div><div class = \'tab\'>И первый цвет \"невесты\" наблюдаю,</div><div class = \'tab\'>Как-будто вместе с нею под венцом.</div><br/><div class = \'tab\'>Живѐм в гармонии и мире –</div><div class = \'tab\'>Люблю еѐ я белые цветы.</div><div class = \'tab\'>Она мне утром улыбается,</div><div class = \'tab\'>И с нею мы давно уже на \"ты\".</div><br/><div class = \'tab\'>И хорошо идти мне к ней на встречу,</div><div class = \'tab\'>Плоды рисует образ мой.</div><div class = \'tab\'>Давно-давно я замечаю –</div><div class = \'tab\'>Она любуется собой.</div><br/><div class = \'tab\'>Кокетка флирт затеяла по-новой –</div><div class = \'tab\'>Играет красками маня,</div><div class = \'tab\'>Обворожила милая калина,</div><div class = \'tab\'>Влюбился по уши и я.</div><br/><div class = \'tab\'>Калина красная – \"жена\" прекрасная</div><div class = \'tab\'>Живѐт в саду, саду моѐм.</div><div class = \'tab\'>Калина красная, калина милая,</div><div class = \'tab\'>Мы так давно с калинушкой вдвоѐм.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(531, 36, 'Вода біжить', '<div class = \'tab\'>Вода холодна із криниці</div><div class = \'tab\'>Тече до річки, як сльоза,</div><div class = \'tab\'>Щодня поблискує на сонці,</div><div class = \'tab\'>І нею живиться лоза.</div><br/><div class = \'tab\'>Зростають соковиті трави</div><div class = \'tab\'>В заплаві Пени, аж до Псла</div><div class = \'tab\'>Копиці сіна величаві,</div><div class = \'tab\'>Накошені коровам і коням.</div><br/><div class = \'tab\'>Рясні дощі приносять воду,</div><div class = \'tab\'>Яка заплаву залива,</div><div class = \'tab\'>А перший з осені морозець</div><div class = \'tab\'>Гладеньким льодом укрива.</div><br/><div class = \'tab\'>Тут всі чекають на розваги,</div><div class = \'tab\'>Де мóлоді веселий ґвалт:</div><div class = \'tab\'>Там хлопців-козаків ватаги</div><div class = \'tab\'>Санчата, ковзани й юрба дівчат.</div><br/><div class = \'tab\'>Отак постійно в берегах</div><div class = \'tab\'>Вода джерельна \"гомонить\",</div><div class = \'tab\'>Між лоз і кладок дерев’яних</div><div class = \'tab\'>Вона біжить, біжить, біжить.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(532, 36, 'Вирішення проблем', '<div class = \'tab\'>Зібрались якось на нараду</div><div class = \'tab\'>Чиновники високого польоту</div><div class = \'tab\'>З метою розробити ту пораду,</div><div class = \'tab\'>Щоб вирішила всі проблеми зльоту!</div><br/><div class = \'tab\'>Нарешті прибуло гостей багато</div><div class = \'tab\'>З усіх куточків нашої держави:</div><div class = \'tab\'>До міста Києва, немов на свято</div><div class = \'tab\'>Приїхали спеціалісти по порадах.</div><br/><div class = \'tab\'>Питання гострі тут постали:</div><div class = \'tab\'>Чому ще не збираємо ми жито?</div><div class = \'tab\'>Чому у нас так мало хліба,</div><div class = \'tab\'>І взагалі, як завтра будем жити?</div><br/><div class = \'tab\'>А внаслідок бурхливих обговорень</div><div class = \'tab\'>Забули, що потрібно розглядати!</div><div class = \'tab\'>Міністр згадав, що вибори вже скоро…</div><div class = \'tab\'>І прибульці враз стали електоратом.</div><br/><div class = \'tab\'>Бичились, галділи, порядок денний розгляділи,</div><div class = \'tab\'>Прийняли єдине рішення всього:</div><div class = \'tab\'>Просунути в сільські й Верховну ради</div><div class = \'tab\'>Усіх прибічників і родичів міністра одного.</div><br/><div class = \'tab\'>А де ж те жито? З полів зібрали,</div><div class = \'tab\'>І його давно немає!</div><div class = \'tab\'>А як немає, то й гаразд:</div><div class = \'tab\'>Тоді й проблем у нашому селі немає!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(533, 36, 'Наш Універ', '<div class = \'tab\'>Чимало на світі куточків прекрасних,</div><div class = \'tab\'>З якими лишаєш частинку душі,</div><div class = \'tab\'>Що кожному близькі, у кожного власні,</div><div class = \'tab\'>Присвячують їм і пісні, і вірші.</div><br/><div class = \'tab\'>Є місце на рідній землі особливе,</div><div class = \'tab\'>Куди молодими прийшли восени,</div><div class = \'tab\'>Це наш Універ – alma mater мрійлива,</div><div class = \'tab\'>Де всі ми навчались чотири зими.</div><br/><div class = \'tab\'>Птахів щебетання, красива природа,</div><div class = \'tab\'>Ідеї, пізнання, книжки на столі…</div><div class = \'tab\'>Любов до навчання – це та нагорода,</div><div class = \'tab\'>Яку дарували нам всім вчителі.</div><br/><div class = \'tab\'>Нелегко навчання давалось спочатку,</div><div class = \'tab\'>Були нелегкими уроки життя,</div><div class = \'tab\'>Збирали докупи знання по зернятку,</div><div class = \'tab\'>Чекали нас істина і відкриття.</div><br/><div class = \'tab\'>Назавжди у серці посвята в студенти,</div><div class = \'tab\'>Практичні заняття та лекцій азарт.</div><div class = \'tab\'>Студентські змагання й дипломні проекти</div><div class = \'tab\'>Уже не повернеш назад…</div><br/><div class = \'tab\'>Люблю Універ до нестями, жагуче!</div><div class = \'tab\'>З тобою лишилась частинка буття!</div><div class = \'tab\'>Зустрінемось знову, мій випуск квітучий.</div><div class = \'tab\'>До нової зустрічі і вороття!</div><br/><div class = \'tab\'>Не будьмо байдужі до праці сільської,</div><div class = \'tab\'>Віддаймо цій справі себе до кінця!</div><div class = \'tab\'>Немає найкращої долі людської–</div><div class = \'tab\'>Донести й лишити свій слід у серцях!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1);
INSERT INTO `LYRICS` (`id`, `set_id`, `name`, `content`, `write_date`, `author`, `description`, `author_comment`, `lyrics_type`, `TABBED`, `BRS`, `STROPHE_LENGTH`, `STROPHE_DELIM`, `LINE_WRAPPER`, `LINE_WRAPPER_END`, `LINE_WRAPPER_TABBED`, `LINE_WRAPPER_TABBED_END`, `auto_format`) VALUES
(534, 36, 'Дарить надежду и любовь', '<div class = \'tab\'>Вода струится ручейком</div><div class = \'tab\'>И нежно всѐ прохладой омывает.</div><div class = \'tab\'>Смотри: растеньям так легко,</div><div class = \'tab\'>Природа снова отдыхает.</div><br/><div class = \'tab\'>Жара июльская сошла вконец –</div><div class = \'tab\'>Судьбу агрария жалеет.</div><div class = \'tab\'>Работе будет красочный венец –</div><div class = \'tab\'>Господь его старания лелеет.</div><br/><div class = \'tab\'>Надежда появляется опять,</div><div class = \'tab\'>На поле взгляд бросаю строгий...</div><div class = \'tab\'>Не уходи, прохлада, вспять −</div><div class = \'tab\'>Ведь это свыше знак Господний.</div><br/><div class = \'tab\'>Пшеницы будет урожай</div><div class = \'tab\'>Побольше прошлогоднего!</div><div class = \'tab\'>На каждый стол свой каравай –</div><div class = \'tab\'>Итог труда народного.</div><br/><div class = \'tab\'>Хвала тебе, Господь ты наш,</div><div class = \'tab\'>За силушку нам верную!</div><div class = \'tab\'>И в этот год терпенье дашь,</div><div class = \'tab\'>В судьбу свою я верую!</div><br/><div class = \'tab\'>Хочу творить и побеждать,</div><div class = \'tab\'>Всегда надеждой жить.</div><div class = \'tab\'>Любви к земле не избежать,</div><div class = \'tab\'>И надо ей служить!</div><br/><div class = \'tab\'>Я рад за подвиг трудовой</div><div class = \'tab\'>Хозяина-агрария!</div><div class = \'tab\'>Народ воздаст ему хвалу,</div><div class = \'tab\'>Свою любовь, признания!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(535, 36, 'Соняшник', '<div class = \'tab\'>Сонях у віконці</div><div class = \'tab\'>Квітне, мов те сонце,</div><div class = \'tab\'>Користь всім приносить</div><div class = \'tab\'>І турботи просить.</div><br/><div class = \'tab\'>Бджілки у вуликах</div><div class = \'tab\'>Разом «буркотять»,</div><div class = \'tab\'>Весь нектар із соняхів</div><div class = \'tab\'>Несуть як долетять!</div><br/><div class = \'tab\'>Соняшник міцненько</div><div class = \'tab\'>Зайняв усі края,</div><div class = \'tab\'>Навіть в Україні</div><div class = \'tab\'>Є такі поля!</div><br/><div class = \'tab\'>Всі, хто лиш наблизився</div><div class = \'tab\'>До цієї рослини, –</div><div class = \'tab\'>Мають все в достатку</div><div class = \'tab\'>В домі і родині.</div><br/><div class = \'tab\'>Але є потрібним</div><div class = \'tab\'>Соняшник-оливковий,</div><div class = \'tab\'>Щоб здоров’я людям</div><div class = \'tab\'>Швидко придавав.</div><br/><div class = \'tab\'>Так! У вчених-юр’ївців</div><div class = \'tab\'>Ці гібриди є,</div><div class = \'tab\'>Їх сьогодні створює</div><div class = \'tab\'>Група фахівців.</div><br/><div class = \'tab\'>Спільно з одеситами</div><div class = \'tab\'>Створені давно,</div><div class = \'tab\'>Мають вони назви:</div><div class = \'tab\'>Гектор й Ореол.</div><br/><div class = \'tab\'>В скриньці у селекції</div><div class = \'tab\'>Соняхи, як пальма, є,</div><div class = \'tab\'>Також зі стеаратом,</div><div class = \'tab\'>І з вітаміном «Е».</div><br/><div class = \'tab\'>Так шануйте соняшник,</div><div class = \'tab\'>Він у нас росте.</div><div class = \'tab\'>Маєм новий задум,</div><div class = \'tab\'>Тож бюджет зросте!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(536, 36, 'Душа агрария', '<div class = \'tab\'>Поле пшеницы янтарное</div><div class = \'tab\'>Стоит, красотою маня.</div><div class = \'tab\'>Навеки любовь лучезарная</div><div class = \'tab\'>С тем полем связала меня!</div><br/><div class = \'tab\'>Связала с народом аграрным,</div><div class = \'tab\'>С народом святого труда,</div><div class = \'tab\'>Стабильного, мирного, доброго,</div><div class = \'tab\'>Любимого всеми всегда.</div><br/><div class = \'tab\'>Процесс выпекания хлеба –</div><div class = \'tab\'>Священное действо для нас!</div><div class = \'tab\'>Душа хлебопашца и пекаря</div><div class = \'tab\'>Присутствует в каждом из вас.</div><br/><div class = \'tab\'>Мы знаем, что ваша работа</div><div class = \'tab\'>В почете во все времена.</div><div class = \'tab\'>Без капель солѐного пота</div><div class = \'tab\'>Не станет великой страна.</div><br/><div class = \'tab\'>А вам, едокам-сибаритам,</div><div class = \'tab\'>Советую чаще бывать</div><div class = \'tab\'>В бескрайних полях Украины –</div><div class = \'tab\'>Агрария душу понять.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(537, 36, 'Дідова порада', '<div class = \'tab\'>Дорогі внучата! Ось моя порада,</div><div class = \'tab\'>Слухайте уважно дідові слова.</div><div class = \'tab\'>І мені сьогодні дуже це важливо,</div><div class = \'tab\'>Що зернята Правди, як свята вода.</div><br/><div class = \'tab\'>З молоком матусі всмоктуємо Правду,</div><div class = \'tab\'>Її не заміниш грошем золотим.</div><div class = \'tab\'>Вам даю пораду: хай на користь буде</div><div class = \'tab\'>Правда в поєднанні з образом святим!</div><br/><div class = \'tab\'>З Правдою у серці міцно вам зрости,</div><div class = \'tab\'>Укупі всім триматись, «останніх не пасти»,</div><div class = \'tab\'>Набиратись мудрості та корисних знань,</div><div class = \'tab\'>Хай батьківські завіти ведуть вас до визнань!</div><br/><div class = \'tab\'>…Адже повернулась до усіх «по-новому»</div><div class = \'tab\'>Вимога сусіда не іти вперед.</div><div class = \'tab\'>Довелось отримати весь цей плід гірким,</div><div class = \'tab\'>Стати не Європою, а плестись за ним.</div><br/><div class = \'tab\'>Ні, мої внучата – це не ваша доля!</div><div class = \'tab\'>Це «підказка» ворога направлена до вас –</div><div class = \'tab\'>Вас скорити хочуть силою й брехнею.</div><div class = \'tab\'>Вам така «щедрота» нічого не дасть!</div><br/><div class = \'tab\'>Ми ж бо – українці, ми – не «НЛО»,</div><div class = \'tab\'>І в своїй сторонці лад наведемо!</div><div class = \'tab\'>Знайте: сила Правди вище права сили,</div><div class = \'tab\'>Вірте у державу, що Правдою зростили!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(538, 36, 'Про себе', '<div class = \'tab\'>Мені пощастило в дитинстві,</div><div class = \'tab\'>Я мешкав в чудовім краю,</div><div class = \'tab\'>Піснями оспіваним генієм,</div><div class = \'tab\'>Й сьогодні я ще у строю!</div><br/><div class = \'tab\'>Моє існування на світі</div><div class = \'tab\'>Відзначено богом самим</div><div class = \'tab\'>Дитинство суворе зі смислом</div><div class = \'tab\'>Дай, боженька, долі другим!</div><br/><div class = \'tab\'>Навчався у школі і вдома, –</div><div class = \'tab\'>Це шлях до пізнання в житті.</div><div class = \'tab\'>Любив я і школу, і технікум,</div><div class = \'tab\'>Любив вчителів я завжди.</div><br/><div class = \'tab\'>І батько, і мати так мріяли,</div><div class = \'tab\'>Щоб син їх навчався всьому.</div><div class = \'tab\'>І те, що бажали – здійснилося:</div><div class = \'tab\'>Навчився багато чому.</div><br/><div class = \'tab\'>Отримав диплом агронома,</div><div class = \'tab\'>Червоний диплом, оце так!</div><div class = \'tab\'>Спішив на роботу у поле,</div><div class = \'tab\'>Хоча був смішний, як дивак.</div><br/><div class = \'tab\'>Навалював міцно роботу</div><div class = \'tab\'>На плечі та руки свої,</div><div class = \'tab\'>Не йшов я додому до ночі</div><div class = \'tab\'>Й хотів розібратись в собі.</div><br/><div class = \'tab\'>Всього не навчили у «бурсі», –</div><div class = \'tab\'>Так звали ми технікум свій –</div><div class = \'tab\'>Хотілося жити в науці,</div><div class = \'tab\'>Щоб перевернути цей світ.</div><br/><div class = \'tab\'>А згодом служив я народу,</div><div class = \'tab\'>Три роки віддав я муштрі,</div><div class = \'tab\'>В курсантській навчався я роті,</div><div class = \'tab\'>А потім навчав «школярів».</div><br/><div class = \'tab\'>Пройшли ці роки не даремно,</div><div class = \'tab\'>Надбав я і сили, і знань</div><div class = \'tab\'>Терплячим я став й принциповим</div><div class = \'tab\'>Від наших армійських змагань.</div><br/><div class = \'tab\'>Про свій Універ вже писав я, –</div><div class = \'tab\'>То кращі роки, як в раю!</div><div class = \'tab\'>Весь досвід від армії й «бурси»</div><div class = \'tab\'>Я друзям усім віддаю.</div><br/><div class = \'tab\'>Роки набування освіти</div><div class = \'tab\'>Пройшли недаремно, повір:</div><div class = \'tab\'>Майстри-вихователі вміло</div><div class = \'tab\'>Знання надавали мені.</div><br/><div class = \'tab\'>Мої вчителі завжди вчили:</div><div class = \'tab\'>Потрібно любити людей.</div><div class = \'tab\'>Вони у студентах ростили</div><div class = \'tab\'>Появу славетних ідей.</div><br/><div class = \'tab\'>Так! Згодом вони і з\'явились:</div><div class = \'tab\'>Гібриди, сорти, технології,</div><div class = \'tab\'>Й назавжди в душі оселились</div><div class = \'tab\'>Всі учні мої з біології!</div><br/><div class = \'tab\'>Вони вже очолили напрями,</div><div class = \'tab\'>І відлік в науці ведуть,</div><div class = \'tab\'>А завтра вони знамениті</div><div class = \'tab\'>Черговий свій залік здадуть.</div><br/><div class = \'tab\'>Оце – нагорода учителю,</div><div class = \'tab\'>Який готував для життя.</div><div class = \'tab\'>І так поступово щороку</div><div class = \'tab\'>Пробуджуєм ми майбуття.</div><br/><div class = \'tab\'>І можна так все перелічувать</div><div class = \'tab\'>Безмежно і довго підряд,</div><div class = \'tab\'>Стомити Вас дуже не хочеться,</div><div class = \'tab\'>Вже досить дідівських порад.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(539, 36, 'Перша школа', '<div class = \'tab\'>Школа моя біля дому,</div><div class = \'tab\'>Знайомі стежки в спориші…</div><div class = \'tab\'>Все рідне, до болю знайоме</div><div class = \'tab\'>Зі мною назавжди в душі.</div><br/><div class = \'tab\'>Вчителька перша, красива,</div><div class = \'tab\'>Вела нас в поля по ріллі</div><div class = \'tab\'>Й навчала любові до ниви,</div><div class = \'tab\'>До праці й людей на землі.</div><br/><div class = \'tab\'>Школа колгоспна, старенька…</div><div class = \'tab\'>І класи, і вікна малі,</div><div class = \'tab\'>Не нові, але чепурненькі, –</div><div class = \'tab\'>Бо дбали про неї в селі.</div><br/><div class = \'tab\'>Мої першокласники-друзі</div><div class = \'tab\'>Слухняні були й навпаки,</div><div class = \'tab\'>Та жили ми всі у союзі</div><div class = \'tab\'>У ті повоєнні роки.</div><br/><div class = \'tab\'>Але головне неодмінно –</div><div class = \'tab\'>Щасливі й бадьорі завжди –</div><div class = \'tab\'>Навчались ми вмінню й сумлінню,</div><div class = \'tab\'>У школі не знали біди.</div><br/><div class = \'tab\'>Сім років пройшли як секунда –</div><div class = \'tab\'>Дорослими стали ми всі,</div><div class = \'tab\'>А з восьмого класу навчались</div><div class = \'tab\'>В сусідньому з нашим селі.</div><br/><div class = \'tab\'>І ті кілометри, ті відстані</div><div class = \'tab\'>Я згадую з радістю знов...</div><div class = \'tab\'>І зараз та школа працює,</div><div class = \'tab\'>Бо в ній збереглася любов.</div><br/><div class = \'tab\'>Любов до малечі й дорослих,</div><div class = \'tab\'>Любов до навчання й батьків,</div><div class = \'tab\'>Любов до села і Вітчизни,</div><div class = \'tab\'>Любов, що не знає років.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(540, 36, 'Думка про озимі', '<div class = \'tab\'>Зима так раптово з’являється</div><div class = \'tab\'>У нашім чудовім краю,</div><div class = \'tab\'>Мороз уночі проявляється –</div><div class = \'tab\'>Не терпить поразку свою.</div><br/><div class = \'tab\'>Оточує снігом поспішно</div><div class = \'tab\'>Масиви зелених полів.</div><div class = \'tab\'>Озимі заснули успішно,</div><div class = \'tab\'>Як боженька їм повелів.</div><br/><div class = \'tab\'>Й наука зробила свій внесок</div><div class = \'tab\'>В природу генома цього:</div><div class = \'tab\'>Він міститься в серці пшениці,</div><div class = \'tab\'>Де в генах достатньо всього.</div><br/><div class = \'tab\'>Не треба спинятися тільки,</div><div class = \'tab\'>Потрібно ще часу ген-ген,</div><div class = \'tab\'>Та з’явиться сорт зимостійкий,</div><div class = \'tab\'>В якому «сховався» той ген!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(541, 36, 'Весенняя страда...', '<div class = \'tab\'>Солнышко рано встаѐт</div><div class = \'tab\'>Сияет лучами отменно,</div><div class = \'tab\'>Ясен и чист небосвод,</div><div class = \'tab\'>Денѐк стал длиннее заметно.</div><br/><div class = \'tab\'>Зимняя спячка прошла,</div><div class = \'tab\'>Первый цветочек приметен,</div><div class = \'tab\'>Страда полевая в село подошла,</div><div class = \'tab\'>Азарт и в науке заметен...</div><br/><div class = \'tab\'>Подведен последний «штришок»,</div><div class = \'tab\'>К посеву готовы отделы,</div><div class = \'tab\'>Наметили точный денѐк,</div><div class = \'tab\'>А это – почти что полдела.</div><br/><div class = \'tab\'>Шум-гам у приѐмной стоит,</div><div class = \'tab\'>Всем хочется первым наметить</div><div class = \'tab\'>Свой новый поход полевой,</div><div class = \'tab\'>Директор ведь это отметит!</div><br/><div class = \'tab\'>Из города прибыли люди,</div><div class = \'tab\'>Автобусы еле живы!</div><div class = \'tab\'>В поля все спешат на работу –</div><div class = \'tab\'>Сегодня ведь руки нужны.</div><br/><div class = \'tab\'>Ручные посевы пшеницы</div><div class = \'tab\'>Так просятся срочно прилечь</div><div class = \'tab\'>Во влажную землю, как первенцы,</div><div class = \'tab\'>Поэтому надо \"рабсилу\" привлечь.</div><br/><div class = \'tab\'>Горох уступать не желает,</div><div class = \'tab\'>Готов к посевной на все сто!</div><div class = \'tab\'>Завлаб в селекцентре сгорает –</div><div class = \'tab\'>Торопит любимых «орлов».</div><br/><div class = \'tab\'>Ячмень надо в поле уж бросить,</div><div class = \'tab\'>К работе готовы сотрудники все,</div><div class = \'tab\'>Куратор подмогу в дирекции просит,</div><div class = \'tab\'>Поддавшись весенней страде.</div><br/><div class = \'tab\'>И поздние все поспешают,</div><div class = \'tab\'>Готовят успешно \"посмат\",</div><div class = \'tab\'>Но всѐ же тепло ожидают</div><div class = \'tab\'>И в небо с надеждой глядят.</div><br/><div class = \'tab\'>И так каждый год повторяется –</div><div class = \'tab\'>Процесс при посеве весной.</div><div class = \'tab\'>В селекции сорт появляется,</div><div class = \'tab\'>Как новый продукт основной.</div><br/><div class = \'tab\'>Пройдѐт он свой путь испытаний,</div><div class = \'tab\'>И в жѐсткой, серьѐзной борьбе</div><div class = \'tab\'>Он сдаст свой особый экзамен</div><div class = \'tab\'>И будет приравнен к звезде.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(542, 36, 'Ялинки', '<div class = \'tab\'>Три ялинки біля дому</div><div class = \'tab\'>Посадили два сини,</div><div class = \'tab\'>І пройшло багато років –</div><div class = \'tab\'>З ними й виросли вони.</div><br/><div class = \'tab\'>Три ялинки – три красуні</div><div class = \'tab\'>Вже й онуків радують:</div><div class = \'tab\'>В Новім році всі під ними,</div><div class = \'tab\'>Мріяння загадують.</div><br/><div class = \'tab\'>Кожен день ми поглядаєм</div><div class = \'tab\'>На красу з вікна.</div><div class = \'tab\'>Прийде час – і всі бажання</div><div class = \'tab\'>Збудуться сповна.</div><br/><div class = \'tab\'>Не лінуйтесь, люди добрі,</div><div class = \'tab\'>Кличте завзяття –</div><div class = \'tab\'>Посадіть дерев побільше</div><div class = \'tab\'>За своє життя.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(543, 36, 'Нашому вчителю', '<div class = \'tab\'>В буденні і святкові дні</div><div class = \'tab\'>Ми стали наче вчителі,</div><div class = \'tab\'>Яких зустріли ми</div><div class = \'tab\'>У нашому щасливому бутті.</div><br/><div class = \'tab\'>Всі пам\'ятаємо той час,</div><div class = \'tab\'>Хто нас зустрів і руку дав,</div><div class = \'tab\'>Хто нас любив і хто так дбав</div><div class = \'tab\'>Про долю кожного із нас.</div><br/><div class = \'tab\'>...Сьогодні свято Вчителів</div><div class = \'tab\'>В особі нашого Михайла.</div><div class = \'tab\'>Дійшов до певної межі,</div><div class = \'tab\'>Але це не кінець.</div><br/><div class = \'tab\'>Йому і зараз, мов \"двадцятник\",</div><div class = \'tab\'>І можна прямо під вінець.</div><div class = \'tab\'>Соратник, однодумець,</div><div class = \'tab\'>Нехай Вам душу зігріває</div><br/><div class = \'tab\'>Любов від нашого кола,</div><div class = \'tab\'>Яке поповнюється враз,</div><div class = \'tab\'>Як тільки рік мина.</div><div class = \'tab\'>Всі ми цінуємо Вас дуже</div><br/><div class = \'tab\'>Бажаємо і щастя, і добра!</div><div class = \'tab\'>Живіть, працюйте, вірний друже,</div><div class = \'tab\'>Хай будуть довгими літа.</div><div class = \'tab\'>У повсякденному житті</div><br/><div class = \'tab\'>Ми помічаємо усі,</div><div class = \'tab\'>Що навіть думка в нас одна –</div><div class = \'tab\'>Така, як у М. А. Бобра.</div>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(544, 36, 'Селянська завзятість', '<div class = \'tab\'>Спекотна петрівка прийшла,</div><div class = \'tab\'>Пшениця аж сяє в долоньці,</div><div class = \'tab\'>Ячмінна пора підійшла,</div><div class = \'tab\'>І соняшник квітне на сонці.</div><br/><div class = \'tab\'>В турботах селяни завжди,</div><div class = \'tab\'>Тому що найважча пора</div><div class = \'tab\'>Зібрати зерно до пори</div><div class = \'tab\'>Та й інших завдань ще гора.</div><br/><div class = \'tab\'>Комбайни гудять до півночі,</div><div class = \'tab\'>Рокочуть невтомно на полі</div><div class = \'tab\'>Все збіжжя зібрати наочі</div><div class = \'tab\'>На місце, що є у коморі.</div><br/><div class = \'tab\'>Славетний у праці народ</div><div class = \'tab\'>В змаганні за тонни і якість!</div><div class = \'tab\'>Іде боротьба за рекорд –</div><div class = \'tab\'>Відчутна селянська завзятість!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(545, 36, 'Побажання нащадкам', '<div class = \'tab\'>Ми з Вами зустрілись на святі,</div><div class = \'tab\'>Яке влаштував Універ,</div><div class = \'tab\'>Усі ми тут сестри і браття,</div><div class = \'tab\'>З студентських часів й дотепер.</div><br/><div class = \'tab\'>У кожного шлях особливий,</div><div class = \'tab\'>І кожен досяг до висот.</div><div class = \'tab\'>Нічого, що чуб уже сивий,</div><div class = \'tab\'>Нам стане й сьогодні турбот!</div><br/><div class = \'tab\'>В житті не спинялись ніколи,</div><div class = \'tab\'>Дітей колисали своїх,</div><div class = \'tab\'>Тепер йдуть онуки до школи,</div><div class = \'tab\'>Так радісно чути їх сміх!</div><br/><div class = \'tab\'>Стоїть Універ непохитно</div><div class = \'tab\'>У місті, яке будували,</div><div class = \'tab\'>Дерева у парку буяють,</div><div class = \'tab\'>Вже інші тут жваво гуляють.</div><br/><div class = \'tab\'>Тут знову навчаються діти:</div><div class = \'tab\'>Це наші онуки та правнуки.</div><div class = \'tab\'>Хотілось, щоб це все продовжилось</div><div class = \'tab\'>На довгі-предовгі роки.</div><br/><div class = \'tab\'>Всі теплі слова переказані,</div><div class = \'tab\'>І сльози, і радість були.</div><div class = \'tab\'>До зустрічі знов ми готові –</div><div class = \'tab\'>Дай Бог, щоб навчались вони.</div><br/><div class = \'tab\'>Мов пташки до рідного поля</div><div class = \'tab\'>Зберетесь тут радісно знов −</div><div class = \'tab\'>Нехай посміхнеться вам доля,</div><div class = \'tab\'>Нехай вас освітить любов.</div><br/><div class = \'tab\'>У вас всі бажання дорослі,</div><div class = \'tab\'>Тож будьте сміливі завжди!</div><div class = \'tab\'>Багато пройде років поспіль,</div><div class = \'tab\'>До істини дійдете ви.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(546, 36, 'Істина', '<div class = \'tab\'>В чому істина,</div><div class = \'tab\'>І де вона існує?</div><div class = \'tab\'>В житті спочатку</div><div class = \'tab\'>Чи в кінці?</div><br/><div class = \'tab\'>Важке завдання</div><div class = \'tab\'>Вибрано поетом</div><div class = \'tab\'>Він в пошуках її</div><div class = \'tab\'>Постійно, з віком.</div><br/><div class = \'tab\'>Прочитано багато</div><div class = \'tab\'>Мудрих слів</div><div class = \'tab\'>У Біблії, у спогадах</div><div class = \'tab\'>Святих отців.</div><br/><div class = \'tab\'>Жорстока боротьба</div><div class = \'tab\'>Не зупиняється ніколи,</div><div class = \'tab\'>В душі своїй</div><div class = \'tab\'>Безжально, аж до болю.</div><br/><div class = \'tab\'>Філософи минулого</div><div class = \'tab\'>Й сучасні мудреці</div><div class = \'tab\'>Найти все намагаються</div><div class = \'tab\'>Ту істину, в кінці.</div><br/><div class = \'tab\'>А істина свята –</div><div class = \'tab\'>Це низка знань,</div><div class = \'tab\'>Але пряма, вузенька,</div><div class = \'tab\'>Як лезо грань.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(547, 36, 'Перелом', '<div class = \'tab\'>В душе моей горит огонь,</div><div class = \'tab\'>И я согласен с тем,</div><div class = \'tab\'>О чѐм судачат каждый день,</div><div class = \'tab\'>Не могут отойти от тем.</div><br/><div class = \'tab\'>Проходит время и встаѐт</div><div class = \'tab\'>Новейшая проблема.</div><div class = \'tab\'>Опять замыслился народ –</div><div class = \'tab\'>Готов на новый поворот.</div><br/><div class = \'tab\'>Из искры пламенный порыв</div><div class = \'tab\'>Достаточно поднять,</div><div class = \'tab\'>Народ готов на перелом,</div><div class = \'tab\'>Готов его принять.</div><br/><div class = \'tab\'>Но в этой жизни-кутерьме</div><div class = \'tab\'>Народ наш надо враз понять,</div><div class = \'tab\'>Негоже на него пенять –</div><div class = \'tab\'>Им надо справедливо управлять.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(548, 36, 'Ювілей науки', '<div class = \'tab\'>Славетний інститут</div><div class = \'tab\'>Святкує ювілей</div><div class = \'tab\'>І підсумки підводить</div><div class = \'tab\'>Без жодних привілей.</div><br/><div class = \'tab\'>Чудова установа –</div><div class = \'tab\'>Це гордість всій науки,</div><div class = \'tab\'>У світі всім відома</div><div class = \'tab\'>Численії роки.</div><br/><div class = \'tab\'>Генетика, ресурси,</div><div class = \'tab\'>Селекція й насіння,</div><div class = \'tab\'>А ще навчальні курси –</div><div class = \'tab\'>Найкращії знання.</div><br/><div class = \'tab\'>І кадри особливі –</div><div class = \'tab\'>Це доктори наук</div><div class = \'tab\'>Найкращі кандидати,</div><div class = \'tab\'>Які працюють тут.</div><br/><div class = \'tab\'>Аспіранти і агрономи,</div><div class = \'tab\'>Комбайнери, інженери,</div><div class = \'tab\'>Молоді докторанти,</div><div class = \'tab\'>Трактористи й лаборанти.</div><br/><div class = \'tab\'>Під покровою Божою,</div><div class = \'tab\'>У слобожанськім краю</div><div class = \'tab\'>Науці присягнули –</div><div class = \'tab\'>Вважають за свою.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(549, 36, 'Зло і добро', '<div class = \'tab\'>Не пізнавши зло</div><div class = \'tab\'>В пошуках любові,</div><div class = \'tab\'>Не знайдеш її</div><div class = \'tab\'>На шляху терновім.</div><br/><div class = \'tab\'>В молитвах до Бога</div><div class = \'tab\'>Ти знайдеш любов –</div><div class = \'tab\'>Це твоя дорога</div><div class = \'tab\'>Знову, знову, знов....</div><br/><div class = \'tab\'>А нова дорога</div><div class = \'tab\'>Встелена камінням.</div><div class = \'tab\'>Вона тому і нова,</div><div class = \'tab\'>Що в межах розуміння</div><br/><div class = \'tab\'>У пошуках любові</div><div class = \'tab\'>Йде назустріч спокій,</div><div class = \'tab\'>Але у дорозі</div><div class = \'tab\'>Зло вже на порозі.</div><br/><div class = \'tab\'>Істина відома –</div><div class = \'tab\'>Бродить біля дому,</div><div class = \'tab\'>Не пізнавши зло,</div><div class = \'tab\'>Не знайдеш добро.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(550, 36, 'Будни агронома', '<div class = \'tab\'>Дождик прошѐл –</div><div class = \'tab\'>Капельки блестят,</div><div class = \'tab\'>Солнышка нет –</div><div class = \'tab\'>Птицы молчат.</div><br/><div class = \'tab\'>Трава позеленела –</div><div class = \'tab\'>Тишина кругом,</div><div class = \'tab\'>Лай собак далѐкий</div><div class = \'tab\'>Слышу за бугром.</div><br/><div class = \'tab\'>Ехать мотоциклом</div><div class = \'tab\'>Уже нельзя, нельзя.</div><div class = \'tab\'>Дорога размокла –</div><div class = \'tab\'>Поползла земля.</div><br/><div class = \'tab\'>Подожду я солнце,</div><div class = \'tab\'>Подожду я ветер –</div><div class = \'tab\'>Пусть подсушат землю,</div><div class = \'tab\'>Что в полях, под вечер.</div><br/><div class = \'tab\'>Ты куда торопишься,</div><div class = \'tab\'>Что за непоседа?</div><div class = \'tab\'>Будь сегодня дома –</div><div class = \'tab\'>Поддержи беседу.</div><br/><div class = \'tab\'>Нет! Посмотрю посевы,</div><div class = \'tab\'>Побегу в поля,</div><div class = \'tab\'>И проверю, сколько</div><div class = \'tab\'>Выпало дождя.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(551, 36, 'Мечта туриста', '<div class = \'tab\'>Так хочется ринуться</div><div class = \'tab\'>Вдаль поскорее,</div><div class = \'tab\'>Увидеть просторы</div><div class = \'tab\'>И гор, и морей.</div><br/><div class = \'tab\'>Услышать язык,</div><div class = \'tab\'>Почувствовать нравы</div><div class = \'tab\'>Совсем необычных людей.</div><div class = \'tab\'>И в мире сегодня</div><br/><div class = \'tab\'>Все смотрят по-новому.</div><div class = \'tab\'>Туристы мечтают –</div><div class = \'tab\'>Идут до конца.</div><div class = \'tab\'>И нужен им берег,</div><br/><div class = \'tab\'>Берег Турецкий,</div><div class = \'tab\'>И Африка стала нужна.</div><div class = \'tab\'>Ничто нам не чуждо,</div><div class = \'tab\'>Мы всех уважаем,</div><br/><div class = \'tab\'>Глазами окинув простор,</div><div class = \'tab\'>И с чувством особым</div><div class = \'tab\'>Относимся мы</div><div class = \'tab\'>К народам с долин</div><br/><div class = \'tab\'>И к хозяевам гор.</div><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(552, 36, 'Желание', '<div class = \'tab\'>Моя дорогая страна,</div><div class = \'tab\'>Моя дорогая Отчизна,</div><div class = \'tab\'>Люблю тебя, очень люблю,</div><div class = \'tab\'>И это для всех очевидно.</div><br/><div class = \'tab\'>Прошёл все поля, все дороги,</div><div class = \'tab\'>Трудился я в поте лица,</div><div class = \'tab\'>Прошёл тыщи вёрст спозаранку</div><div class = \'tab\'>И этому нету конца.</div><br/><div class = \'tab\'>А создано мною немало –</div><div class = \'tab\'>Хороший для всех человек,</div><div class = \'tab\'>Но хочется что-то добавить</div><div class = \'tab\'>И с пользой оставить свой след.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(553, 37, 'Повернути мир', '<div class = \'tab\'>Чому не змовкають гармати?</div><div class = \'tab\'>Навіщо братерськая кров?</div><div class = \'tab\'>Нам бачити краще світанки,</div><div class = \'tab\'>Природні, як наша любов.</div><br/><div class = \'tab\'>Шириться злива снарядів,</div><div class = \'tab\'>Руйнується дружба людей.</div><div class = \'tab\'>Це робить сьогодні жахливим</div><div class = \'tab\'>Життя ні в чому не винних дітей!</div><br/><div class = \'tab\'>Подумайте, чада державні,</div><div class = \'tab\'>Навіщо нам розбрат і блуд.</div><div class = \'tab\'>Наразі людей більш нормальних,</div><div class = \'tab\'>Ніж недругів-покидьків тут.</div><br/><div class = \'tab\'>Що коїться зараз у світі,</div><div class = \'tab\'>Навіщо іде боротьба</div><div class = \'tab\'>Жорстока, страшна і кривава?</div><div class = \'tab\'>Напевно, прийшов сатана.</div><br/><div class = \'tab\'>Йому до вподоби насилля,</div><div class = \'tab\'>Неспокій, страждання людей.</div><div class = \'tab\'>Прикласти нам треба зусилля</div><div class = \'tab\'>Супроти нелюдських ідей.</div><br/><div class = \'tab\'>Нам хочеться жити й творити</div><div class = \'tab\'>На благо Планети Земля,</div><div class = \'tab\'>Її захистити, дітей народити,</div><div class = \'tab\'>І не піти в небуття.</div><br/><div class = \'tab\'>За що, християни, ми так провинились</div><div class = \'tab\'>Перед Господнім судом?</div><div class = \'tab\'>Напевно, усім нам потрібно молитись,</div><div class = \'tab\'>Щоб мир і любов повернути з добром.</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(554, 37, 'Історичний шанс', '<div class = \'tab\'>Історії відомі події в Україні,</div><div class = \'tab\'>Жорстокі і криваві битви за життя.</div><div class = \'tab\'>Гетьмани-керманичі мудрі і щасливі –</div><div class = \'tab\'>Рідкість, як не прикро, шукали укриття.</div><br/><div class = \'tab\'>Між ляхом і турком по граблях ходили,</div><div class = \'tab\'>Сформували думку козакам:</div><div class = \'tab\'>Краще бути разом з північним сусідом, –</div><div class = \'tab\'>Й віддались за гроші Романовим-царям.</div><br/><div class = \'tab\'>Правду все шукали, до кого пристати</div><div class = \'tab\'>Для щасливої долі, кращого буття.</div><div class = \'tab\'>Вижили в умовах нелюдського горя</div><div class = \'tab\'>Без ідеї гідної за свої права.</div><br/><div class = \'tab\'>Краще збігти в сторону, де чужинців море,</div><div class = \'tab\'>Захопились \"раєм\" без дому процвітать,</div><div class = \'tab\'>Жити поодинці – \"моя хата з краю\",</div><div class = \'tab\'>Просити у \"господаря\" − може щось подасть.</div><br/><div class = \'tab\'>Ні, панове-браття, це зовсім невірно.</div><div class = \'tab\'>Українцям новим слід подумать враз:</div><div class = \'tab\'>Вектор європейський будувати треба,</div><div class = \'tab\'>Мої дорогенькі, наступив той час!</div><br/><div class = \'tab\'>Підставляйте плечі, не опускайте руки.</div><div class = \'tab\'>Відступати нікуди – москаль наступа.</div><div class = \'tab\'>Не пускайте гада, вже набридло, досить!</div><div class = \'tab\'>Бороніть Державу – вона у нас одна!</div><br/><div class = \'tab\'>Буде щастя в домі? Знаю вірно – буде!</div><div class = \'tab\'>Але поспішайте, не марнуйте час.</div><div class = \'tab\'>Світова спільнота вірить українцям,</div><div class = \'tab\'>Вірить в Україну – тож не згаймо шанс!</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(555, 37, 'Мир для всіх', '<div class = \'tab\'>Усі ми любим тишу</div><div class = \'tab\'>Без галасу і маршу,</div><div class = \'tab\'>Без стрілянини чергами,</div><div class = \'tab\'>Без гуркоту гармат.</div><br/><div class = \'tab\'>На захисті Вітчизни</div><div class = \'tab\'>Стоять сини-українці,</div><div class = \'tab\'>Боронять нашу неньку</div><div class = \'tab\'>Незламнії борці.</div><br/><div class = \'tab\'>Борці не задля слави −</div><div class = \'tab\'>За мир у нас в державі,</div><div class = \'tab\'>Борці за справедливість,</div><div class = \'tab\'>За гідне майбуття.</div><br/><div class = \'tab\'>Дай, Боже, їм наснаги,</div><div class = \'tab\'>Терпіння і звитяги,</div><div class = \'tab\'>Підтримай їх в молитві</div><div class = \'tab\'>І поверни живих.</div><br/><div class = \'tab\'>Вони нас захищають,</div><div class = \'tab\'>Наш сон оберігають</div><div class = \'tab\'>Від ворога, що ходить</div><div class = \'tab\'>Не по своїй землі.</div><br/><div class = \'tab\'>Я вірю, все ми зможемо,</div><div class = \'tab\'>Бо правда переможе,</div><div class = \'tab\'>І мир тоді настане</div><div class = \'tab\'>Для українців всіх!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(556, 37, 'Боже, благаю!', '<div class = \'tab\'>Усі християни святої України</div><div class = \'tab\'>У чесній молитві, як завжди, тверді:</div><div class = \'tab\'>Господь-Вседержитель, помилуй країну</div><div class = \'tab\'>І всіх захисти у біді!</div><br/><div class = \'tab\'>Чому розлютилися сестри і браття,</div><div class = \'tab\'>Єдині по крові і вірі одній?</div><div class = \'tab\'>Чого нам для миру тепер недостатньо?</div><div class = \'tab\'>До чого ми дійдем? Неясно мені...</div><br/><div class = \'tab\'>Усі нам відомі «великі» здобутки</div><div class = \'tab\'>Багатих і хитрих хапуг,</div><div class = \'tab\'>Які йшли до храмів у чорних костюмах</div><div class = \'tab\'>І поглядом хижим всіх міряли вкруг.</div><br/><div class = \'tab\'>Не хлібом єдиним живуть всі у світі.</div><div class = \'tab\'>В нас воля і гідність – найліпші скарби!</div><div class = \'tab\'>Це їх захищали Герої Майдану,</div><div class = \'tab\'>За них батальйони стоять щодоби!</div><br/><div class = \'tab\'>Тримайтеся, люди, за всіх помоліться</div><div class = \'tab\'>За тих, хто загинув, і за живих,</div><div class = \'tab\'>Мирянам усім до землі поклоніться,</div><div class = \'tab\'>Що двері до правди знаходять завжди.</div><br/><div class = \'tab\'>Бо в правді є сила, що все переможе, Для нас, християн, – це шлях головний.</div><div class = \'tab\'>О боже, благаю! Підтримай, як можеш,</div><div class = \'tab\'>Дай сили утримати дух бойовий!</div>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(557, 37, 'Морской конвой', '<div class = \'tab\'>Штормит и беснуется море,</div><div class = \'tab\'>Идѐт наш конвой основной.</div><div class = \'tab\'>Холодное Белое море</div><div class = \'tab\'>Согрето матросской душой.</div><br/><div class = \'tab\'>Море суровой вражды,</div><div class = \'tab\'>Море тревог и надежды,</div><div class = \'tab\'>Море богатства и крови,</div><div class = \'tab\'>Море трагедий и скорби.</div><br/><div class = \'tab\'>Грохочут разрывы снарядов,</div><div class = \'tab\'>Торпеды шныряют кругом,</div><div class = \'tab\'>Бомбы летят словно \"грады\"</div><div class = \'tab\'>Жизнь моряка нипочем.</div><br/><div class = \'tab\'>Звучат позывные тревожно,</div><div class = \'tab\'>Волны бушуют везде,</div><div class = \'tab\'>Команде уже всѐ неважно –</div><div class = \'tab\'>Почти половина на дне.</div><br/><div class = \'tab\'>Бьѐтся свинцовое море,</div><div class = \'tab\'>Идѐт наш конвой основной.</div><div class = \'tab\'>Холодное Белое море</div><div class = \'tab\'>Согрето матросской душой.</div><br/><div class = \'tab\'>Дошли мы до порта приписки,</div><div class = \'tab\'>Оделись в бушлаты новы,</div><div class = \'tab\'>Поправили все бескозырки,</div><div class = \'tab\'>И рады, что все же живы.</div><br/><div class = \'tab\'>На пирсе стоят в ожидании:</div><div class = \'tab\'>Начальство, обслуга, охрана.</div><div class = \'tab\'>Суровые лица опущены,</div><div class = \'tab\'>И горькие слѐзы, как рана.</div><br/><div class = \'tab\'>Беснуется, плещется море...</div><div class = \'tab\'>Дошѐл караван основной.</div><div class = \'tab\'>Свинцовое Белое море</div><div class = \'tab\'>Согрето матросской душой.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(558, 37, 'Народження Харкова', '<div class = \'tab\'>Ще здавна тут проходив шлях</div><div class = \'tab\'>Від Криму до застав Московії,</div><div class = \'tab\'>Не гомоніло ще життя,</div><div class = \'tab\'>Був край глухим, ліси – терновії.</div><br/><div class = \'tab\'>Річки бурхливі навесні</div><div class = \'tab\'>Несли з собою небезпеку,</div><div class = \'tab\'>Не всі могли перепливти</div><div class = \'tab\'>В човні міцному з того боку.</div><br/><div class = \'tab\'>Болота, гаті, чагарник,</div><div class = \'tab\'>Ліси, трясовина та луки</div><div class = \'tab\'>Не дозволяли тут пройти,</div><div class = \'tab\'>Це для чужинців були муки.</div><br/><div class = \'tab\'>Мале поселення росло</div><div class = \'tab\'>І розвивалося неспішно,</div><div class = \'tab\'>З’явились праця й ремесло,</div><div class = \'tab\'>Все будувалося успішно.</div><br/><div class = \'tab\'>Козак Харко та вся ватага</div><div class = \'tab\'>Забудували край валами</div><div class = \'tab\'>(Велика в тім була звитяга!)</div><div class = \'tab\'>Й гармату притягли волами.</div><br/><div class = \'tab\'>Для захисту звели пости –</div><div class = \'tab\'>Фортецями їх турки звали,</div><div class = \'tab\'>На річці Лопань, в низині</div><div class = \'tab\'>«Харковим» місто те прозвали.</div><br/><div class = \'tab\'>Відгородились частоколом,</div><div class = \'tab\'>Звели і церкву на валу.</div><div class = \'tab\'>ЇЇ дзвіниці передзвоном</div><div class = \'tab\'>Несли божественну хвалу.</div><br/><div class = \'tab\'>Пройшло десяток років поспіль.</div><div class = \'tab\'>Харкове місто розрослось.</div><div class = \'tab\'>Роботи всім було удосталь</div><div class = \'tab\'>За працю люду воздалось.</div><br/><div class = \'tab\'>Земля ця, щедра, Богом дана</div><div class = \'tab\'>Буяє в череді століть,</div><div class = \'tab\'>Й Харко сьогодні в монументі</div><div class = \'tab\'>У місті рідному стоїть!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(559, 37, 'Пісня бандуриста', '<div class = \'tab\'>По зимовій дорозі</div><div class = \'tab\'>У безмежному полі</div><div class = \'tab\'>Козак молоденький</div><div class = \'tab\'>Додому йде поволі.</div><br/><div class = \'tab\'>Вітер дме,</div><div class = \'tab\'>Снігом замітає,</div><div class = \'tab\'>Козак в думках</div><div class = \'tab\'>Дівчину кохає.</div><br/><div class = \'tab\'>А дівчина гарна</div><div class = \'tab\'>Козака чекає,</div><div class = \'tab\'>Дуже чекає,</div><div class = \'tab\'>Безмежно кохає.</div><br/><div class = \'tab\'>Залишилось трішки</div><div class = \'tab\'>Дійти до дому –</div><div class = \'tab\'>Наскочили вороженьки,</div><div class = \'tab\'>Кинули додолу.</div><br/><div class = \'tab\'>Скрутили, волочили</div><div class = \'tab\'>До чужого краю.</div><div class = \'tab\'>За що ти, Боже,</div><div class = \'tab\'>Козака караєш?</div><br/><div class = \'tab\'>Що за злая доля</div><div class = \'tab\'>Молодій дівчині –</div><div class = \'tab\'>Чекати на волі</div><div class = \'tab\'>Хлопця-козачину?</div><br/><div class = \'tab\'>Ось і час весілля,</div><div class = \'tab\'>Радості, застілля,</div><div class = \'tab\'>А його немає,</div><div class = \'tab\'>Милого з Запілля.</div><br/><div class = \'tab\'>Ой, пройдуть годочки,</div><div class = \'tab\'>Рочки чималенькі,</div><div class = \'tab\'>А могли би бути</div><div class = \'tab\'>Козачки молоденькі.</div><br/><div class = \'tab\'>Отак і велося</div><div class = \'tab\'>В час Руїни лютий</div><div class = \'tab\'>По Вкраїні милій –</div><div class = \'tab\'>У козацтві – скрута.</div><br/><div class = \'tab\'>Хлопців полонили</div><div class = \'tab\'>І тягли на ринок.</div><div class = \'tab\'>Вороття не було</div><div class = \'tab\'>Ні за який вчинок.</div><br/><div class = \'tab\'>Вимирало панство</div><div class = \'tab\'>Шляхетне, козацьке,</div><div class = \'tab\'>Не було хоробрих</div><div class = \'tab\'>Воїнів достатньо,</div><br/><div class = \'tab\'>Які б боронили</div><div class = \'tab\'>Доньок, наречених.</div><div class = \'tab\'>Край спустошив ворог,</div><div class = \'tab\'>Ворог навіжений.</div><br/><div class = \'tab\'>Воїни з\'явились –</div><div class = \'tab\'>Геть пішла Руїна,</div><div class = \'tab\'>Цвітом вся укрилась</div><div class = \'tab\'>Рідна Україна!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(560, 37, 'Два вусача', '<div class = \'tab\'>Земля здригалась і горіла,</div><div class = \'tab\'>Вогонь та дим закрили небо,</div><div class = \'tab\'>Солдатська доля всім набридла…</div><div class = \'tab\'>Кому й навіщо все це треба?</div><br/><div class = \'tab\'>Ділили землю і людей,</div><div class = \'tab\'>І руки щиро потискали,</div><div class = \'tab\'>Усмішки хитрі, мов в звірей</div><div class = \'tab\'>У вусах чорних не ховали.</div><br/><div class = \'tab\'>Два звіра лютих не могли</div><div class = \'tab\'>Ужитись поруч на планеті.</div><div class = \'tab\'>Себе так міцно стерегли:</div><div class = \'tab\'>Створили війська із багнетів.</div><br/><div class = \'tab\'>Дурили всіх навкруг себе,</div><div class = \'tab\'>Листи писали вечорами,</div><div class = \'tab\'>І хитро ранками клялись</div><div class = \'tab\'>У вічній дружбі до нестями.</div><br/><div class = \'tab\'>Гнобили тих, хто проти був,</div><div class = \'tab\'>Психоз роздули у країнах,</div><div class = \'tab\'>Кордони зводили нові</div><div class = \'tab\'>З людей військових на руїнах.</div><br/><div class = \'tab\'>А люди з двох боків кордону</div><div class = \'tab\'>Сприймали як свою біду</div><div class = \'tab\'>Амбіцій їх потворний спокій</div><div class = \'tab\'>Й жили в примарному аду.</div><br/><div class = \'tab\'>Війна відкрила очі всім,</div><div class = \'tab\'>Що ними це було замислено –</div><div class = \'tab\'>Закритим клубом двох «вождів».</div><div class = \'tab\'>Хіба таке можливо й мислимо?</div><br/><div class = \'tab\'>Крутили світом сяк і так,</div><div class = \'tab\'>Ділили все, і навіть цвяхи,</div><div class = \'tab\'>Й самі взялись переставляти</div><div class = \'tab\'>Солдат та армії як шахи.</div><br/><div class = \'tab\'>І так дурили увесь світ</div><div class = \'tab\'>Від сходу і до заходу.</div><div class = \'tab\'>Напились крові різних груп,</div><div class = \'tab\'>Були в шаленім захваті.</div><br/><div class = \'tab\'>Мільйони знищили (за що?)</div><div class = \'tab\'>Старих і молодих людей!</div><div class = \'tab\'>Чи винен в чомусь перед Богом</div><div class = \'tab\'>Будь-хто, хоч німець, хоч єврей?</div><br/><div class = \'tab\'>… Пройшли роки, відкрились таємниці.</div><div class = \'tab\'>Чому від вусачів залежав кожен?</div><div class = \'tab\'>Тому сьогодні люди різних націй</div><div class = \'tab\'>Повинні пам’ятати і боротись – це ми можемо!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(561, 37, 'Законы на службу людей', '<div class = \'tab\'>Солнышко рано встаѐт,</div><div class = \'tab\'>Светом своим озаряет.</div><div class = \'tab\'>Я долго смотрю на восток –</div><div class = \'tab\'>Душа от тревог замирает.</div><br/><div class = \'tab\'>Там братья мои по крови,</div><div class = \'tab\'>Там сѐстры мои проживают,</div><div class = \'tab\'>Зачем мы живѐм не в любви,</div><div class = \'tab\'>Что нам всем родным не хватает?</div><br/><div class = \'tab\'>Обиженный люд из востока</div><div class = \'tab\'>Наслушался разных речей,</div><div class = \'tab\'>Жил, словно в разврате и блуде,</div><div class = \'tab\'>Что было в порядке вещей.</div><br/><div class = \'tab\'>Другие уж рьяно раздули</div><div class = \'tab\'>Значение, роль языка,</div><div class = \'tab\'>Верхи головою не думали,</div><div class = \'tab\'>Проблемы решали слегка.</div><br/><div class = \'tab\'>Язык – это мощное средство</div><div class = \'tab\'>В борьбе за свободу и честь.</div><div class = \'tab\'>Вчера и сегодня за это</div><div class = \'tab\'>Пролито крови – не счесть!</div><br/><div class = \'tab\'>Наверно от жадности нашей,</div><div class = \'tab\'>От скуки и лени в душе,</div><div class = \'tab\'>От неуменья поставить</div><div class = \'tab\'>Законы на службу людей.</div><br/><div class = \'tab\'>Воров-казнокрадов всѐ больше,</div><div class = \'tab\'>Просящих о помощи – море,</div><div class = \'tab\'>Чиновников подлых не меряно,</div><div class = \'tab\'>Нечистых на руку. О горе!</div><br/><div class = \'tab\'>А выход один есть в державе:</div><div class = \'tab\'>Закона незыблемость вправе</div><div class = \'tab\'>Поставить действительно точно</div><div class = \'tab\'>На службу народу – и точка!</div><br/><div class = \'tab\'>Железной рукою внедрить,</div><div class = \'tab\'>И так, чтобы не было ходу</div><div class = \'tab\'>Назад никому, даже избранным</div><div class = \'tab\'>И новым помощникам модным.</div><br/><div class = \'tab\'>Солнышко встало на запад,</div><div class = \'tab\'>Светом своим окрыляет,</div><div class = \'tab\'>С надеждой мы смотрим туда,</div><div class = \'tab\'>Где ждут нас и всѐ нам прощают.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(562, 37, 'Три дуби', '<div class = \'tab\'>Три дуби, мов брати,</div><div class = \'tab\'>На галявині стоять.</div><div class = \'tab\'>Три дуби-побратими</div><div class = \'tab\'>Між собою гомонять.</div><br/><div class = \'tab\'>Гомонять все про минуле,</div><div class = \'tab\'>Про часи війни.</div><div class = \'tab\'>Гомонять і про сучасність:</div><div class = \'tab\'>Бачать, знають теж вони.</div><br/><div class = \'tab\'>...Тут проходив бій жорстокий –</div><div class = \'tab\'>Кров лилася за Свободу,</div><div class = \'tab\'>Полягло бійців чимало</div><div class = \'tab\'>З українського народу.</div><br/><div class = \'tab\'>І сьогодні ворог лютий</div><div class = \'tab\'>Не дає спокійно жити,</div><div class = \'tab\'>Він не хоче бути мирним,</div><div class = \'tab\'>Він не може вже дружити.</div><br/><div class = \'tab\'>Пройде час і схаменеться,</div><div class = \'tab\'>Але пізно буде –</div><div class = \'tab\'>Йому вірно не минеться</div><div class = \'tab\'>Божої кари й суду.</div><br/><div class = \'tab\'>Три дуби, три брати</div><div class = \'tab\'>Все спостерігають,</div><div class = \'tab\'>Люди наші, як дуби,</div><div class = \'tab\'>Добре все запам\'ятають.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(563, 37, 'Карпатська пісня', '<div class = \'tab\'>Нічка темна опустилась</div><div class = \'tab\'>На наші Карпати,</div><div class = \'tab\'>Гори сплять і сплять смереки,</div><div class = \'tab\'>Діткам треба спати.</div><br/><div class = \'tab\'>Спів дівчини з полонини –</div><div class = \'tab\'>Сплески Черемоша,</div><div class = \'tab\'>Зачаровують хлопчину</div><div class = \'tab\'>Гей! Слова хороші.</div><br/><div class = \'tab\'>Пісня тая – вишиванка –</div><div class = \'tab\'>Душу криє й гріє,</div><div class = \'tab\'>Пісня тій дівчини милій</div><div class = \'tab\'>Парубка жаліє.</div><br/><div class = \'tab\'>Пісню чути так далеко –</div><div class = \'tab\'>По всій-всій долині,</div><div class = \'tab\'>Про хоробрих вояків –</div><div class = \'tab\'>Слава Україні!</div><br/><div class = \'tab\'>Про хоробрих вояків</div><div class = \'tab\'>Слава Україні!</div><div class = \'tab\'>Про хоробрих вояків</div><div class = \'tab\'>Слава Україні!</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1);
INSERT INTO `LYRICS` (`id`, `set_id`, `name`, `content`, `write_date`, `author`, `description`, `author_comment`, `lyrics_type`, `TABBED`, `BRS`, `STROPHE_LENGTH`, `STROPHE_DELIM`, `LINE_WRAPPER`, `LINE_WRAPPER_END`, `LINE_WRAPPER_TABBED`, `LINE_WRAPPER_TABBED_END`, `auto_format`) VALUES
(564, 37, 'Молитва за сина', '<div class = \'tab\'>Наш рідненький сину,</div><div class = \'tab\'>Де тебе шукати?</div><div class = \'tab\'>Жде тебе додому</div><div class = \'tab\'>Вже сивенька мати.</div><br/><div class = \'tab\'>Дорогенький синку,</div><div class = \'tab\'>В серці ми з тобою,</div><div class = \'tab\'>Скучили і чекаємо</div><div class = \'tab\'>Невдовзі з Перемогою.</div><br/><div class = \'tab\'>Нам не все відомо,</div><div class = \'tab\'>Як ти поживаєш,</div><div class = \'tab\'>В думках розуміємо,</div><div class = \'tab\'>Що нас захищаєш.</div><br/><div class = \'tab\'>Ворог хижий, лютий –</div><div class = \'tab\'>Знову смалить з танку.</div><div class = \'tab\'>Разом відчуваємо –</div><div class = \'tab\'>Було тяжко з ранку.</div><br/><div class = \'tab\'>Наш рідненький синку,</div><div class = \'tab\'>Де перебуваєш?</div><div class = \'tab\'>В думках все про тебе –</div><div class = \'tab\'>Серця розриваєш.</div><br/><div class = \'tab\'>Боже милосердний,</div><div class = \'tab\'>Просимо – благаємо,</div><div class = \'tab\'>Збережи нам сина,</div><div class = \'tab\'>Ми його чекаємо.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(565, 37, 'Якщо всі та разом', '<div class = \'tab\'>Якщо я, якщо ти, якщо ми</div><div class = \'tab\'>В нашій пам\'яті місце залишили</div><div class = \'tab\'>Для Героїв Майдану й війни,</div><div class = \'tab\'>Котрі в вічність геройськи пішли.</div><br/><div class = \'tab\'>Що пішли в небеса, в небуття</div><div class = \'tab\'>За Свободу та Волю єдині</div><div class = \'tab\'>Й просування усіх в майбуття</div><div class = \'tab\'>За щасливе життя в Україні.</div><br/><div class = \'tab\'>Допоможе нам Бог подолати останню недугу,</div><div class = \'tab\'>Що роками ганьбила народ,</div><div class = \'tab\'>Скільки можна терпіти цю кляту, нестерпну наругу,</div><div class = \'tab\'>Яка знищила віру у вільність свобод.</div><br/><div class = \'tab\'>Допоможе нам Бог зберегти мову в пам\'яті,</div><div class = \'tab\'>Вона має чарівні слова</div><div class = \'tab\'>Щодо правди подій та земної любові в співдружності,</div><div class = \'tab\'>Щодо впливу на захист любові й добра.</div><br/><div class = \'tab\'>Так крокуйте вперед без зупинки,</div><div class = \'tab\'>Сміливіше розправивши груди,</div><div class = \'tab\'>Бережіть свою мову співочу</div><div class = \'tab\'>І спілкуйтеся нею повсюди.</div><br/><div class = \'tab\'>Якщо всі та разом – гармонічно,</div><div class = \'tab\'>Вже в сучасному світові знов</div><div class = \'tab\'>Жити з рідною мовою вічно,</div><div class = \'tab\'>Щоб у нас панувала любов!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(566, 37, 'Перебудова', '<div class = \'tab\'>Дитинство моє безтурботне:</div><div class = \'tab\'>Мандрівки по лісу й ярам,</div><div class = \'tab\'>Навчання у школі зимовій,</div><div class = \'tab\'>А влітку в зелених полях.</div><br/><div class = \'tab\'>Красотами рідного краю</div><div class = \'tab\'>Я милувався щодня,</div><div class = \'tab\'>Дитинство пройшло у роботі –</div><div class = \'tab\'>Так склалася доля моя.</div><br/><div class = \'tab\'>...Мама молилася Богу</div><div class = \'tab\'>За кожен із прожитих днів,</div><div class = \'tab\'>Ніщо не просила для себе,</div><div class = \'tab\'>Крім прощення людських гріхів.</div><br/><div class = \'tab\'>А їх було дуже багато –</div><div class = \'tab\'>Навкруг насувало безвладдя –</div><div class = \'tab\'>Дорогу забули до храму,</div><div class = \'tab\'>Бо склалося зверху всевладдя.</div><br/><div class = \'tab\'>...Отрутою з Уряду дихали,</div><div class = \'tab\'>За все була грізна стаття.</div><div class = \'tab\'>Хіба таке можна терпіти –</div><div class = \'tab\'>Дорослим нестерпне життя!</div><br/><div class = \'tab\'>І тут все прорвало водночас,</div><div class = \'tab\'>Коли вже пройшло років сто,</div><div class = \'tab\'>І наші онуки помстилися</div><div class = \'tab\'>За роками нанесене зло.</div><br/><div class = \'tab\'>...Вони не такі ангажовані,</div><div class = \'tab\'>Як ми у радянські часи,</div><div class = \'tab\'>Їх відрізняє сміливість,</div><div class = \'tab\'>Бажання до Правди дійти.</div><br/><div class = \'tab\'>Іти на Майдан та на кулі</div><div class = \'tab\'>Не кожен спроможний із нас,</div><div class = \'tab\'>А наші онуки на подвиг</div><div class = \'tab\'>Готові іти всі за вас.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(567, 38, 'Дочекалась...', '<div class = \'tab\'>Ой, гоп, три калини</div><div class = \'tab\'>Зашарілись біля млина!</div><div class = \'tab\'>Я дівчинонька проворна</div><div class = \'tab\'>І красою неповторна.</div><br/><div class = \'tab\'>Маю груди, як коліна,</div><div class = \'tab\'>Хочу мельникова сина!</div><div class = \'tab\'>Знаю я: себе не дам</div><div class = \'tab\'>На поругу ворогам.</div><br/><div class = \'tab\'>Ой, гоп – два коліна!</div><div class = \'tab\'>Де ж мій любий біля млина?</div><div class = \'tab\'>Я лишилася одна,</div><div class = \'tab\'>Час так швидко проминá.</div><br/><div class = \'tab\'>Не жалкую у цей раз,</div><div class = \'tab\'>Надію плекаю –</div><div class = \'tab\'>Прийде скоро новий час,</div><div class = \'tab\'>Кожен день чекаю.</div><br/><div class = \'tab\'>Ой, гоп, дві калини!</div><div class = \'tab\'>У сусіда є три сина.</div><div class = \'tab\'>Радить мати почекати</div><div class = \'tab\'>І ще трішки погуляти.</div><br/><div class = \'tab\'>Почекаю я хлопчину −</div><div class = \'tab\'>Мельника меншóго сина,</div><div class = \'tab\'>А для інших у коморі</div><div class = \'tab\'>Пан гарбуз лежить готовий!</div><br/><div class = \'tab\'>Тож чекаю на порозі...</div><div class = \'tab\'>От іде хтось по дорозі:</div><div class = \'tab\'>Має вуса, ноги й руки –</div><div class = \'tab\'>Може вийти від розпуки?</div><br/><div class = \'tab\'>Вийшла, бачу – а це він,</div><div class = \'tab\'>Мельника найстарший син!</div><div class = \'tab\'>Як же я йому зраділа!</div><div class = \'tab\'>Чом раніш не розгляділа?</div><br/><div class = \'tab\'>Він же краще, ніж молодший:</div><div class = \'tab\'>Чорновусий, кароокий!</div><div class = \'tab\'>От і дочекалась я:</div><div class = \'tab\'>Буде в нас своя сім\"я!</div><br/><div class = \'tab\'></div><br/>', '2019-03-17', 'В. В. Кириченко', '', '', 'Лірика', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(568, 38, 'Хвора голова', '<div class = \'tab\'>Ой, гоп, три копиці!</div><div class = \'tab\'>Маю сина, маю Гриця,</div><div class = \'tab\'>Маю доньку і жону,</div><div class = \'tab\'>Самогоночку жену.</div><br/><div class = \'tab\'>Випив, випив, погуляв, –</div><div class = \'tab\'>Самогон лишився.</div><div class = \'tab\'>До куми зайшов і взнав,</div><div class = \'tab\'>Що роман скінчився.</div><br/><div class = \'tab\'>Маю сваху та золовку,</div><div class = \'tab\'>Маю хворую головку,</div><div class = \'tab\'>Маю кума та куму –</div><div class = \'tab\'>Через неї всіх кляну.</div><br/><div class = \'tab\'>Взяв я пляшку та пішов,</div><div class = \'tab\'>Якби замиритися</div><div class = \'tab\'>Там я кума враз знайшов –</div><div class = \'tab\'>Прийшлось посваритися.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(569, 38, '\"Друзья\"', '<div class = \'tab\'>Как-то утром на лужайке</div><div class = \'tab\'>Собрались \"друзья по пайке\":</div><div class = \'tab\'>Бобик, Хам и сам Балбес</div><div class = \'tab\'>Опустились как с небес.</div><br/><div class = \'tab\'>Начали делить корма –</div><div class = \'tab\'>Только время тратить зря.</div><div class = \'tab\'>Разругались, разошлись,</div><div class = \'tab\'>Еще больше завелись.</div><br/><div class = \'tab\'>Не решили тот вопрос,</div><div class = \'tab\'>На который ставили,</div><div class = \'tab\'>Видно, Бобик не дорос,</div><div class = \'tab\'>Балбес и Хам его обставили.</div><br/><div class = \'tab\'>Так как их осталось два</div><div class = \'tab\'>Корм решили не делить.</div><div class = \'tab\'>Лучше ночью незаметно</div><div class = \'tab\'>Часть лужайки отхватить.</div><br/><div class = \'tab\'>Тут поднялся шум и гам,</div><div class = \'tab\'>Как же быть другим зверям?</div><div class = \'tab\'>Кукиш крепкий им скрутили,</div><div class = \'tab\'>Рады сами, что схитрили!</div><br/><div class = \'tab\'>Мораль у басни такова:</div><div class = \'tab\'>Не трудись напрасно,</div><div class = \'tab\'>Жизнь собачья коротка,</div><div class = \'tab\'>Надо жить иначе.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(570, 38, '«Честный» суд', '<div class = \'tab\'>Как-то раннею порою,</div><div class = \'tab\'>Только спряталась луна –</div><div class = \'tab\'>На поляну вышли двое,</div><div class = \'tab\'>К ним Она пришла одна.</div><br/><div class = \'tab\'>Стало быть, всего их трое</div><div class = \'tab\'>Собралось на завтрак ранний.</div><div class = \'tab\'>Угощений было вдоволь</div><div class = \'tab\'>Приготовлено заранее.</div><br/><div class = \'tab\'>Двое и «подруга»</div><div class = \'tab\'>Что-то обсуждали…</div><div class = \'tab\'>Слушали друг друга –</div><div class = \'tab\'>Лишь глаза сверкали.</div><br/><div class = \'tab\'>Обсуждали и решали,</div><div class = \'tab\'>Тостом «закреплялись»,</div><div class = \'tab\'>Меморандум зачитали,</div><div class = \'tab\'>Там и распрощались.</div><br/><div class = \'tab\'>К вечеру пошел слушок</div><div class = \'tab\'>Меж братвой звериной,</div><div class = \'tab\'>Что не их теперь лужок,</div><div class = \'tab\'>А одной «змеины»!</div><br/><div class = \'tab\'>Кто? Откуда ты взялась?</div><div class = \'tab\'>Где же аргументы? –</div><div class = \'tab\'>Двое скрылись не таясь</div><div class = \'tab\'>С важным документом.</div><br/><div class = \'tab\'>«Что за рейдерский захват?» –</div><div class = \'tab\'>Вопрошали звери.</div><div class = \'tab\'>«Кто, скажите, виноват,</div><div class = \'tab\'>В том, что луг потерян?»</div><br/><div class = \'tab\'>Большинство решило тут:</div><div class = \'tab\'>Луг пока на месте, –</div><div class = \'tab\'>Обратиться в местный суд,</div><div class = \'tab\'>Решить вопрос по чести!</div><br/><div class = \'tab\'>Утром все пришли к суду</div><div class = \'tab\'>С жалобой звериной,</div><div class = \'tab\'>Ба! Встречает их судья</div><div class = \'tab\'>С головой змеиной!</div><br/><div class = \'tab\'>Как вы смотрите, друзья,</div><div class = \'tab\'>На случай, столь позорный?</div><div class = \'tab\'>Чью сторону займет Она</div><div class = \'tab\'>И прокурор проворный?</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(571, 38, 'Телевізор-брехунець', '<div class = \'tab\'>Телевізор-брехунець –</div><div class = \'tab\'>Мов те радіо радянське,</div><div class = \'tab\'>В нас урвався вже терпець</div><div class = \'tab\'>Слухати з екранів \"страсті\".</div><br/><div class = \'tab\'>Вранці, ввечері і вдень</div><div class = \'tab\'>\"Страсті\" нагнітаються,</div><div class = \'tab\'>На проплачених каналах</div><div class = \'tab\'>\"Експерти\" вихваляються.</div><br/><div class = \'tab\'>Схаменіться, люди добрі,</div><div class = \'tab\'>Зробіть відпочинок.</div><div class = \'tab\'>Все одно вони нас дурять,</div><div class = \'tab\'>Перекрутять любий вчинок.</div><br/><div class = \'tab\'>Чим критичніша пурга,</div><div class = \'tab\'>Тим спокійніше сприймайте.</div><div class = \'tab\'>Бо мета у них одна –</div><div class = \'tab\'>Налякати \"чувака\".</div><br/><div class = \'tab\'>Для сенсації дорогу</div><div class = \'tab\'>Знов уже готують</div><div class = \'tab\'>І підхвалюють потроху,</div><div class = \'tab\'>Всіх підряд зомбують.</div><br/><div class = \'tab\'>Скільки можна цей терпіти</div><div class = \'tab\'>Зомбі-брехунець,</div><div class = \'tab\'>Краще вийти в сад, на грядку...</div><div class = \'tab\'>Все, закінчився терпець!</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(572, 38, 'Молодіжні жарти', '<div class = \'tab\'>Ой! Прокинувся та встав,</div><div class = \'tab\'>І на кухню поскакав.</div><div class = \'tab\'>Люба очі враз відкрила</div><div class = \'tab\'>Та й сказала щось \"про крила\".</div><br/><div class = \'tab\'>Кругом пусто, хоч гуляй,</div><div class = \'tab\'>Тиша в домі аж бринить,</div><div class = \'tab\'>Ніби звечора Мамай</div><div class = \'tab\'>З військом взяв усе за мить.</div><br/><div class = \'tab\'>Подивився за дверцята,</div><div class = \'tab\'>Де заначку сховано, –</div><div class = \'tab\'>Слава Богу, ще зісталась –</div><div class = \'tab\'>Настрій не зіпсований.</div><br/><div class = \'tab\'>На душі так стало гарно,</div><div class = \'tab\'>Потепліло всьому тілу,</div><div class = \'tab\'>Я й до подруги поплентав</div><div class = \'tab\'>В настрої весільному.</div><br/><div class = \'tab\'>Тут і сталось так, як треба,</div><div class = \'tab\'>Замість очей \"олені\"</div><div class = \'tab\'>Враз відкрились у коханки</div><div class = \'tab\'>Всі місця оголені.</div><br/><div class = \'tab\'>\"Де ти був? Чому так довго?\" –</div><div class = \'tab\'>Раптом запитала.</div><div class = \'tab\'>Ой! Хотів було збрехати,</div><div class = \'tab\'>Та вона дістала.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(573, 38, 'Мечта бездельника', '<div class = \'tab\'>Понедельник – не бездельник:</div><div class = \'tab\'>Все спешат, спешат, спешат,</div><div class = \'tab\'>Будто утром спозаранку</div><div class = \'tab\'>Окатили из ушат.</div><br/><div class = \'tab\'>Вторник тоже весь в заботах,</div><div class = \'tab\'>Славно выполнен заказ.</div><div class = \'tab\'>Накрутили, написали,</div><div class = \'tab\'>Получилось в самый раз.</div><br/><div class = \'tab\'>Вот средина всей недели,</div><div class = \'tab\'>Дел поделано сполна:</div><div class = \'tab\'>Мы уже всѐ просидели,</div><div class = \'tab\'>И концовка уж видна.</div><br/><div class = \'tab\'>А в четверг в хозяйство едем,</div><div class = \'tab\'>Нас там ждут уже давно,</div><div class = \'tab\'>Но водитель выпил утром –</div><div class = \'tab\'>Залегаем все на дно.</div><br/><div class = \'tab\'>Самый лучший день подходит,</div><div class = \'tab\'>Можно запросто \"слинять\",</div><div class = \'tab\'>И начальник не заметит –</div><div class = \'tab\'>Его тоже нет опять.</div><br/><div class = \'tab\'>А суббота – не работа,</div><div class = \'tab\'>Все ж законный выходной,</div><div class = \'tab\'>И поэтому в субботу</div><div class = \'tab\'>Тишина на проходной.</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(574, 38, 'Ярило', '<div class = \'tab\'>\"Ярило\" у перекладі зі</div><div class = \'tab\'>старо-слов\'янського – \"сонце\"</div><div class = \'tab\'>Настане день</div><div class = \'tab\'>І зійде сонце,</div><br/><div class = \'tab\'>Осяє світлом</div><div class = \'tab\'>Твоє віконце.</div><div class = \'tab\'>З самого ранку</div><div class = \'tab\'>Розгонить тінь</div><br/><div class = \'tab\'>І з твого тіла</div><div class = \'tab\'>Прогонить лінь.</div><div class = \'tab\'>Світ обігріє</div><div class = \'tab\'>Водночас навкруги,</div><br/><div class = \'tab\'>Зрадієш ти,</div><div class = \'tab\'>Зрадіємо всі ми.</div><div class = \'tab\'>...Життя почнеться −</div><div class = \'tab\'>Підеш по колу,</div><br/><div class = \'tab\'>Відкриєш книгу −</div><div class = \'tab\'>Згадаєш школу.</div><div class = \'tab\'>Авжеж сьогодні</div><div class = \'tab\'>Поля чекають,</div><br/><div class = \'tab\'>Приїдуть люди</div><div class = \'tab\'>І знов зорають.</div><div class = \'tab\'>Засієш ниву</div><div class = \'tab\'>Насінням добрим,</div><br/><div class = \'tab\'>А щоб вродило –</div><div class = \'tab\'>Додай ще добрив.</div><div class = \'tab\'>...І врешті-решт</div><div class = \'tab\'>Зберемо ми врожай,</div><br/><div class = \'tab\'>Якого вистачить усім</div><div class = \'tab\'>Аж через край.</div><div class = \'tab\'>Проте, не забувай −</div><div class = \'tab\'>Готуй насіння</div><br/><div class = \'tab\'>Та пам\'ятай,</div><div class = \'tab\'>Що дбаєш сам про урожай.</div><div class = \'tab\'>Це все цікаво,</div><div class = \'tab\'>Про що ми говорили,</div><br/><div class = \'tab\'>Але на сто відсотків</div><div class = \'tab\'>Залежить від Ярила.</div>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(575, 38, 'Накрутив себе', '<div class = \'tab\'>Весняним днем іду додому,</div><div class = \'tab\'>Чекає мати та рідня,</div><div class = \'tab\'>А в голові думки про неї –</div><div class = \'tab\'>Мою кохану, думки щодня.</div><br/><div class = \'tab\'>Пройшли три роки як століття,</div><div class = \'tab\'>Ні – пролетіли, як три дні.</div><div class = \'tab\'>Іду додому, до матусі,</div><div class = \'tab\'>Бачу себе серед рідні.</div><br/><div class = \'tab\'>Роки пройшли в служінні народу.</div><div class = \'tab\'>Листи читав коханої щодня,</div><div class = \'tab\'>Але так сталося між нами –</div><div class = \'tab\'>Мене залишила вона.</div><br/><div class = \'tab\'>Напевно хтось їй підвернувся,</div><div class = \'tab\'>Пройшов селом дуже лукаво,</div><div class = \'tab\'>Моя кохана полюбила</div><div class = \'tab\'>Його недавно враз так жваво.</div><br/><div class = \'tab\'>Можливо був я нелюбим,</div><div class = \'tab\'>Можливо наклеп не розкрив,</div><div class = \'tab\'>Тепер я чітко уявляю,</div><div class = \'tab\'>Коли пішло все на розрив.</div><br/><div class = \'tab\'>...Авжеж, додому півдороги –</div><div class = \'tab\'>Весна буяє навкруги,</div><div class = \'tab\'>А в голові мої думки</div><div class = \'tab\'>Пливуть, пливуть, як ті круги.</div><br/><div class = \'tab\'>Солдате бравий, симпатичний,</div><div class = \'tab\'>Ти припини свої думки.</div><div class = \'tab\'>Вже там нове село за лісом,</div><div class = \'tab\'>Пройти зосталося ставки.</div><br/><div class = \'tab\'>І тут згадалося дитинство,</div><div class = \'tab\'>Моя робота та батьки,</div><div class = \'tab\'>Думки пішли вже іншим курсом –</div><div class = \'tab\'>Стареньким як допомогти.</div><br/><div class = \'tab\'>Напевно двері похились,</div><div class = \'tab\'>Городина посаджена не вся,</div><div class = \'tab\'>Цегляна стінка відвалилась,</div><div class = \'tab\'>А щодо зради – то брехня...</div><br/>', '2019-03-17', 'В.В. Кириченко', '', '', '', 0, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(576, 39, 'Тестовий вірш', '<div class = \'tab\'>Тестовий рядок №1</div><div class = \'tab\'>Тестовий рядок №2</div><div class = \'tab\'>Тестовий рядок №3</div><div class = \'tab\'>Тестовий рядок №4</div><br/><div class = \'double_tab\'>Тестовий рядок №5</div><div class = \'double_tab\'>Тестовий рядок №6</div><div class = \'double_tab\'>Тестовий рядок №7</div><div class = \'double_tab\'>Тестовий рядок №8</div><br/>', '2019-03-27', 'В. В. Кириченко', 'Присвячено великому Даркнету', 'КЕК', 'Лірика', 1, 1, 4, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1),
(577, 39, 'Тестовий вірш', '<div class = \'tab\'>Тестовий рядок №1</div><div class = \'tab\'>Тестовий рядок №2</div><br/><div class = \'double_tab\'>Тестовий рядок №3</div><div class = \'double_tab\'>Тестовий рядок №4</div><br/><div class = \'tab\'>Тестовий рядок №5</div><div class = \'tab\'>Тестовий рядок №6</div><br/><div class = \'double_tab\'>Тестовий рядок №7</div><div class = \'double_tab\'>Тестовий рядок №8</div><br/>', '2019-03-28', 'В. В. Кириченко', 'Присвячено великому Даркнету', 'Кек', 'Лірика', 1, 1, 2, '<br/>', '<div class = \'tab\'>', '</div>', '<div class = \'double_tab\'>', '</div>', 1);
