INSERT INTO `NEWS` (`id`, `title`, `description`, `news_date`, `src`, `video_src`) VALUES
(11, 'Тритикале — культура XXI сторіччя', 'Тритикале — гібрид пшениці та жита, що поєднує у собі їх найкращі властивості. Йому належні підвищена морозо- та хворобо-стійкість, знижені вимоги до родючості грунту, великий вміст білка та насиченість вітамінами.\r\nНа жаль, не дивлячись на корисність та неабиякі пекарські властивості тритикале, його виробництво в Україні досить невелике та не реалізує весь потенціал цієї культури.', '2016-10-05', 'NULL', 'https://www.youtube.com/embed/8oWv3YN6Rks'),
